INSERT INTO [spell] ([id], [name], [altname], [school], [subschool], [descriptor], [spellcraft_dc], [level], [components], [casting_time], [range], [target], [area], [effect], [duration], [saving_throw], [spell_resistance], [short_description], [to_develop], [material_components], [arcane_material_components], [focus], [description], [xp_cost], [arcane_focus], [wizard_focus], [verbal_components], [sorcerer_focus], [bard_focus], [cleric_focus], [druid_focus], [full_text], [reference])
VALUES 
  (130, 'Bestow Curse', 'bestow curse', 'Necromancy', NULL, NULL, NULL, 'Cleric 3, Sorcerer/Wizard 4', 'V, S', '1 standard action', 'Touch', 'Creature touched', NULL, NULL, 'Permanent', 'Will negates', 'Yes', '-6 to an ability score; -4 on attack rolls, saves, and checks; or 50% chance of losing each action.', NULL, NULL, NULL, NULL, '&lt;p&gt;You place a curse on the subject. Choose one of the following three effects.&lt;/p&gt;
      &lt;ul&gt;
 &lt;li&gt; -6 decrease to an ability score (minimum 1).&lt;/li&gt;
 &lt;li&gt; -4 penalty on attack rolls, saves, ability checks, and skill checks.&lt;/li&gt;
 &lt;li&gt; Each turn, the target has a 50% chance to act normally; otherwise, it takes no action.&lt;/li&gt;
&lt;/ul&gt;
      &lt;p&gt;You may also invent your own curse, but it should be no more powerful than those described above.&lt;/p&gt;
      &lt;p&gt;The &lt;i&gt;curse&lt;/i&gt; bestowed by this spell cannot be dispelled, but it can be removed with a &lt;i&gt;break enchantment&lt;/i&gt;, &lt;i&gt;limited wish&lt;/i&gt;, &lt;i&gt;miracle&lt;/i&gt;, &lt;i&gt;remove curse&lt;/i&gt;, or &lt;i&gt;wish&lt;/i&gt; spell.&lt;/p&gt;
      &lt;p&gt;&lt;i&gt;Bestow curse&lt;/i&gt; counters &lt;i&gt;remove curse&lt;/i&gt;.&lt;/p&gt;
      &lt;p/&gt;', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '&lt;div topic=&quot;Bestow Curse&quot; level=&quot;5&quot;&gt;&lt;p&gt;&lt;h5&gt;Bestow Curse&lt;/h5&gt;&lt;/p&gt;&lt;table width=&quot;100%&quot; border=&quot;1&quot; cellpadding=&quot;2&quot; cellspacing=&quot;2&quot; frame=&quot;VOID&quot; rules=&quot;ROWS&quot;&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;/&gt;&lt;td&gt;Necromancy&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Level:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Cleric 3, Sorcerer/Wizard 4&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Components:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;V, S&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Casting Time:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;1 standard action&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Range:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Touch&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Target:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Creature touched&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Duration:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Permanent&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Saving Throw:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Will negates&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Spell Resistance:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Yes&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;p&gt;You place a curse on the subject. Choose one of the following three effects.&lt;/p&gt;&lt;ul&gt;
 &lt;li&gt; -6 decrease to an ability score (minimum 1).&lt;/li&gt;
 &lt;li&gt; -4 penalty on attack rolls, saves, ability checks, and skill checks.&lt;/li&gt;
 &lt;li&gt; Each turn, the target has a 50% chance to act normally; otherwise, it takes no action.&lt;/li&gt;
&lt;/ul&gt;&lt;p&gt;You may also invent your own curse, but it should be no more powerful than those described above.&lt;/p&gt;&lt;p&gt;The &lt;i&gt;curse&lt;/i&gt; bestowed by this spell cannot be dispelled, but it can be removed with a &lt;i&gt;break enchantment&lt;/i&gt;, &lt;i&gt;limited wish&lt;/i&gt;, &lt;i&gt;miracle&lt;/i&gt;, &lt;i&gt;remove curse&lt;/i&gt;, or &lt;i&gt;wish&lt;/i&gt; spell.&lt;/p&gt;&lt;p&gt;&lt;i&gt;Bestow curse&lt;/i&gt; counters &lt;i&gt;remove curse&lt;/i&gt;.&lt;/p&gt;&lt;p/&gt;
&lt;/div&gt;', 'SRD 3.5 SpellsA-B')
GO

INSERT INTO [spell] ([id], [name], [altname], [school], [subschool], [descriptor], [spellcraft_dc], [level], [components], [casting_time], [range], [target], [area], [effect], [duration], [saving_throw], [spell_resistance], [short_description], [to_develop], [material_components], [arcane_material_components], [focus], [description], [xp_cost], [arcane_focus], [wizard_focus], [verbal_components], [sorcerer_focus], [bard_focus], [cleric_focus], [druid_focus], [full_text], [reference])
VALUES 
  (131, 'Binding', 'binding', 'Enchantment', 'Compulsion', 'Mind-Affecting', NULL, 'Sorcerer/Wizard 8', 'The components for a binding spell vary according to the version of the spell, but they always include a continuous chanting utterance read from the scroll or spellbook page containing the spell, somatic gestures, and materials appropriate to the form of binding used. These components can include such items as miniature chains of special metals, soporific herbs of the rarest sort (for slumber bindings), a bell jar of the finest crystal, and the like.', 'One minute', 'Close (7,5 m (25 ft.) + 1,5 m (5 ft.)/2 levels)', 'One living creature', NULL, NULL, 'See Book (D)', 'Will negates; See Book', 'Yes', 'Utilizes an array of techniques to imprison a creature.', NULL, NULL, NULL, NULL, '&lt;p&gt;A &lt;i&gt;binding&lt;/i&gt; spell creates a magical restraint to hold a creature. The target gets an initial saving throw only if its Hit Dice equal at least one-half your caster level.&lt;/p&gt;
      &lt;p&gt;You may have as many as six assistants help you with the spell. For each assistant who casts &lt;i&gt;suggestion&lt;/i&gt;, your caster level for this casting of &lt;i&gt;binding&lt;/i&gt; increases by 1. For each assistant who casts &lt;i&gt;dominate animal&lt;/i&gt;,  &lt;i&gt;dominate person&lt;/i&gt;,  or &lt;i&gt;dominate monster&lt;/i&gt;,  your caster level for this casting of &lt;i&gt;binding&lt;/i&gt; increases by a number equal to one-third of that assistant''s level, provided that the spell''s target is appropriate for a &lt;i&gt;binding&lt;/i&gt; spell. Since the assistants'' spells are cast simply to improve your caster level for the purpose of the &lt;i&gt;binding&lt;/i&gt; spell, saving throws and spell resistance against the assistants'' spells are irrelevant. Your caster level determines whether the target gets an initial Will saving throw and how long the &lt;i&gt;binding&lt;/i&gt; lasts. All &lt;i&gt;binding&lt;/i&gt; spells are dismissible.&lt;/p&gt;
      &lt;p&gt;Regardless of the version of &lt;i&gt;binding&lt;/i&gt; you cast, you can specify triggering conditions that end the spell and release the creature whenever they occur. These triggers can be as simple or elaborate as you desire, but the condition must be reasonable and have a likelihood of coming to pass. The conditions can be based on a creature''s name, identity, or alignment but otherwise must be based on observable actions or qualities. Intangibles such as level, class, Hit Dice, or hit points don''t qualify. Once the spell is cast, its triggering conditions cannot be changed. Setting a release condition increases the save DC (assuming a saving throw is allowed) by 2.&lt;/p&gt;
      &lt;p&gt;If you are casting any of the first three versions of &lt;i&gt;binding&lt;/i&gt; (those with limited durations), you may cast additional &lt;i&gt;binding&lt;/i&gt; spells to prolong the effect, since the durations overlap. If you do so, the target gets a saving throw at the end of the first spell''s duration, even if your caster level was high enough to disallow an initial saving throw. If the creature succeeds on this save, all the &lt;i&gt;binding&lt;/i&gt; spells it has received are broken.&lt;/p&gt;
      &lt;p&gt;The &lt;i&gt;binding&lt;/i&gt; spell has six versions. Choose one of the following versions when you cast the spell.&lt;/p&gt;
      &lt;p&gt;&lt;i&gt;Chaining:&lt;/i&gt; The subject is confined by restraints that generate an &lt;i&gt;Antipathy&lt;/i&gt; spell affecting all creatures who approach the subject, except you. The duration is one year per caster level. The subject of this form of &lt;i&gt;binding&lt;/i&gt; is confined to the spot it occupied when it received the spell.&lt;/p&gt;
      &lt;p&gt;&lt;i&gt;Slumber:&lt;/i&gt; This version causes the subject to become comatose for as long as one year per caster level. The subject does not need to eat or drink while &lt;i&gt;slumbering&lt;/i&gt;, nor does it age. This form of &lt;i&gt;binding&lt;/i&gt; is more difficult to cast than &lt;i&gt;chaining&lt;/i&gt;, making it slightly easier to resist. Reduce the spell''s save DC by 1.&lt;/p&gt;
      &lt;p&gt;&lt;i&gt;Bound Slumber:&lt;/i&gt; This combination of &lt;i&gt;chaining&lt;/i&gt; and &lt;i&gt;slumber&lt;/i&gt; lasts for as long as one month per caster level. Reduce the save DC by 2.&lt;/p&gt;
      &lt;p&gt;&lt;i&gt;Hedged Prison:&lt;/i&gt; The subject is transported to or otherwise brought within a confined area from which it cannot wander by any means. The effect is permanent. Reduce the save DC by 3.&lt;/p&gt;
      &lt;p&gt;&lt;i&gt;Metamorphosis:&lt;/i&gt; The subject assumes gaseous form, except for its head or face. It is held harmless in a jar or other container, which may be transparent if you so choose. The creature remains aware of its surroundings and can speak, but it cannot leave the container, attack, or use any of its powers or abilities. The &lt;i&gt;binding&lt;/i&gt; is permanent. The subject does not need to breathe, eat, or drink while &lt;i&gt;metamorphosed&lt;/i&gt;, nor does it age. Reduce the save DC by 4.&lt;/p&gt;
      &lt;p&gt;&lt;i&gt;Minimus Containment:&lt;/i&gt; The subject is shrunk to a height of 1 inch or even less and held within some gem, jar, or similar object. The &lt;i&gt;binding&lt;/i&gt; is permanent. The subject does not need to breathe, eat, or drink while &lt;i&gt;contained&lt;/i&gt;, nor does it age. Reduce the save DC by 4.&lt;/p&gt;
      &lt;p&gt;You can''t dispel a &lt;i&gt;binding&lt;/i&gt; spell with &lt;i&gt;dispel magic&lt;/i&gt; or a similar effect, though an &lt;i&gt;antimagic field&lt;/i&gt; or &lt;i&gt;Mage''s disjunction&lt;/i&gt; affects it normally. A bound extraplanar creature cannot be sent back to its home plane due to &lt;i&gt;dismissal&lt;/i&gt;,  &lt;i&gt;banishment&lt;/i&gt;,  or a similar effect.&lt;/p&gt;
      &lt;p&gt;In addition to the specially made props suited to the specific type of &lt;i&gt;binding&lt;/i&gt; (cost 500 gp), the spell requires opals worth at least 500 gp for each HD of the target and a vellum depiction or carved statuette of the subject to be captured.&lt;/p&gt;
      &lt;p/&gt;', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '&lt;div topic=&quot;Binding&quot; level=&quot;5&quot;&gt;&lt;p&gt;&lt;h5&gt;Binding&lt;/h5&gt;&lt;/p&gt;&lt;table width=&quot;100%&quot; border=&quot;1&quot; cellpadding=&quot;2&quot; cellspacing=&quot;2&quot; frame=&quot;VOID&quot; rules=&quot;ROWS&quot;&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;/&gt;&lt;td&gt;Enchantment (Compulsion) [Mind-Affecting]&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Level:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Sorcerer/Wizard 8&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Components:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;V, S, M&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Casting Time:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;One minute&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Range:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Close (7,5 m (25 ft.) + 1,5 m (5 ft.)/2 levels)&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Target:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;One living creature&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Duration:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;See Book (D)&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Saving Throw:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Will negates; See Book&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Spell Resistance:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Yes&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;p&gt;A &lt;i&gt;binding&lt;/i&gt; spell creates a magical restraint to hold a creature. The target gets an initial saving throw only if its Hit Dice equal at least one-half your caster level.&lt;/p&gt;&lt;p&gt;You may have as many as six assistants help you with the spell. For each assistant who casts &lt;i&gt;suggestion&lt;/i&gt;, your caster level for this casting of &lt;i&gt;binding&lt;/i&gt; increases by 1. For each assistant who casts &lt;i&gt;dominate animal&lt;/i&gt;,  &lt;i&gt;dominate person&lt;/i&gt;,  or &lt;i&gt;dominate monster&lt;/i&gt;,  your caster level for this casting of &lt;i&gt;binding&lt;/i&gt; increases by a number equal to one-third of that assistant''s level, provided that the spell''s target is appropriate for a &lt;i&gt;binding&lt;/i&gt; spell. Since the assistants'' spells are cast simply to improve your caster level for the purpose of the &lt;i&gt;binding&lt;/i&gt; spell, saving throws and spell resistance against the assistants'' spells are irrelevant. Your caster level determines whether the target gets an initial Will saving throw and how long the &lt;i&gt;binding&lt;/i&gt; lasts. All &lt;i&gt;binding&lt;/i&gt; spells are dismissible.&lt;/p&gt;&lt;p&gt;Regardless of the version of &lt;i&gt;binding&lt;/i&gt; you cast, you can specify triggering conditions that end the spell and release the creature whenever they occur. These triggers can be as simple or elaborate as you desire, but the condition must be reasonable and have a likelihood of coming to pass. The conditions can be based on a creature''s name, identity, or alignment but otherwise must be based on observable actions or qualities. Intangibles such as level, class, Hit Dice, or hit points don''t qualify. Once the spell is cast, its triggering conditions cannot be changed. Setting a release condition increases the save DC (assuming a saving throw is allowed) by 2.&lt;/p&gt;&lt;p&gt;If you are casting any of the first three versions of &lt;i&gt;binding&lt;/i&gt; (those with limited durations), you may cast additional &lt;i&gt;binding&lt;/i&gt; spells to prolong the effect, since the durations overlap. If you do so, the target gets a saving throw at the end of the first spell''s duration, even if your caster level was high enough to disallow an initial saving throw. If the creature succeeds on this save, all the &lt;i&gt;binding&lt;/i&gt; spells it has received are broken.&lt;/p&gt;&lt;p&gt;The &lt;i&gt;binding&lt;/i&gt; spell has six versions. Choose one of the following versions when you cast the spell.&lt;/p&gt;&lt;p&gt;&lt;i&gt;Chaining:&lt;/i&gt; The subject is confined by restraints that generate an &lt;i&gt;Antipathy&lt;/i&gt; spell affecting all creatures who approach the subject, except you. The duration is one year per caster level. The subject of this form of &lt;i&gt;binding&lt;/i&gt; is confined to the spot it occupied when it received the spell.&lt;/p&gt;&lt;p&gt;&lt;i&gt;Slumber:&lt;/i&gt; This version causes the subject to become comatose for as long as one year per caster level. The subject does not need to eat or drink while &lt;i&gt;slumbering&lt;/i&gt;, nor does it age. This form of &lt;i&gt;binding&lt;/i&gt; is more difficult to cast than &lt;i&gt;chaining&lt;/i&gt;, making it slightly easier to resist. Reduce the spell''s save DC by 1.&lt;/p&gt;&lt;p&gt;&lt;i&gt;Bound Slumber:&lt;/i&gt; This combination of &lt;i&gt;chaining&lt;/i&gt; and &lt;i&gt;slumber&lt;/i&gt; lasts for as long as one month per caster level. Reduce the save DC by 2.&lt;/p&gt;&lt;p&gt;&lt;i&gt;Hedged Prison:&lt;/i&gt; The subject is transported to or otherwise brought within a confined area from which it cannot wander by any means. The effect is permanent. Reduce the save DC by 3.&lt;/p&gt;&lt;p&gt;&lt;i&gt;Metamorphosis:&lt;/i&gt; The subject assumes gaseous form, except for its head or face. It is held harmless in a jar or other container, which may be transparent if you so choose. The creature remains aware of its surroundings and can speak, but it cannot leave the container, attack, or use any of its powers or abilities. The &lt;i&gt;binding&lt;/i&gt; is permanent. The subject does not need to breathe, eat, or drink while &lt;i&gt;metamorphosed&lt;/i&gt;, nor does it age. Reduce the save DC by 4.&lt;/p&gt;&lt;p&gt;&lt;i&gt;Minimus Containment:&lt;/i&gt; The subject is shrunk to a height of 1 inch or even less and held within some gem, jar, or similar object. The &lt;i&gt;binding&lt;/i&gt; is permanent. The subject does not need to breathe, eat, or drink while &lt;i&gt;contained&lt;/i&gt;, nor does it age. Reduce the save DC by 4.&lt;/p&gt;&lt;p&gt;You can''t dispel a &lt;i&gt;binding&lt;/i&gt; spell with &lt;i&gt;dispel magic&lt;/i&gt; or a similar effect, though an &lt;i&gt;antimagic field&lt;/i&gt; or &lt;i&gt;Mage''s disjunction&lt;/i&gt; affects it normally. A bound extraplanar creature cannot be sent back to its home plane due to &lt;i&gt;dismissal&lt;/i&gt;,  &lt;i&gt;banishment&lt;/i&gt;,  or a similar effect.&lt;/p&gt;&lt;p&gt;&lt;i&gt;Components:&lt;/i&gt; The components for a &lt;i&gt;binding&lt;/i&gt; spell vary according to the version of the spell, but they always include a continuous chanting utterance read from the scroll or spellbook page containing the spell, somatic gestures, and materials appropriate to the form of &lt;i&gt;binding&lt;/i&gt; used. These components can include such items as miniature chains of special metals, soporific herbs of the rarest sort (for &lt;i&gt;slumber&lt;/i&gt; bindings), a bell jar of the finest crystal, and the like.&lt;/p&gt;&lt;p&gt;In addition to the specially made props suited to the specific type of &lt;i&gt;binding&lt;/i&gt; (cost 500 gp), the spell requires opals worth at least 500 gp for each HD of the target and a vellum depiction or carved statuette of the subject to be captured.&lt;/p&gt;&lt;p/&gt;
&lt;/div&gt;', 'SRD 3.5 SpellsA-B')
GO

INSERT INTO [spell] ([id], [name], [altname], [school], [subschool], [descriptor], [spellcraft_dc], [level], [components], [casting_time], [range], [target], [area], [effect], [duration], [saving_throw], [spell_resistance], [short_description], [to_develop], [material_components], [arcane_material_components], [focus], [description], [xp_cost], [arcane_focus], [wizard_focus], [verbal_components], [sorcerer_focus], [bard_focus], [cleric_focus], [druid_focus], [full_text], [reference])
VALUES 
  (132, 'Black Tentacles', 'black tentacles', 'Conjuration', 'Creation', NULL, NULL, 'Sorcerer/Wizard 4', 'V, S, M', '1 standard action', 'Medium (30 m (100 ft.) + 3 m (10 ft.)/level)', NULL, '6 m (20-ft.). -radius spread', NULL, '1 round/level (D)', 'None', 'No', 'Tentacles grapple all within 6 m (20 ft.) spread.', NULL, 'A piece of tentacle from a giant octopus or a giant squid.', NULL, NULL, '&lt;p&gt;This spell conjures a field of rubbery black tentacles, each 10 feet long. These waving members seem to spring forth from the earth, floor, or whatever surface is underfoot-including water. They grasp and entwine around creatures that enter the area, holding them fast and crushing them with great strength.&lt;/p&gt;
      &lt;p&gt;Every creature within the area of the spell must make a grapple check, opposed by the grapple check of the tentacles. Treat the tentacles attacking a particular target as a Large creature with a base attack bonus equal to your caster level and a Strength score of 19. Thus, its grapple check modifier is equal to your caster level +8. The tentacles are immune to all types of damage.&lt;/p&gt;
      &lt;p&gt;Once the tentacles grapple an opponent, they may make a grapple check each round on your turn to deal 1d6+4 points of bludgeoning damage. The tentacles continue to crush the opponent until the spell ends or the opponent escapes.&lt;/p&gt;
      &lt;p&gt;Any creature that enters the area of the spell is immediately attacked by the tentacles. Even creatures who aren''t grappling with the tentacles may move through the area at only half normal speed.&lt;/p&gt;
      &lt;p/&gt;', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '&lt;div topic=&quot;Black Tentacles&quot; level=&quot;5&quot;&gt;&lt;p&gt;&lt;h5&gt;Black Tentacles&lt;/h5&gt;&lt;/p&gt;&lt;table width=&quot;100%&quot; border=&quot;1&quot; cellpadding=&quot;2&quot; cellspacing=&quot;2&quot; frame=&quot;VOID&quot; rules=&quot;ROWS&quot;&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;/&gt;&lt;td&gt;Conjuration (Creation)&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Level:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Sorcerer/Wizard 4&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Components:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;V, S, M&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Casting Time:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;1 standard action&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Range:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Medium (30 m (100 ft.) + 3 m (10 ft.)/level)&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Area:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;6 m (20-ft.). -radius spread&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Duration:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;1 round/level (D)&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Saving Throw:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;None&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Spell Resistance:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;No&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;p&gt;This spell conjures a field of rubbery black tentacles, each 10 feet long. These waving members seem to spring forth from the earth, floor, or whatever surface is underfoot-including water. They grasp and entwine around creatures that enter the area, holding them fast and crushing them with great strength.&lt;/p&gt;&lt;p&gt;Every creature within the area of the spell must make a grapple check, opposed by the grapple check of the tentacles. Treat the tentacles attacking a particular target as a Large creature with a base attack bonus equal to your caster level and a Strength score of 19. Thus, its grapple check modifier is equal to your caster level +8. The tentacles are immune to all types of damage.&lt;/p&gt;&lt;p&gt;Once the tentacles grapple an opponent, they may make a grapple check each round on your turn to deal 1d6+4 points of bludgeoning damage. The tentacles continue to crush the opponent until the spell ends or the opponent escapes.&lt;/p&gt;&lt;p&gt;Any creature that enters the area of the spell is immediately attacked by the tentacles. Even creatures who aren''t grappling with the tentacles may move through the area at only half normal speed.&lt;/p&gt;&lt;p&gt;&lt;i&gt;Material Component:&lt;/i&gt; A piece of tentacle from a giant octopus or a giant squid.&lt;/p&gt;&lt;p/&gt;
&lt;/div&gt;', 'SRD 3.5 SpellsA-B')
GO

INSERT INTO [spell] ([id], [name], [altname], [school], [subschool], [descriptor], [spellcraft_dc], [level], [components], [casting_time], [range], [target], [area], [effect], [duration], [saving_throw], [spell_resistance], [short_description], [to_develop], [material_components], [arcane_material_components], [focus], [description], [xp_cost], [arcane_focus], [wizard_focus], [verbal_components], [sorcerer_focus], [bard_focus], [cleric_focus], [druid_focus], [full_text], [reference])
VALUES 
  (133, 'Blade Barrier', 'blade barrier', 'Evocation', NULL, 'Force', NULL, 'Cleric 6, Good 6, War 6', 'V, S', '1 standard action', 'Medium (30 m (100 ft.) + 3 m (10 ft.)/level)', NULL, NULL, 'Wall of whirling blades up to 6 m (20 ft.) long/level, or a ringed wall of whirling blades with a radius of up to 1,5 m (5 ft.) per two levels; either form 6 m (20 ft.) high', '1 min./level (D)', 'Reflex half or Reflex negates; See Book', 'Yes', 'Wall of blades deals 1d6/level damage.', NULL, NULL, NULL, NULL, '&lt;p&gt;An immobile, vertical curtain of whirling blades shaped of pure force springs into existence. Any creature passing through the wall takes 1d6 points of damage per caster level (maximum 15d6), with a Reflex save for half damage.&lt;/p&gt;
      &lt;p&gt;If you evoke the barrier so that it appears where creatures are, each creature takes damage as if passing through the wall. Each such creature can avoid the wall (ending up on the side of its choice) and thus take no damage by making a successful Reflex save.&lt;/p&gt;
      &lt;p&gt;A &lt;i&gt;blade barrier&lt;/i&gt; provides cover (+4 bonus to AC, +2 bonus on Reflex saves) against attacks made through it.&lt;/p&gt;
      &lt;p/&gt;', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '&lt;div topic=&quot;Blade Barrier&quot; level=&quot;5&quot;&gt;&lt;p&gt;&lt;h5&gt;Blade Barrier&lt;/h5&gt;&lt;/p&gt;&lt;table width=&quot;100%&quot; border=&quot;1&quot; cellpadding=&quot;2&quot; cellspacing=&quot;2&quot; frame=&quot;VOID&quot; rules=&quot;ROWS&quot;&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;/&gt;&lt;td&gt;Evocation [Force]&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Level:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Cleric 6, Good 6, War 6&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Components:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;V, S&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Casting Time:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;1 standard action&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Range:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Medium (30 m (100 ft.) + 3 m (10 ft.)/level)&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Effect:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Wall of whirling blades up to 6 m (20 ft.) long/level, or a ringed wall of whirling blades with a radius of up to 1,5 m (5 ft.) per two levels; either form 6 m (20 ft.) high&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Duration:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;1 min./level (D)&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Saving Throw:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Reflex half or Reflex negates; See Book&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Spell Resistance:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Yes&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;p&gt;An immobile, vertical curtain of whirling blades shaped of pure force springs into existence. Any creature passing through the wall takes 1d6 points of damage per caster level (maximum 15d6), with a Reflex save for half damage.&lt;/p&gt;&lt;p&gt;If you evoke the barrier so that it appears where creatures are, each creature takes damage as if passing through the wall. Each such creature can avoid the wall (ending up on the side of its choice) and thus take no damage by making a successful Reflex save.&lt;/p&gt;&lt;p&gt;A &lt;i&gt;blade barrier&lt;/i&gt; provides cover (+4 bonus to AC, +2 bonus on Reflex saves) against attacks made through it.&lt;/p&gt;&lt;p/&gt;
&lt;/div&gt;', 'SRD 3.5 SpellsA-B')
GO

INSERT INTO [spell] ([id], [name], [altname], [school], [subschool], [descriptor], [spellcraft_dc], [level], [components], [casting_time], [range], [target], [area], [effect], [duration], [saving_throw], [spell_resistance], [short_description], [to_develop], [material_components], [arcane_material_components], [focus], [description], [xp_cost], [arcane_focus], [wizard_focus], [verbal_components], [sorcerer_focus], [bard_focus], [cleric_focus], [druid_focus], [full_text], [reference])
VALUES 
  (134, 'Blasphemy', 'blasphemy', 'Evocation', NULL, 'Evil, Sonic', NULL, 'Cleric 7, Evil 7', 'V', '1 standard action', '40 ft.', NULL, 'Nonevil creatures in a 12 m (40-ft.) -radius spread centered on you', NULL, 'Instantaneous', 'None or Will negates; See Book', 'Yes', 'Kills, paralyzes, weakens, or dazes nonevil subjects.', NULL, NULL, NULL, NULL, '&lt;p&gt;Any nonevil creature within the area of a &lt;i&gt;blasphemy&lt;/i&gt; spell suffers the following ill effects.&lt;/p&gt;
      &lt;table width=&quot;100%&quot; border=&quot;1&quot; cellpadding=&quot;2&quot; cellspacing=&quot;2&quot; frame=&quot;VOID&quot; rules=&quot;ROWS&quot;&gt;

&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;
&lt;td&gt;&lt;b&gt;HD&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;
&lt;td&gt;&lt;b&gt;Effect&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;

&lt;/tr&gt;

&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;
&lt;td&gt;Equal to caster level&lt;br/&gt;&lt;/td&gt;
&lt;td&gt;Dazed&lt;br/&gt;&lt;/td&gt;

&lt;/tr&gt;
&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;
&lt;td&gt;Up to caster level -1&lt;br/&gt;&lt;/td&gt;
&lt;td&gt;Weakened, dazed&lt;br/&gt;&lt;/td&gt;

&lt;/tr&gt;
&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;
&lt;td&gt;Up to caster level -5&lt;br/&gt;&lt;/td&gt;
&lt;td&gt;Paralyzed, weakened, dazed&lt;br/&gt;&lt;/td&gt;

&lt;/tr&gt;

&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;
&lt;td&gt;Up to caster level -10&lt;br/&gt;&lt;/td&gt;
&lt;td&gt;Killed, paralyzed, weakened, dazed&lt;br/&gt;&lt;/td&gt;

&lt;/tr&gt;
&lt;/table&gt;
      &lt;p&gt;The effects are cumulative and concurrent.&lt;/p&gt;
      &lt;p&gt;No saving throw is allowed against these effects.&lt;/p&gt;
      &lt;p&gt;&lt;i&gt;Dazed:&lt;/i&gt; The creature can take no actions for 1 round, though it defends itself normally.&lt;/p&gt;
      &lt;p&gt;&lt;i&gt;Weakened:&lt;/i&gt; The creature''s Strength score decreases by 2d6 points for 2d4 rounds.&lt;/p&gt;
      &lt;p&gt;&lt;i&gt;Paralyzed:&lt;/i&gt; The creature is paralyzed and helpless for 1d10 minutes.&lt;/p&gt;
      &lt;p&gt;&lt;i&gt;Killed:&lt;/i&gt; Living creatures die. Undead creatures are destroyed.&lt;/p&gt;
      &lt;p&gt;Furthermore, if you are on your home plane when you cast this spell, nonevil extraplanar creatures within the area are instantly banished back to their home planes. Creatures so banished cannot return for at least 24 hours. This effect takes place regardless of whether the creatures hear the &lt;i&gt;blasphemy&lt;/i&gt;. The banishment effect allows a Will save (at a -4 penalty) to negate.&lt;/p&gt;
      &lt;p&gt;Creatures whose Hit Dice exceed your caster level are unaffected by &lt;i&gt;blasphemy&lt;/i&gt;.&lt;/p&gt;
      &lt;p/&gt;', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '&lt;div topic=&quot;Blasphemy&quot; level=&quot;5&quot;&gt;&lt;p&gt;&lt;h5&gt;Blasphemy&lt;/h5&gt;&lt;/p&gt;&lt;table width=&quot;100%&quot; border=&quot;1&quot; cellpadding=&quot;2&quot; cellspacing=&quot;2&quot; frame=&quot;VOID&quot; rules=&quot;ROWS&quot;&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;/&gt;&lt;td&gt;Evocation [Evil, Sonic]&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Level:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Cleric 7, Evil 7&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Components:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;V&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Casting Time:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;1 standard action&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Range:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;40 ft.&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Area:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Nonevil creatures in a 12 m (40-ft.) -radius spread centered on you&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Duration:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Instantaneous&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Saving Throw:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;None or Will negates; See Book&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Spell Resistance:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Yes&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;p&gt;Any nonevil creature within the area of a &lt;i&gt;blasphemy&lt;/i&gt; spell suffers the following ill effects.&lt;/p&gt;&lt;table width=&quot;100%&quot; border=&quot;1&quot; cellpadding=&quot;2&quot; cellspacing=&quot;2&quot; frame=&quot;VOID&quot; rules=&quot;ROWS&quot;&gt;

&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;
&lt;td&gt;&lt;b&gt;HD&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;
&lt;td&gt;&lt;b&gt;Effect&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;

&lt;/tr&gt;

&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;
&lt;td&gt;Equal to caster level&lt;br/&gt;&lt;/td&gt;
&lt;td&gt;Dazed&lt;br/&gt;&lt;/td&gt;

&lt;/tr&gt;
&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;
&lt;td&gt;Up to caster level -1&lt;br/&gt;&lt;/td&gt;
&lt;td&gt;Weakened, dazed&lt;br/&gt;&lt;/td&gt;

&lt;/tr&gt;
&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;
&lt;td&gt;Up to caster level -5&lt;br/&gt;&lt;/td&gt;
&lt;td&gt;Paralyzed, weakened, dazed&lt;br/&gt;&lt;/td&gt;

&lt;/tr&gt;

&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;
&lt;td&gt;Up to caster level -10&lt;br/&gt;&lt;/td&gt;
&lt;td&gt;Killed, paralyzed, weakened, dazed&lt;br/&gt;&lt;/td&gt;

&lt;/tr&gt;
&lt;/table&gt;&lt;p&gt;The effects are cumulative and concurrent.&lt;/p&gt;&lt;p&gt;No saving throw is allowed against these effects.&lt;/p&gt;&lt;p&gt;&lt;i&gt;Dazed:&lt;/i&gt; The creature can take no actions for 1 round, though it defends itself normally.&lt;/p&gt;&lt;p&gt;&lt;i&gt;Weakened:&lt;/i&gt; The creature''s Strength score decreases by 2d6 points for 2d4 rounds.&lt;/p&gt;&lt;p&gt;&lt;i&gt;Paralyzed:&lt;/i&gt; The creature is paralyzed and helpless for 1d10 minutes.&lt;/p&gt;&lt;p&gt;&lt;i&gt;Killed:&lt;/i&gt; Living creatures die. Undead creatures are destroyed.&lt;/p&gt;&lt;p&gt;Furthermore, if you are on your home plane when you cast this spell, nonevil extraplanar creatures within the area are instantly banished back to their home planes. Creatures so banished cannot return for at least 24 hours. This effect takes place regardless of whether the creatures hear the &lt;i&gt;blasphemy&lt;/i&gt;. The banishment effect allows a Will save (at a -4 penalty) to negate.&lt;/p&gt;&lt;p&gt;Creatures whose Hit Dice exceed your caster level are unaffected by &lt;i&gt;blasphemy&lt;/i&gt;.&lt;/p&gt;&lt;p/&gt;
&lt;/div&gt;', 'SRD 3.5 SpellsA-B')
GO

INSERT INTO [spell] ([id], [name], [altname], [school], [subschool], [descriptor], [spellcraft_dc], [level], [components], [casting_time], [range], [target], [area], [effect], [duration], [saving_throw], [spell_resistance], [short_description], [to_develop], [material_components], [arcane_material_components], [focus], [description], [xp_cost], [arcane_focus], [wizard_focus], [verbal_components], [sorcerer_focus], [bard_focus], [cleric_focus], [druid_focus], [full_text], [reference])
VALUES 
  (135, 'Bless', 'bless', 'Enchantment', 'Compulsion', 'Mind-Affecting', NULL, 'Cleric 1, Paladin 1', 'V, S, DF', '1 standard action', '50 ft.', NULL, 'The caster and all allies within a 15 m (50-ft.) burst, centered on the caster', NULL, '1 min./level', 'None', 'Yes (harmless)', 'Allies gain +1 on attack rolls and +1 on saves against fear.', NULL, NULL, NULL, NULL, '&lt;p&gt;&lt;i&gt;Bless&lt;/i&gt; fills your allies with courage. Each ally gains a +1 morale bonus on attack rolls and on saving throws against fear effects.&lt;/p&gt;
      &lt;p&gt;&lt;i&gt;Bless&lt;/i&gt; counters and dispels &lt;i&gt;bane&lt;/i&gt;.&lt;/p&gt;
      &lt;p/&gt;', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '&lt;div topic=&quot;Bless&quot; level=&quot;5&quot;&gt;&lt;p&gt;&lt;h5&gt;Bless&lt;/h5&gt;&lt;/p&gt;&lt;table width=&quot;100%&quot; border=&quot;1&quot; cellpadding=&quot;2&quot; cellspacing=&quot;2&quot; frame=&quot;VOID&quot; rules=&quot;ROWS&quot;&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;/&gt;&lt;td&gt;Enchantment (Compulsion) [Mind-Affecting]&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Level:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Cleric 1, Paladin 1&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Components:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;V, S, DF&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Casting Time:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;1 standard action&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Range:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;50 ft.&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Area:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;The caster and all allies within a 15 m (50-ft.) burst, centered on the caster&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Duration:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;1 min./level&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Saving Throw:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;None&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Spell Resistance:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Yes (harmless)&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;p&gt;&lt;i&gt;Bless&lt;/i&gt; fills your allies with courage. Each ally gains a +1 morale bonus on attack rolls and on saving throws against fear effects.&lt;/p&gt;&lt;p&gt;&lt;i&gt;Bless&lt;/i&gt; counters and dispels &lt;i&gt;bane&lt;/i&gt;.&lt;/p&gt;&lt;p/&gt;
&lt;/div&gt;', 'SRD 3.5 SpellsA-B')
