INSERT INTO [spell] ([id], [name], [altname], [school], [subschool], [descriptor], [spellcraft_dc], [level], [components], [casting_time], [range], [target], [area], [effect], [duration], [saving_throw], [spell_resistance], [short_description], [to_develop], [material_components], [arcane_material_components], [focus], [description], [xp_cost], [arcane_focus], [wizard_focus], [verbal_components], [sorcerer_focus], [bard_focus], [cleric_focus], [druid_focus], [full_text], [reference])
VALUES 
  (145, 'Burning Hands', 'burning hands', 'Evocation', NULL, 'Fire', NULL, 'Fire 1, Sorcerer/Wizard 1', 'V, S', '1 standard action', '15 ft.', NULL, 'Cone-shaped burst', NULL, 'Instantaneous', 'Reflex half', 'Yes', '1d4/level fire damage (max 5d4).', NULL, NULL, NULL, NULL, '&lt;p&gt;A cone of searing flame shoots from your fingertips. Any creature in the area of the flames takes 1d4 points of fire damage per caster level (maximum 5d4). Flammable materials burn if the flames touch them. A character can extinguish burning items as a full-round action.&lt;/p&gt;
      &lt;p/&gt;', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '&lt;div topic=&quot;Burning Hands&quot; level=&quot;5&quot;&gt;&lt;p&gt;&lt;h5&gt;Burning Hands&lt;/h5&gt;&lt;/p&gt;&lt;table width=&quot;100%&quot; border=&quot;1&quot; cellpadding=&quot;2&quot; cellspacing=&quot;2&quot; frame=&quot;VOID&quot; rules=&quot;ROWS&quot;&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;/&gt;&lt;td&gt;Evocation [Fire]&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Level:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Fire 1, Sorcerer/Wizard 1&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Components:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;V, S&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Casting Time:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;1 standard action&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Range:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;15 ft.&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Area:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Cone-shaped burst&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Duration:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Instantaneous&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Saving Throw:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Reflex half&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Spell Resistance:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Yes&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;p&gt;A cone of searing flame shoots from your fingertips. Any creature in the area of the flames takes 1d4 points of fire damage per caster level (maximum 5d4). Flammable materials burn if the flames touch them. A character can extinguish burning items as a full-round action.&lt;/p&gt;&lt;p/&gt;
&lt;/div&gt;', 'SRD 3.5 SpellsA-B')
GO

INSERT INTO [spell] ([id], [name], [altname], [school], [subschool], [descriptor], [spellcraft_dc], [level], [components], [casting_time], [range], [target], [area], [effect], [duration], [saving_throw], [spell_resistance], [short_description], [to_develop], [material_components], [arcane_material_components], [focus], [description], [xp_cost], [arcane_focus], [wizard_focus], [verbal_components], [sorcerer_focus], [bard_focus], [cleric_focus], [druid_focus], [full_text], [reference])
VALUES 
  (146, 'Call Lightning', 'call lightning', 'Evocation', NULL, 'Electricity', NULL, 'Druid 3', 'V, S', '1 round', 'Medium (30 m (100 ft.) + 3 m (10 ft.)/level)', NULL, NULL, 'One or more 9 m (30-ft.) -long vertical lines of lightning', '1 min./level', 'Reflex half', 'Yes', 'Calls down lightning bolts (3d6 per bolt) from sky.', NULL, NULL, NULL, NULL, '&lt;p&gt;Immediately upon completion of the spell, and once per round thereafter, you may call down a 5-foot-wide, 30-foot-long, vertical bolt of lightning that deals 3d6 points of electricity damage. The bolt of lightning flashes down in a vertical stroke at whatever target point you choose within the spell''s range (measured from your position at the time). Any creature in the target square or in the path of the bolt is affected.&lt;/p&gt;
      &lt;p&gt;You need not call a bolt of lightning immediately; other actions, even spellcasting, can be performed. However, each round after the first you may use a standard action (concentrating on the spell) to call a bolt. You may call a total number of bolts equal to your caster level (maximum 10 bolts).&lt;/p&gt;
      &lt;p&gt;If you are outdoors and in a stormy area-a rain shower, clouds and wind, hot and cloudy conditions, or even a tornado (including a whirlwind formed by a djinni or an air elemental of at least Large size)-each bolt deals 3d10 points of electricity damage instead of 3d6.&lt;/p&gt;
      &lt;p&gt;This spell functions indoors or underground but not underwater.&lt;/p&gt;
      &lt;p/&gt;', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '&lt;div topic=&quot;Call Lightning&quot; level=&quot;5&quot;&gt;&lt;p&gt;&lt;h5&gt;Call Lightning&lt;/h5&gt;&lt;/p&gt;&lt;table width=&quot;100%&quot; border=&quot;1&quot; cellpadding=&quot;2&quot; cellspacing=&quot;2&quot; frame=&quot;VOID&quot; rules=&quot;ROWS&quot;&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;/&gt;&lt;td&gt;Evocation [Electricity]&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Level:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Druid 3&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Components:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;V, S&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Casting Time:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;1 round&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Range:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Medium (30 m (100 ft.) + 3 m (10 ft.)/level)&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Effect:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;One or more 9 m (30-ft.) -long vertical lines of lightning&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Duration:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;1 min./level&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Saving Throw:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Reflex half&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Spell Resistance:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Yes&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;p&gt;Immediately upon completion of the spell, and once per round thereafter, you may call down a 5-foot-wide, 30-foot-long, vertical bolt of lightning that deals 3d6 points of electricity damage. The bolt of lightning flashes down in a vertical stroke at whatever target point you choose within the spell''s range (measured from your position at the time). Any creature in the target square or in the path of the bolt is affected.&lt;/p&gt;&lt;p&gt;You need not call a bolt of lightning immediately; other actions, even spellcasting, can be performed. However, each round after the first you may use a standard action (concentrating on the spell) to call a bolt. You may call a total number of bolts equal to your caster level (maximum 10 bolts).&lt;/p&gt;&lt;p&gt;If you are outdoors and in a stormy area-a rain shower, clouds and wind, hot and cloudy conditions, or even a tornado (including a whirlwind formed by a djinni or an air elemental of at least Large size)-each bolt deals 3d10 points of electricity damage instead of 3d6.&lt;/p&gt;&lt;p&gt;This spell functions indoors or underground but not underwater.&lt;/p&gt;&lt;p/&gt;
&lt;/div&gt;', 'SRD 3.5 SpellsC')
GO

INSERT INTO [spell] ([id], [name], [altname], [school], [subschool], [descriptor], [spellcraft_dc], [level], [components], [casting_time], [range], [target], [area], [effect], [duration], [saving_throw], [spell_resistance], [short_description], [to_develop], [material_components], [arcane_material_components], [focus], [description], [xp_cost], [arcane_focus], [wizard_focus], [verbal_components], [sorcerer_focus], [bard_focus], [cleric_focus], [druid_focus], [full_text], [reference])
VALUES 
  (147, 'Call Lightning Storm', 'call lightning storm', 'Evocation', NULL, 'Electricity', NULL, 'Druid 5', 'V, S', '1 round', 'Long (400 ft. + 12 m (40 ft.)/level)', NULL, NULL, 'One or more 9 m (30-ft.) -long vertical lines of lightning', '1 min./level', 'Reflex half', 'Yes', 'As call lightning, but 5d6 damage per bolt.', NULL, NULL, NULL, NULL, '&lt;p&gt;This spell functions like &lt;i&gt;call lightning&lt;/i&gt;, except that each bolt deals 5d6 points of electricity damage (or 5d10 if created outdoors in a stormy area), and you may call a maximum of 15 bolts.&lt;/p&gt;
      &lt;p/&gt;', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '&lt;div topic=&quot;Call Lightning Storm&quot; level=&quot;5&quot;&gt;&lt;p&gt;&lt;h5&gt;Call Lightning Storm&lt;/h5&gt;&lt;/p&gt;&lt;table width=&quot;100%&quot; border=&quot;1&quot; cellpadding=&quot;2&quot; cellspacing=&quot;2&quot; frame=&quot;VOID&quot; rules=&quot;ROWS&quot;&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;/&gt;&lt;td&gt;Evocation [Electricity]&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Level:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Druid 5&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Range:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Long (400 ft. + 12 m (40 ft.)/level)&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;p&gt;This spell functions like &lt;i&gt;call lightning&lt;/i&gt;, except that each bolt deals 5d6 points of electricity damage (or 5d10 if created outdoors in a stormy area), and you may call a maximum of 15 bolts.&lt;/p&gt;&lt;p/&gt;
&lt;/div&gt;', 'SRD 3.5 SpellsC')
GO

INSERT INTO [spell] ([id], [name], [altname], [school], [subschool], [descriptor], [spellcraft_dc], [level], [components], [casting_time], [range], [target], [area], [effect], [duration], [saving_throw], [spell_resistance], [short_description], [to_develop], [material_components], [arcane_material_components], [focus], [description], [xp_cost], [arcane_focus], [wizard_focus], [verbal_components], [sorcerer_focus], [bard_focus], [cleric_focus], [druid_focus], [full_text], [reference])
VALUES 
  (148, 'Calm Animals', 'calm animals', 'Enchantment', 'Compulsion', 'Mind-Affecting', NULL, 'Animal 1, Druid 1, Ranger 1', 'V, S', '1 standard action', 'Close (7,5 m (25 ft.) + 1,5 m (5 ft.)/2 levels)', 'Animals within 9 m (30 ft.) of each other', NULL, NULL, '1 min./level', 'Will negates; See Book', 'Yes', 'Calms (2d4 + level) HD of animals.', NULL, NULL, NULL, NULL, '&lt;p&gt;This spell soothes and quiets animals, rendering them docile and harmless. Only ordinary animals (those with Intelligence scores of 1 or 2) can be affected by this spell. All the subjects must be of the same kind, and no two may be more than 30 feet apart. The maximum number of Hit Dice of animals you can affect is equal to 2d4 + caster level. A dire animal or an animal trained to attack or guard is allowed a saving throw; other animals are not.&lt;/p&gt;
      &lt;p&gt;The affected creatures remain where they are and do not attack or flee. They are not helpless and defend themselves normally if attacked. Any threat breaks the spell on the threatened creatures.&lt;/p&gt;
      &lt;p/&gt;', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '&lt;div topic=&quot;Calm Animals&quot; level=&quot;5&quot;&gt;&lt;p&gt;&lt;h5&gt;Calm Animals&lt;/h5&gt;&lt;/p&gt;&lt;table width=&quot;100%&quot; border=&quot;1&quot; cellpadding=&quot;2&quot; cellspacing=&quot;2&quot; frame=&quot;VOID&quot; rules=&quot;ROWS&quot;&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;/&gt;&lt;td&gt;Enchantment (Compulsion) [Mind-Affecting]&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Level:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Animal 1, Druid 1, Ranger 1&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Components:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;V, S&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Casting Time:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;1 standard action&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Range:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Close (7,5 m (25 ft.) + 1,5 m (5 ft.)/2 levels)&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Targets:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Animals within 9 m (30 ft.) of each other&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Duration:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;1 min./level&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Saving Throw:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Will negates; See Book&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Spell Resistance:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Yes&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;p&gt;This spell soothes and quiets animals, rendering them docile and harmless. Only ordinary animals (those with Intelligence scores of 1 or 2) can be affected by this spell. All the subjects must be of the same kind, and no two may be more than 30 feet apart. The maximum number of Hit Dice of animals you can affect is equal to 2d4 + caster level. A dire animal or an animal trained to attack or guard is allowed a saving throw; other animals are not.&lt;/p&gt;&lt;p&gt;The affected creatures remain where they are and do not attack or flee. They are not helpless and defend themselves normally if attacked. Any threat breaks the spell on the threatened creatures.&lt;/p&gt;&lt;p/&gt;
&lt;/div&gt;', 'SRD 3.5 SpellsC')
GO

INSERT INTO [spell] ([id], [name], [altname], [school], [subschool], [descriptor], [spellcraft_dc], [level], [components], [casting_time], [range], [target], [area], [effect], [duration], [saving_throw], [spell_resistance], [short_description], [to_develop], [material_components], [arcane_material_components], [focus], [description], [xp_cost], [arcane_focus], [wizard_focus], [verbal_components], [sorcerer_focus], [bard_focus], [cleric_focus], [druid_focus], [full_text], [reference])
VALUES 
  (149, 'Calm Emotions', 'calm emotions', 'Enchantment', 'Compulsion', 'Mind-Affecting', NULL, 'Bard 2, Cleric 2, Law 2', 'V, S, DF', '1 standard action', 'Medium (30 m (100 ft.) + 3 m (10 ft.)/level)', NULL, 'Creatures in a 6 m (20-ft.). -radius spread', NULL, 'Concentration, up to 1 round/level (D)', 'Will negates', 'Yes', 'Calms creatures, negating emotion effects.', NULL, NULL, NULL, NULL, '&lt;p&gt;This spell calms agitated creatures. You have no control over the affected creatures, but &lt;i&gt;calm emotions&lt;/i&gt; can stop raging creatures from fighting or joyous ones from reveling. Creatures so affected cannot take violent actions (although they can defend themselves) or do anything destructive. Any aggressive action against or damage dealt to a calmed creature immediately breaks the spell on all calmed creatures.&lt;/p&gt;
      &lt;p&gt;This spell automatically suppresses (but does not dispel) any morale bonuses granted by spells such as &lt;i&gt;bless&lt;/i&gt;,  &lt;i&gt;good hope&lt;/i&gt;,  and &lt;i&gt;rage&lt;/i&gt;,  as well as negating a bard''s ability to inspire courage or a barbarian''s rage ability. It also suppresses any fear effects and removes the &lt;i&gt;confused&lt;/i&gt; condition from all targets. While the spell lasts, a suppressed spell or effect has no effect. When the &lt;i&gt;calm emotions&lt;/i&gt; spell ends, the original spell or effect takes hold of the creature again, provided that its duration has not expired in the meantime.&lt;/p&gt;
      &lt;p/&gt;', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '&lt;div topic=&quot;Calm Emotions&quot; level=&quot;5&quot;&gt;&lt;p&gt;&lt;h5&gt;Calm Emotions&lt;/h5&gt;&lt;/p&gt;&lt;table width=&quot;100%&quot; border=&quot;1&quot; cellpadding=&quot;2&quot; cellspacing=&quot;2&quot; frame=&quot;VOID&quot; rules=&quot;ROWS&quot;&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;/&gt;&lt;td&gt;Enchantment (Compulsion) [Mind-Affecting]&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Level:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Bard 2, Cleric 2, Law 2&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Components:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;V, S, DF&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Casting Time:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;1 standard action&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Range:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Medium (30 m (100 ft.) + 3 m (10 ft.)/level)&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Area:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Creatures in a 6 m (20-ft.). -radius spread&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Duration:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Concentration, up to 1 round/level (D)&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Saving Throw:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Will negates&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Spell Resistance:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Yes&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;p&gt;This spell calms agitated creatures. You have no control over the affected creatures, but &lt;i&gt;calm emotions&lt;/i&gt; can stop raging creatures from fighting or joyous ones from reveling. Creatures so affected cannot take violent actions (although they can defend themselves) or do anything destructive. Any aggressive action against or damage dealt to a calmed creature immediately breaks the spell on all calmed creatures.&lt;/p&gt;&lt;p&gt;This spell automatically suppresses (but does not dispel) any morale bonuses granted by spells such as &lt;i&gt;bless&lt;/i&gt;,  &lt;i&gt;good hope&lt;/i&gt;,  and &lt;i&gt;rage&lt;/i&gt;,  as well as negating a bard''s ability to inspire courage or a barbarian''s rage ability. It also suppresses any fear effects and removes the &lt;i&gt;confused&lt;/i&gt; condition from all targets. While the spell lasts, a suppressed spell or effect has no effect. When the &lt;i&gt;calm emotions&lt;/i&gt; spell ends, the original spell or effect takes hold of the creature again, provided that its duration has not expired in the meantime.&lt;/p&gt;&lt;p/&gt;
&lt;/div&gt;', 'SRD 3.5 SpellsC')
GO

INSERT INTO [spell] ([id], [name], [altname], [school], [subschool], [descriptor], [spellcraft_dc], [level], [components], [casting_time], [range], [target], [area], [effect], [duration], [saving_throw], [spell_resistance], [short_description], [to_develop], [material_components], [arcane_material_components], [focus], [description], [xp_cost], [arcane_focus], [wizard_focus], [verbal_components], [sorcerer_focus], [bard_focus], [cleric_focus], [druid_focus], [full_text], [reference])
VALUES 
  (150, 'Cat''s Grace', 'cat''s grace', 'Transmutation', NULL, NULL, NULL, 'Bard 2, Druid 2, Ranger 2, Sorcerer/Wizard 2', 'V, S, M', '1 standard action', 'Touch', 'Creature touched', NULL, NULL, '1 min./level', 'Will negates (harmless)', 'Yes', 'Subject gains +4 to Dex for 1 min./level.', NULL, 'A pinch of cat fur.', NULL, NULL, '&lt;p&gt;The transmuted creature becomes more graceful, agile, and coordinated. The spell grants a +4 enhancement bonus to Dexterity, adding the usual benefits to AC, Reflex saves, and other uses of the Dexterity modifier.&lt;/p&gt;
      &lt;p/&gt;', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '&lt;div topic=&quot;Cat''s Grace&quot; level=&quot;5&quot;&gt;&lt;p&gt;&lt;h5&gt;Cat''s Grace&lt;/h5&gt;&lt;/p&gt;&lt;table width=&quot;100%&quot; border=&quot;1&quot; cellpadding=&quot;2&quot; cellspacing=&quot;2&quot; frame=&quot;VOID&quot; rules=&quot;ROWS&quot;&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;/&gt;&lt;td&gt;Transmutation&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Level:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Bard 2, Druid 2, Ranger 2, Sorcerer/Wizard 2&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Components:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;V, S, M&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Casting Time:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;1 standard action&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Range:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Touch&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Target:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Creature touched&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Duration:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;1 min./level&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Saving Throw:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Will negates (harmless)&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Spell Resistance:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Yes&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;p&gt;The transmuted creature becomes more graceful, agile, and coordinated. The spell grants a +4 enhancement bonus to Dexterity, adding the usual benefits to AC, Reflex saves, and other uses of the Dexterity modifier.&lt;/p&gt;&lt;p&gt;&lt;i&gt;Material Component:&lt;/i&gt; A pinch of cat fur.&lt;/p&gt;&lt;p/&gt;
&lt;/div&gt;', 'SRD 3.5 SpellsC')
GO

