INSERT INTO [spell] ([id], [name], [altname], [school], [subschool], [descriptor], [spellcraft_dc], [level], [components], [casting_time], [range], [target], [area], [effect], [duration], [saving_throw], [spell_resistance], [short_description], [to_develop], [material_components], [arcane_material_components], [focus], [description], [xp_cost], [arcane_focus], [wizard_focus], [verbal_components], [sorcerer_focus], [bard_focus], [cleric_focus], [druid_focus], [full_text], [reference])
VALUES 
  (182, 'Contingency', 'contingency', 'Evocation', NULL, NULL, NULL, 'Sorcerer/Wizard 6', 'V, S, M, F', 'At least 10 minutes; See Book', 'Personal', 'You', NULL, NULL, 'One day/level (D) or until discharged', NULL, NULL, 'Sets trigger condition for another spell.', NULL, 'That of the companion spell, plus quicksilver and an eyelash of an ogre mage, rakshasa, or similar spell-using creature.', NULL, 'A statuette of you carved from elephant ivory and decorated with gems (worth at least 1,500 gp). You must carry the focus for the contingency to work.', '&lt;p&gt;You can place another spell upon your person so that it comes into effect under some condition you dictate when casting &lt;i&gt;contingency&lt;/i&gt;. The &lt;i&gt;contingency&lt;/i&gt; spell and the companion spell are cast at the same time. The 10-minute casting time is the minimum total for both castings; if the companion spell has a casting time longer than 10 minutes, use that instead.&lt;/p&gt;
      &lt;p&gt;The spell to be brought into effect by the &lt;i&gt;contingency&lt;/i&gt; must be one that affects your person and be of a spell level no higher than one-third your caster level (rounded down, maximum 6th level).&lt;/p&gt;
      &lt;p&gt;The conditions needed to bring the spell into effect must be clear, although they can be general. In all cases, the &lt;i&gt;contingency&lt;/i&gt; immediately brings into effect the companion spell, the latter being ''cast'' instantaneously when the prescribed circumstances occur. If complicated or convoluted conditions are prescribed, the whole spell combination (&lt;i&gt;contingency&lt;/i&gt; and the companion magic) may fail when called on. The companion spell occurs based solely on the stated conditions, regardless of whether you want it to.&lt;/p&gt;
      &lt;p&gt;You can use only one &lt;i&gt;contingency&lt;/i&gt; spell at a time; if a second is cast, the first one (if still active) is dispelled.&lt;/p&gt;
      &lt;p/&gt;', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '&lt;div topic=&quot;Contingency&quot; level=&quot;5&quot;&gt;&lt;p&gt;&lt;h5&gt;Contingency&lt;/h5&gt;&lt;/p&gt;&lt;table width=&quot;100%&quot; border=&quot;1&quot; cellpadding=&quot;2&quot; cellspacing=&quot;2&quot; frame=&quot;VOID&quot; rules=&quot;ROWS&quot;&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;/&gt;&lt;td&gt;Evocation&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Level:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Sorcerer/Wizard 6&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Components:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;V, S, M, F&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Casting Time:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;At least 10 minutes; See Book&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Range:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Personal&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Target:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;You&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Duration:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;One day/level (D) or until discharged&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;p&gt;You can place another spell upon your person so that it comes into effect under some condition you dictate when casting &lt;i&gt;contingency&lt;/i&gt;. The &lt;i&gt;contingency&lt;/i&gt; spell and the companion spell are cast at the same time. The 10-minute casting time is the minimum total for both castings; if the companion spell has a casting time longer than 10 minutes, use that instead.&lt;/p&gt;&lt;p&gt;The spell to be brought into effect by the &lt;i&gt;contingency&lt;/i&gt; must be one that affects your person and be of a spell level no higher than one-third your caster level (rounded down, maximum 6th level).&lt;/p&gt;&lt;p&gt;The conditions needed to bring the spell into effect must be clear, although they can be general. In all cases, the &lt;i&gt;contingency&lt;/i&gt; immediately brings into effect the companion spell, the latter being ''cast'' instantaneously when the prescribed circumstances occur. If complicated or convoluted conditions are prescribed, the whole spell combination (&lt;i&gt;contingency&lt;/i&gt; and the companion magic) may fail when called on. The companion spell occurs based solely on the stated conditions, regardless of whether you want it to.&lt;/p&gt;&lt;p&gt;You can use only one &lt;i&gt;contingency&lt;/i&gt; spell at a time; if a second is cast, the first one (if still active) is dispelled.&lt;/p&gt;&lt;p&gt;&lt;i&gt;Material Component:&lt;/i&gt; That of the companion spell, plus quicksilver and an eyelash of an ogre mage, rakshasa, or similar spell-using creature.&lt;/p&gt;&lt;p&gt;&lt;i&gt;Focus:&lt;/i&gt; A statuette of you carved from elephant ivory and decorated with gems (worth at least 1,500 gp). You must carry the focus for the &lt;i&gt;contingency&lt;/i&gt; to work.&lt;/p&gt;&lt;p/&gt;
&lt;/div&gt;', 'SRD 3.5 SpellsC')
GO

INSERT INTO [spell] ([id], [name], [altname], [school], [subschool], [descriptor], [spellcraft_dc], [level], [components], [casting_time], [range], [target], [area], [effect], [duration], [saving_throw], [spell_resistance], [short_description], [to_develop], [material_components], [arcane_material_components], [focus], [description], [xp_cost], [arcane_focus], [wizard_focus], [verbal_components], [sorcerer_focus], [bard_focus], [cleric_focus], [druid_focus], [full_text], [reference])
VALUES 
  (183, 'Continual Flame', 'continual flame', 'Evocation', NULL, 'Light', NULL, 'Cleric 3, Sorcerer/Wizard 2', 'V, S, M', '1 standard action', 'Touch', 'Object touched', NULL, 'Magical, heatless flame', 'Permanent', 'None', 'No', 'Makes a permanent, heatless torch.', NULL, 'You sprinkle ruby dust (worth 50 gp) on the item that is to carry the flame.', NULL, NULL, '&lt;p&gt;A flame, equivalent in brightness to a torch, springs forth from an object that you touch. The effect looks like a regular flame, but it creates no heat and doesn''t use oxygen. A &lt;i&gt;continual flame&lt;/i&gt; can be covered and hidden but not smothered or quenched.&lt;/p&gt;
      &lt;p&gt;Light spells counter and dispel darkness spells of an equal or lower level.&lt;/p&gt;
      &lt;p/&gt;', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '&lt;div topic=&quot;Continual Flame&quot; level=&quot;5&quot;&gt;&lt;p&gt;&lt;h5&gt;Continual Flame&lt;/h5&gt;&lt;/p&gt;&lt;table width=&quot;100%&quot; border=&quot;1&quot; cellpadding=&quot;2&quot; cellspacing=&quot;2&quot; frame=&quot;VOID&quot; rules=&quot;ROWS&quot;&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;/&gt;&lt;td&gt;Evocation [Light]&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Level:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Cleric 3, Sorcerer/Wizard 2&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Components:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;V, S, M&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Casting Time:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;1 standard action&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Range:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Touch&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Target:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Object touched&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Effect:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Magical, heatless flame&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Duration:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Permanent&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Saving Throw:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;None&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Spell Resistance:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;No&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;p&gt;A flame, equivalent in brightness to a torch, springs forth from an object that you touch. The effect looks like a regular flame, but it creates no heat and doesn''t use oxygen. A &lt;i&gt;continual flame&lt;/i&gt; can be covered and hidden but not smothered or quenched.&lt;/p&gt;&lt;p&gt;Light spells counter and dispel darkness spells of an equal or lower level.&lt;/p&gt;&lt;p&gt;&lt;i&gt;Material Component:&lt;/i&gt; You sprinkle ruby dust (worth 50 gp) on the item that is to carry the flame.&lt;/p&gt;&lt;p/&gt;
&lt;/div&gt;', 'SRD 3.5 SpellsC')
GO

INSERT INTO [spell] ([id], [name], [altname], [school], [subschool], [descriptor], [spellcraft_dc], [level], [components], [casting_time], [range], [target], [area], [effect], [duration], [saving_throw], [spell_resistance], [short_description], [to_develop], [material_components], [arcane_material_components], [focus], [description], [xp_cost], [arcane_focus], [wizard_focus], [verbal_components], [sorcerer_focus], [bard_focus], [cleric_focus], [druid_focus], [full_text], [reference])
VALUES 
  (184, 'Control Plants', 'control plants', 'Transmutation', NULL, NULL, NULL, 'Druid 8, Plant 8', 'V, S, DF', '1 standard action', 'Close (7,5 m (25 ft.) + 1,5 m (5 ft.)/2 levels)', 'Up to 2 HD/level of plant creatures, no two of which can be more than 9 m (30 ft.) apart', NULL, NULL, '1 min./level', 'Will negates', 'No', 'Control actions of one or more plant creatures.', NULL, NULL, NULL, NULL, '&lt;p&gt;This spell enables you to control the actions of one or more plant creatures for a short period of time. You command the creatures by voice and they understand you, no matter what language you speak. Even if vocal communication is impossible  the controlled plants do not attack you. At the end of the spell, the subjects revert to their normal behavior.&lt;/p&gt;
      &lt;p&gt;Suicidal or self-destructive commands are simply ignored.&lt;/p&gt;
      &lt;p/&gt;', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '&lt;div topic=&quot;Control Plants&quot; level=&quot;5&quot;&gt;&lt;p&gt;&lt;h5&gt;Control Plants&lt;/h5&gt;&lt;/p&gt;&lt;table width=&quot;100%&quot; border=&quot;1&quot; cellpadding=&quot;2&quot; cellspacing=&quot;2&quot; frame=&quot;VOID&quot; rules=&quot;ROWS&quot;&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;/&gt;&lt;td&gt;Transmutation&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Level:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Druid 8, Plant 8&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Components:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;V, S, DF&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Casting Time:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;1 standard action&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Range:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Close (7,5 m (25 ft.) + 1,5 m (5 ft.)/2 levels)&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Targets:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Up to 2 HD/level of plant creatures, no two of which can be more than 9 m (30 ft.) apart&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Duration:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;1 min./level&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Saving Throw:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Will negates&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Spell Resistance:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;No&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;p&gt;This spell enables you to control the actions of one or more plant creatures for a short period of time. You command the creatures by voice and they understand you, no matter what language you speak. Even if vocal communication is impossible  the controlled plants do not attack you. At the end of the spell, the subjects revert to their normal behavior.&lt;/p&gt;&lt;p&gt;Suicidal or self-destructive commands are simply ignored.&lt;/p&gt;&lt;p/&gt;
&lt;/div&gt;', 'SRD 3.5 SpellsC')
GO

