INSERT INTO [spell] ([id], [name], [altname], [school], [subschool], [descriptor], [spellcraft_dc], [level], [components], [casting_time], [range], [target], [area], [effect], [duration], [saving_throw], [spell_resistance], [short_description], [to_develop], [material_components], [arcane_material_components], [focus], [description], [xp_cost], [arcane_focus], [wizard_focus], [verbal_components], [sorcerer_focus], [bard_focus], [cleric_focus], [druid_focus], [full_text], [reference])
VALUES 
  (217, 'Deeper Darkness', 'deeper darkness', 'Evocation', NULL, 'Darkness', NULL, 'Cleric 3', 'V, M/DF', '1 standard action', 'Touch', 'Object touched', NULL, NULL, 'One day/level (D)', 'None', 'No', 'Object sheds supernatural shadow in 18 m (60-ft.) radius.', NULL, NULL, 'A bit of bat fur and either a drop of pitch or a piece of coal.', NULL, '&lt;p&gt;This spell functions like &lt;i&gt;darkness&lt;/i&gt;, except that the object radiates shadowy illumination in a 60-foot radius and the &lt;i&gt;darkness&lt;/i&gt; lasts longer.&lt;/p&gt;
      &lt;p&gt;&lt;i&gt;daylight&lt;/i&gt; brought into an area of &lt;i&gt;Deeper darkness&lt;/i&gt; (or vice versa) is temporarily negated, so that the otherwise prevailing light conditions exist in the overlapping areas of effect.&lt;/p&gt;
      &lt;p&gt;&lt;i&gt;Deeper darkness&lt;/i&gt; counters and dispels any light spell of equal or lower level, including &lt;i&gt;daylight&lt;/i&gt; and &lt;i&gt;light&lt;/i&gt;.&lt;/p&gt;
      &lt;p/&gt;', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '&lt;div topic=&quot;Deeper Darkness&quot; level=&quot;5&quot;&gt;&lt;p&gt;&lt;h5&gt;Deeper Darkness&lt;/h5&gt;&lt;/p&gt;&lt;table width=&quot;100%&quot; border=&quot;1&quot; cellpadding=&quot;2&quot; cellspacing=&quot;2&quot; frame=&quot;VOID&quot; rules=&quot;ROWS&quot;&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;/&gt;&lt;td&gt;Evocation [Darkness]&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Level:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Cleric 3&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Duration:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;One day/level (D)&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;p&gt;This spell functions like &lt;i&gt;darkness&lt;/i&gt;, except that the object radiates shadowy illumination in a 60-foot radius and the &lt;i&gt;darkness&lt;/i&gt; lasts longer.&lt;/p&gt;&lt;p&gt;&lt;i&gt;daylight&lt;/i&gt; brought into an area of &lt;i&gt;Deeper darkness&lt;/i&gt; (or vice versa) is temporarily negated, so that the otherwise prevailing light conditions exist in the overlapping areas of effect.&lt;/p&gt;&lt;p&gt;&lt;i&gt;Deeper darkness&lt;/i&gt; counters and dispels any light spell of equal or lower level, including &lt;i&gt;daylight&lt;/i&gt; and &lt;i&gt;light&lt;/i&gt;.&lt;/p&gt;&lt;p/&gt;
&lt;/div&gt;', 'SRD 3.5 SpellsD-E')
GO

INSERT INTO [spell] ([id], [name], [altname], [school], [subschool], [descriptor], [spellcraft_dc], [level], [components], [casting_time], [range], [target], [area], [effect], [duration], [saving_throw], [spell_resistance], [short_description], [to_develop], [material_components], [arcane_material_components], [focus], [description], [xp_cost], [arcane_focus], [wizard_focus], [verbal_components], [sorcerer_focus], [bard_focus], [cleric_focus], [druid_focus], [full_text], [reference])
VALUES 
  (218, 'Delay Poison', 'delay poison', 'Conjuration', 'Healing', NULL, NULL, 'Bard 2, Cleric 2, Druid 2, Paladin 2, Ranger 1', 'V, S, DF', '1 standard action', 'Touch', 'Creature touched', NULL, NULL, '1 hour/level', 'Fortitude negates (harmless)', 'Yes (harmless)', 'Stops poison from harming subject for 1 hour/level.', NULL, NULL, NULL, NULL, '&lt;p&gt;The subject becomes temporarily immune to poison. Any poison in its system or any poison to which it is exposed during the spell''s duration does not affect the subject until the spell''s duration has expired. &lt;i&gt;Delay poison&lt;/i&gt; does not cure any damage that poison may have already done.&lt;/p&gt;
      &lt;p/&gt;', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '&lt;div topic=&quot;Delay Poison&quot; level=&quot;5&quot;&gt;&lt;p&gt;&lt;h5&gt;Delay Poison&lt;/h5&gt;&lt;/p&gt;&lt;table width=&quot;100%&quot; border=&quot;1&quot; cellpadding=&quot;2&quot; cellspacing=&quot;2&quot; frame=&quot;VOID&quot; rules=&quot;ROWS&quot;&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;/&gt;&lt;td&gt;Conjuration (Healing)&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Level:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Bard 2, Cleric 2, Druid 2, Paladin 2, Ranger 1&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Components:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;V, S, DF&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Casting Time:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;1 standard action&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Range:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Touch&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Target:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Creature touched&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Duration:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;1 hour/level&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Saving Throw:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Fortitude negates (harmless)&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Spell Resistance:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Yes (harmless)&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;p&gt;The subject becomes temporarily immune to poison. Any poison in its system or any poison to which it is exposed during the spell''s duration does not affect the subject until the spell''s duration has expired. &lt;i&gt;Delay poison&lt;/i&gt; does not cure any damage that poison may have already done.&lt;/p&gt;&lt;p/&gt;
&lt;/div&gt;', 'SRD 3.5 SpellsD-E')
GO

INSERT INTO [spell] ([id], [name], [altname], [school], [subschool], [descriptor], [spellcraft_dc], [level], [components], [casting_time], [range], [target], [area], [effect], [duration], [saving_throw], [spell_resistance], [short_description], [to_develop], [material_components], [arcane_material_components], [focus], [description], [xp_cost], [arcane_focus], [wizard_focus], [verbal_components], [sorcerer_focus], [bard_focus], [cleric_focus], [druid_focus], [full_text], [reference])
VALUES 
  (219, 'Delayed Blast Fireball', 'delayed blast fireball', 'Evocation', NULL, 'Fire', NULL, 'Sorcerer/Wizard 7', 'V, S, M', '1 standard action', 'Long (400 ft. + 12 m (40 ft.)/level)', NULL, '6 m (20-ft.). -radius spread', NULL, '5 rounds or less; See Book', 'Reflex half', 'Yes', '1d6/level fire damage; you can postpone blast for 5 rounds.', NULL, 'A tiny ball of bat guano and sulfur.', NULL, NULL, '&lt;p&gt;This spell functions like &lt;i&gt;fireball&lt;/i&gt;,  except that it is more powerful and can detonate up to 5 rounds after the spell is cast. The burst of flame deals 1d6 points of fire damage per caster level (maximum 20d6).&lt;/p&gt;
      &lt;p&gt;The glowing bead created by &lt;i&gt;delayed blast fireball&lt;/i&gt; can detonate immediately if you desire, or you can choose to delay the burst for as many as 5 rounds. You select the amount of delay upon completing the spell, and that time cannot change once it has been set unless someone touches the bead (see below). If you choose a delay, the glowing bead sits at its destination until it detonates. A creature can pick up and hurl the bead as a thrown weapon (range increment 10 feet). If a creature handles and moves the bead within 1 round of its detonation, there is a 25% chance that the bead detonates while being handled.&lt;/p&gt;
      &lt;p/&gt;', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '&lt;div topic=&quot;Delayed Blast Fireball&quot; level=&quot;5&quot;&gt;&lt;p&gt;&lt;h5&gt;Delayed Blast Fireball&lt;/h5&gt;&lt;/p&gt;&lt;table width=&quot;100%&quot; border=&quot;1&quot; cellpadding=&quot;2&quot; cellspacing=&quot;2&quot; frame=&quot;VOID&quot; rules=&quot;ROWS&quot;&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;/&gt;&lt;td&gt;Evocation [Fire]&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Level:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Sorcerer/Wizard 7&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Duration:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;5 rounds or less; See Book&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;p&gt;This spell functions like &lt;i&gt;fireball&lt;/i&gt;,  except that it is more powerful and can detonate up to 5 rounds after the spell is cast. The burst of flame deals 1d6 points of fire damage per caster level (maximum 20d6).&lt;/p&gt;&lt;p&gt;The glowing bead created by &lt;i&gt;delayed blast fireball&lt;/i&gt; can detonate immediately if you desire, or you can choose to delay the burst for as many as 5 rounds. You select the amount of delay upon completing the spell, and that time cannot change once it has been set unless someone touches the bead (see below). If you choose a delay, the glowing bead sits at its destination until it detonates. A creature can pick up and hurl the bead as a thrown weapon (range increment 10 feet). If a creature handles and moves the bead within 1 round of its detonation, there is a 25% chance that the bead detonates while being handled.&lt;/p&gt;&lt;p/&gt;
&lt;/div&gt;', 'SRD 3.5 SpellsD-E')
GO

INSERT INTO [spell] ([id], [name], [altname], [school], [subschool], [descriptor], [spellcraft_dc], [level], [components], [casting_time], [range], [target], [area], [effect], [duration], [saving_throw], [spell_resistance], [short_description], [to_develop], [material_components], [arcane_material_components], [focus], [description], [xp_cost], [arcane_focus], [wizard_focus], [verbal_components], [sorcerer_focus], [bard_focus], [cleric_focus], [druid_focus], [full_text], [reference])
VALUES 
  (220, 'Demand', 'demand', 'Enchantment', 'Compulsion', 'Mind-Affecting', NULL, 'Sorcerer/Wizard 8', 'V, S, M/DF', '10 minutes', 'See Book', 'One creature', NULL, NULL, '1 round; See Book', 'Will partial', 'Yes', 'As sending, plus you can send suggestion.', NULL, 'A short piece of copper wire and some small part of the subject-a hair, a bit of nail, or the like.', 'A short piece of fine copper wire.', NULL, '&lt;p&gt;This spell functions like &lt;i&gt;sending&lt;/i&gt;,  but the message can also contain a &lt;i&gt;suggestion&lt;/i&gt; (see the &lt;i&gt;suggestion&lt;/i&gt; spell), which the subject does its best to carry out. A successful Will save negates the &lt;i&gt;suggestion&lt;/i&gt; effect but not the contact itself. The &lt;i&gt;demand&lt;/i&gt;,  if received, is understood even if the subject''s Intelligence score is as low as 1. If the message is impossible or meaningless according to the circumstances that exist for the subject at the time the &lt;i&gt;demand&lt;/i&gt; is issued, the message is understood but the &lt;i&gt;suggestion&lt;/i&gt; is ineffective.&lt;/p&gt;
      &lt;p&gt;The &lt;i&gt;demand&lt;/i&gt;''s message to the creature must be twenty-five words or less, including the &lt;i&gt;suggestion&lt;/i&gt;. The creature can also give a short reply immediately.&lt;/p&gt;
      &lt;p/&gt;', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '&lt;div topic=&quot;Demand&quot; level=&quot;5&quot;&gt;&lt;p&gt;&lt;h5&gt;Demand&lt;/h5&gt;&lt;/p&gt;&lt;table width=&quot;100%&quot; border=&quot;1&quot; cellpadding=&quot;2&quot; cellspacing=&quot;2&quot; frame=&quot;VOID&quot; rules=&quot;ROWS&quot;&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;/&gt;&lt;td&gt;Enchantment (Compulsion) [Mind-Affecting]&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Level:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Sorcerer/Wizard 8&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Saving Throw:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Will partial&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Spell Resistance:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Yes&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;p&gt;This spell functions like &lt;i&gt;sending&lt;/i&gt;,  but the message can also contain a &lt;i&gt;suggestion&lt;/i&gt; (see the &lt;i&gt;suggestion&lt;/i&gt; spell), which the subject does its best to carry out. A successful Will save negates the &lt;i&gt;suggestion&lt;/i&gt; effect but not the contact itself. The &lt;i&gt;demand&lt;/i&gt;,  if received, is understood even if the subject''s Intelligence score is as low as 1. If the message is impossible or meaningless according to the circumstances that exist for the subject at the time the &lt;i&gt;demand&lt;/i&gt; is issued, the message is understood but the &lt;i&gt;suggestion&lt;/i&gt; is ineffective.&lt;/p&gt;&lt;p&gt;The &lt;i&gt;demand&lt;/i&gt;''s message to the creature must be twenty-five words or less, including the &lt;i&gt;suggestion&lt;/i&gt;. The creature can also give a short reply immediately.&lt;/p&gt;&lt;p&gt;&lt;i&gt;Material Component:&lt;/i&gt; A short piece of copper wire and some small part of the subject-a hair, a bit of nail, or the like.&lt;/p&gt;&lt;p/&gt;
&lt;/div&gt;', 'SRD 3.5 SpellsD-E')
GO

INSERT INTO [spell] ([id], [name], [altname], [school], [subschool], [descriptor], [spellcraft_dc], [level], [components], [casting_time], [range], [target], [area], [effect], [duration], [saving_throw], [spell_resistance], [short_description], [to_develop], [material_components], [arcane_material_components], [focus], [description], [xp_cost], [arcane_focus], [wizard_focus], [verbal_components], [sorcerer_focus], [bard_focus], [cleric_focus], [druid_focus], [full_text], [reference])
VALUES 
  (221, 'Desecrate', 'desecrate', 'Evocation', NULL, 'Evil', NULL, 'Cleric 2, Evil 2', 'V, S, M, DF', '1 standard action', 'Close (7,5 m (25 ft.) + 1,5 m (5 ft.)/2 levels)', NULL, '6 m (20-ft.). -radius emanation', NULL, '2 hours/level', 'None', 'Yes', 'Fills area with negative energy, making undead stronger.', NULL, 'A vial of unholy water and 25 gp worth (5 pounds) of silver dust, all of which must be sprinkled around the area.', NULL, NULL, '&lt;p&gt;This spell imbues an area with negative energy. Each Charisma check made to turn undead within this area takes a -3 profane penalty, and every undead creature entering a &lt;i&gt;desecrated&lt;/i&gt; area gains a +1 profane bonus on attack rolls, damage rolls, and saving throws. An undead creature created within or summoned into such an area gains +1 hit points per HD.&lt;/p&gt;
      &lt;p&gt;If the &lt;i&gt;desecrated&lt;/i&gt; area contains an altar, shrine, or other permanent fixture dedicated to your deity or aligned higher power, the modifiers given above are doubled (-6 profane penalty on turning checks, +2 profane bonus and +2 hit points per HD for undead in the area).&lt;/p&gt;
      &lt;p&gt;Furthermore, anyone who casts &lt;i&gt;animate dead&lt;/i&gt; within this area may create as many as double the normal amount of undead (that is, 4 HD per caster level rather than 2 HD per caster level).&lt;/p&gt;
      &lt;p&gt;If the area contains an altar, shrine, or other permanent fixture of a deity, pantheon, or higher power other than your patron, the &lt;i&gt;Desecrate&lt;/i&gt; spell instead curses the area, cutting off its connection with the associated deity or power. This secondary function, if used, does not also grant the bonuses and penalties relating to undead, as given above.&lt;/p&gt;
      &lt;p&gt;&lt;i&gt;Desecrate&lt;/i&gt; counters and dispels &lt;i&gt;consecrate&lt;/i&gt;.&lt;/p&gt;
      &lt;p/&gt;', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '&lt;div topic=&quot;Desecrate&quot; level=&quot;5&quot;&gt;&lt;p&gt;&lt;h5&gt;Desecrate&lt;/h5&gt;&lt;/p&gt;&lt;table width=&quot;100%&quot; border=&quot;1&quot; cellpadding=&quot;2&quot; cellspacing=&quot;2&quot; frame=&quot;VOID&quot; rules=&quot;ROWS&quot;&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;/&gt;&lt;td&gt;Evocation [Evil]&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Level:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Cleric 2, Evil 2&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Components:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;V, S, M, DF&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Casting Time:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;1 standard action&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Range:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Close (7,5 m (25 ft.) + 1,5 m (5 ft.)/2 levels)&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Area:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;6 m (20-ft.). -radius emanation&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Duration:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;2 hours/level&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Saving Throw:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;None&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Spell Resistance:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Yes&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;p&gt;This spell imbues an area with negative energy. Each Charisma check made to turn undead within this area takes a -3 profane penalty, and every undead creature entering a &lt;i&gt;desecrated&lt;/i&gt; area gains a +1 profane bonus on attack rolls, damage rolls, and saving throws. An undead creature created within or summoned into such an area gains +1 hit points per HD.&lt;/p&gt;&lt;p&gt;If the &lt;i&gt;desecrated&lt;/i&gt; area contains an altar, shrine, or other permanent fixture dedicated to your deity or aligned higher power, the modifiers given above are doubled (-6 profane penalty on turning checks, +2 profane bonus and +2 hit points per HD for undead in the area).&lt;/p&gt;&lt;p&gt;Furthermore, anyone who casts &lt;i&gt;animate dead&lt;/i&gt; within this area may create as many as double the normal amount of undead (that is, 4 HD per caster level rather than 2 HD per caster level).&lt;/p&gt;&lt;p&gt;If the area contains an altar, shrine, or other permanent fixture of a deity, pantheon, or higher power other than your patron, the &lt;i&gt;Desecrate&lt;/i&gt; spell instead curses the area, cutting off its connection with the associated deity or power. This secondary function, if used, does not also grant the bonuses and penalties relating to undead, as given above.&lt;/p&gt;&lt;p&gt;&lt;i&gt;Desecrate&lt;/i&gt; counters and dispels &lt;i&gt;consecrate&lt;/i&gt;.&lt;/p&gt;&lt;p&gt;&lt;i&gt;Material Component:&lt;/i&gt; A vial of unholy water and 25 gp worth (5 pounds) of silver dust, all of which must be sprinkled around the area.&lt;/p&gt;&lt;p/&gt;
&lt;/div&gt;', 'SRD 3.5 SpellsD-E')
GO

INSERT INTO [spell] ([id], [name], [altname], [school], [subschool], [descriptor], [spellcraft_dc], [level], [components], [casting_time], [range], [target], [area], [effect], [duration], [saving_throw], [spell_resistance], [short_description], [to_develop], [material_components], [arcane_material_components], [focus], [description], [xp_cost], [arcane_focus], [wizard_focus], [verbal_components], [sorcerer_focus], [bard_focus], [cleric_focus], [druid_focus], [full_text], [reference])
VALUES 
  (222, 'Destruction', 'destruction', 'Necromancy', NULL, 'Death', NULL, 'Cleric 7, Death 7', 'V, S, F', '1 standard action', 'Close (7,5 m (25 ft.) + 1,5 m (5 ft.)/2 levels)', 'One creature', NULL, NULL, 'Instantaneous', 'Fortitude partial', 'Yes', 'Kills subject and destroys remains.', NULL, NULL, NULL, 'A special holy (or unholy) symbol of silver marked with verses of anathema (cost 500 gp).', '&lt;p&gt;This spell instantly slays the subject and consumes its remains (but not its equipment and possessions) utterly. If the target''s Fortitude saving throw succeeds, it instead takes 10d6 points of damage. The only way to restore life to a character who has failed to save against this spell is to use &lt;i&gt;true resurrection&lt;/i&gt;, a carefully worded &lt;i&gt;wish&lt;/i&gt; spell followed by &lt;i&gt;resurrection&lt;/i&gt;, or &lt;i&gt;miracle&lt;/i&gt;.&lt;/p&gt;
      &lt;p/&gt;', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '&lt;div topic=&quot;Destruction&quot; level=&quot;5&quot;&gt;&lt;p&gt;&lt;h5&gt;Destruction&lt;/h5&gt;&lt;/p&gt;&lt;table width=&quot;100%&quot; border=&quot;1&quot; cellpadding=&quot;2&quot; cellspacing=&quot;2&quot; frame=&quot;VOID&quot; rules=&quot;ROWS&quot;&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;/&gt;&lt;td&gt;Necromancy [Death]&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Level:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Cleric 7, Death 7&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Components:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;V, S, F&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Casting Time:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;1 standard action&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Range:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Close (7,5 m (25 ft.) + 1,5 m (5 ft.)/2 levels)&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Target:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;One creature&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Duration:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Instantaneous&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Saving Throw:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Fortitude partial&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Spell Resistance:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Yes&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;p&gt;This spell instantly slays the subject and consumes its remains (but not its equipment and possessions) utterly. If the target''s Fortitude saving throw succeeds, it instead takes 10d6 points of damage. The only way to restore life to a character who has failed to save against this spell is to use &lt;i&gt;true resurrection&lt;/i&gt;, a carefully worded &lt;i&gt;wish&lt;/i&gt; spell followed by &lt;i&gt;resurrection&lt;/i&gt;, or &lt;i&gt;miracle&lt;/i&gt;.&lt;/p&gt;&lt;p&gt;&lt;i&gt;Focus:&lt;/i&gt; A special holy (or unholy) symbol of silver marked with verses of anathema (cost 500 gp).&lt;/p&gt;&lt;p/&gt;
&lt;/div&gt;', 'SRD 3.5 SpellsD-E')
GO

INSERT INTO [spell] ([id], [name], [altname], [school], [subschool], [descriptor], [spellcraft_dc], [level], [components], [casting_time], [range], [target], [area], [effect], [duration], [saving_throw], [spell_resistance], [short_description], [to_develop], [material_components], [arcane_material_components], [focus], [description], [xp_cost], [arcane_focus], [wizard_focus], [verbal_components], [sorcerer_focus], [bard_focus], [cleric_focus], [druid_focus], [full_text], [reference])
VALUES 
  (223, 'Detect Animals or Plants', 'detect animals or plants', 'Divination', NULL, NULL, NULL, 'Druid 1, Ranger 1', 'V, S', '1 standard action', 'Long (400 ft. + 12 m (40 ft.)/level)', NULL, 'Cone-shaped emanation', NULL, 'Concentration, up to 10 min./level (D)', 'None', 'No', 'Detects kinds of animals or plants.', NULL, NULL, NULL, NULL, '&lt;p&gt;You can detect a particular kind of animal or plant in a cone emanating out from you in whatever direction you face. You must think of a kind of animal or plant when using the spell, but you can change the animal or plant kind each round. The amount of information revealed depends on how long you search a particular area or focus on a specific kind of animal or plant.&lt;/p&gt;
      &lt;p&gt;&lt;i&gt;1st Round:&lt;/i&gt; Presence or absence of that kind of animal or plant in the area.&lt;/p&gt;
      &lt;p&gt;&lt;i&gt;2nd Round:&lt;/i&gt; Number of individuals of the specified kind in the area, and the condition of the healthiest specimen.&lt;/p&gt;
      &lt;p&gt;&lt;i&gt;3rd Round:&lt;/i&gt; The condition (see below) and location of each individual present. If an animal or plant is outside your line of sight, then you discern its direction but not its exact location.&lt;/p&gt;
      &lt;p&gt;&lt;i&gt;Conditions:&lt;/i&gt; For purposes of this spell, the categories of condition are as follows:&lt;/p&gt;
      &lt;p&gt;Normal: Has at least 90% of full normal hit points, free of disease.&lt;/p&gt;
      &lt;p&gt;Fair: 30% to 90% of full normal hit points remaining.&lt;/p&gt;
      &lt;p&gt;Poor: Less than 30% of full normal hit points remaining, afflicted with a disease, or suffering from a debilitating injury.&lt;/p&gt;
      &lt;p&gt;Weak: 0 or fewer hit points remaining, afflicted with a disease in the terminal stage, or crippled.&lt;/p&gt;
      &lt;p&gt;If a creature falls into more than one category, the spell indicates the weaker of the two.&lt;/p&gt;
      &lt;p&gt;Each round you can turn to detect a kind of animal or plant in a new area. The spell can penetrate barriers, but 1 foot of stone, 1 inch of common metal, a thin sheet of lead, or 3 feet of wood or dirt blocks it.&lt;/p&gt;
      &lt;p/&gt;', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '&lt;div topic=&quot;Detect Animals or Plants&quot; level=&quot;5&quot;&gt;&lt;p&gt;&lt;h5&gt;Detect Animals or Plants&lt;/h5&gt;&lt;/p&gt;&lt;table width=&quot;100%&quot; border=&quot;1&quot; cellpadding=&quot;2&quot; cellspacing=&quot;2&quot; frame=&quot;VOID&quot; rules=&quot;ROWS&quot;&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;/&gt;&lt;td&gt;Divination&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Level:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Druid 1, Ranger 1&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Components:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;V, S&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Casting Time:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;1 standard action&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Range:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Long (400 ft. + 12 m (40 ft.)/level)&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Area:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Cone-shaped emanation&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Duration:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Concentration, up to 10 min./level (D)&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Saving Throw:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;None&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Spell Resistance:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;No&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;p&gt;You can detect a particular kind of animal or plant in a cone emanating out from you in whatever direction you face. You must think of a kind of animal or plant when using the spell, but you can change the animal or plant kind each round. The amount of information revealed depends on how long you search a particular area or focus on a specific kind of animal or plant.&lt;/p&gt;&lt;p&gt;&lt;i&gt;1st Round:&lt;/i&gt; Presence or absence of that kind of animal or plant in the area.&lt;/p&gt;&lt;p&gt;&lt;i&gt;2nd Round:&lt;/i&gt; Number of individuals of the specified kind in the area, and the condition of the healthiest specimen.&lt;/p&gt;&lt;p&gt;&lt;i&gt;3rd Round:&lt;/i&gt; The condition (see below) and location of each individual present. If an animal or plant is outside your line of sight, then you discern its direction but not its exact location.&lt;/p&gt;&lt;p&gt;&lt;i&gt;Conditions:&lt;/i&gt; For purposes of this spell, the categories of condition are as follows:&lt;/p&gt;&lt;p&gt;Normal: Has at least 90% of full normal hit points, free of disease.&lt;/p&gt;&lt;p&gt;Fair: 30% to 90% of full normal hit points remaining.&lt;/p&gt;&lt;p&gt;Poor: Less than 30% of full normal hit points remaining, afflicted with a disease, or suffering from a debilitating injury.&lt;/p&gt;&lt;p&gt;Weak: 0 or fewer hit points remaining, afflicted with a disease in the terminal stage, or crippled.&lt;/p&gt;&lt;p&gt;If a creature falls into more than one category, the spell indicates the weaker of the two.&lt;/p&gt;&lt;p&gt;Each round you can turn to detect a kind of animal or plant in a new area. The spell can penetrate barriers, but 1 foot of stone, 1 inch of common metal, a thin sheet of lead, or 3 feet of wood or dirt blocks it.&lt;/p&gt;&lt;p/&gt;
&lt;/div&gt;', 'SRD 3.5 SpellsD-E')
GO

INSERT INTO [spell] ([id], [name], [altname], [school], [subschool], [descriptor], [spellcraft_dc], [level], [components], [casting_time], [range], [target], [area], [effect], [duration], [saving_throw], [spell_resistance], [short_description], [to_develop], [material_components], [arcane_material_components], [focus], [description], [xp_cost], [arcane_focus], [wizard_focus], [verbal_components], [sorcerer_focus], [bard_focus], [cleric_focus], [druid_focus], [full_text], [reference])
VALUES 
  (224, 'Detect Chaos', 'detect chaos', 'Divination', NULL, NULL, NULL, 'Cleric 1', 'V, S, DF', '1 standard action', '60 ft.', NULL, 'Cone-shaped emanation', NULL, 'Concentration, up to 10 min./level (D)', 'None', 'No', 'Reveals creatures, spells, or objects of selected alignment.', NULL, NULL, NULL, NULL, '&lt;p&gt;This spell functions like &lt;i&gt;detect evil&lt;/i&gt;, except that it detects the auras of chaotic creatures, clerics of chaotic deities, chaotic spells, and chaotic magic items, and you are vulnerable to an overwhelming chaotic aura if you are lawful.&lt;/p&gt;
      &lt;p/&gt;', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '&lt;div topic=&quot;Detect Chaos&quot; level=&quot;5&quot;&gt;&lt;p&gt;&lt;h5&gt;Detect Chaos&lt;/h5&gt;&lt;/p&gt;&lt;table width=&quot;100%&quot; border=&quot;1&quot; cellpadding=&quot;2&quot; cellspacing=&quot;2&quot; frame=&quot;VOID&quot; rules=&quot;ROWS&quot;&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;/&gt;&lt;td&gt;Divination&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Level:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Cleric 1&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;p&gt;This spell functions like &lt;i&gt;detect evil&lt;/i&gt;, except that it detects the auras of chaotic creatures, clerics of chaotic deities, chaotic spells, and chaotic magic items, and you are vulnerable to an overwhelming chaotic aura if you are lawful.&lt;/p&gt;&lt;p/&gt;
&lt;/div&gt;', 'SRD 3.5 SpellsD-E')
GO

INSERT INTO [spell] ([id], [name], [altname], [school], [subschool], [descriptor], [spellcraft_dc], [level], [components], [casting_time], [range], [target], [area], [effect], [duration], [saving_throw], [spell_resistance], [short_description], [to_develop], [material_components], [arcane_material_components], [focus], [description], [xp_cost], [arcane_focus], [wizard_focus], [verbal_components], [sorcerer_focus], [bard_focus], [cleric_focus], [druid_focus], [full_text], [reference])
VALUES 
  (225, 'Detect Evil', 'detect evil', 'Divination', NULL, NULL, NULL, 'Cleric 1', 'V, S, DF', '1 standard action', '60 ft.', NULL, 'Cone-shaped emanation', NULL, 'Concentration, up to 10 min./level (D)', 'None', 'No', 'Reveals creatures, spells, or objects of selected alignment.', NULL, NULL, NULL, NULL, '&lt;p&gt;You can sense the presence of evil. The amount of information revealed depends on how long you study a particular area or subject.&lt;/p&gt;
      &lt;p&gt;&lt;i&gt;1st Round:&lt;/i&gt; Presence or absence of evil.&lt;/p&gt;
      &lt;p&gt;&lt;i&gt;2nd Round:&lt;/i&gt; Number of evil auras (creatures, objects, or spells) in the area and the power of the most potent evil aura present.&lt;/p&gt;
      &lt;p&gt;If you are of good alignment, and the strongest evil aura''s power is overwhelming (see below), and the HD or level of the aura''s source is at least twice your character level, you are stunned for 1 round and the spell ends.&lt;/p&gt;
      &lt;p&gt;&lt;i&gt;3rd Round:&lt;/i&gt; The power and location of each aura. If an aura is outside your line of sight, then you discern its direction but not its exact location.&lt;/p&gt;
      &lt;p&gt;&lt;i&gt;Aura Power:&lt;/i&gt; An evil aura''s power depends on the type of evil creature or object that you''re detecting and its HD, caster level, or (in the case of a cleric) class level; see the accompanying table. If an aura falls into more than one strength category, the spell indicates the stronger of the two.&lt;/p&gt;
      &lt;table width=&quot;100%&quot; border=&quot;1&quot; cellpadding=&quot;2&quot; cellspacing=&quot;2&quot; frame=&quot;VOID&quot; rules=&quot;ROWS&quot;&gt;

&lt;tr maxcol=&quot;6&quot; curcol=&quot;3&quot;&gt;
&lt;td/&gt;
&lt;td colspan=&quot;4&quot; align=&quot;center&quot;&gt;&lt;b&gt;-------- Aura Power --------&lt;/b&gt;&lt;br/&gt;&lt;/td&gt; 
&lt;/tr&gt;

&lt;tr maxcol=&quot;6&quot; curcol=&quot;6&quot;&gt;
&lt;td&gt;&lt;b&gt;Creature/Object&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;
&lt;td&gt;&lt;b&gt;Faint&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;
&lt;td&gt;&lt;b&gt;Moderate&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;
&lt;td&gt;&lt;b&gt;Strong&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;
&lt;td&gt;&lt;b&gt;Overwhelming&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;

&lt;/tr&gt;
&lt;tr maxcol=&quot;6&quot; curcol=&quot;6&quot;&gt;
&lt;td&gt;Evil creature&lt;b&gt;1&lt;/b&gt; (HD)&lt;br/&gt;&lt;/td&gt;
&lt;td&gt;10 or lower&lt;br/&gt;&lt;/td&gt;
&lt;td&gt;11-25&lt;br/&gt;&lt;/td&gt;
&lt;td&gt;26-50&lt;br/&gt;&lt;/td&gt;
&lt;td&gt;51 or higher&lt;br/&gt;&lt;/td&gt;

&lt;/tr&gt;
&lt;tr maxcol=&quot;6&quot; curcol=&quot;6&quot;&gt;
&lt;td&gt;Undead (HD)&lt;br/&gt;&lt;/td&gt;
&lt;td&gt;2 or lower&lt;br/&gt;&lt;/td&gt;
&lt;td&gt;3-8&lt;br/&gt;&lt;/td&gt;
&lt;td&gt;9-20&lt;br/&gt;&lt;/td&gt;
&lt;td&gt;21 or higher&lt;br/&gt;&lt;/td&gt;

&lt;/tr&gt;
&lt;tr maxcol=&quot;6&quot; curcol=&quot;6&quot;&gt;
&lt;td&gt;Evil outsider (HD)&lt;br/&gt;&lt;/td&gt;
&lt;td&gt;1 or lower&lt;br/&gt;&lt;/td&gt;
&lt;td&gt;2-4&lt;br/&gt;&lt;/td&gt;
&lt;td&gt;5-10&lt;br/&gt;&lt;/td&gt;
&lt;td&gt;11 or higher&lt;br/&gt;&lt;/td&gt;

&lt;/tr&gt;
&lt;tr maxcol=&quot;6&quot; curcol=&quot;6&quot;&gt;
&lt;td&gt;Cleric of an evil deity &lt;b&gt;2&lt;/b&gt; (class levels)&lt;br/&gt;&lt;/td&gt;
&lt;td&gt;1&lt;br/&gt;&lt;/td&gt;
&lt;td&gt;2-4&lt;br/&gt;&lt;/td&gt;
&lt;td&gt;5-10&lt;br/&gt;&lt;/td&gt;
&lt;td&gt;11 or higher&lt;br/&gt;&lt;/td&gt;

&lt;/tr&gt;
&lt;tr maxcol=&quot;6&quot; curcol=&quot;6&quot;&gt;
&lt;td&gt;Evil magic item or spell (caster level)&lt;br/&gt;&lt;/td&gt;
&lt;td&gt;2nd or lower&lt;br/&gt;&lt;/td&gt;
&lt;td&gt;3rd-8th&lt;br/&gt;&lt;/td&gt;
&lt;td&gt;9th-20th&lt;br/&gt;&lt;/td&gt;
&lt;td&gt;21st or higher&lt;br/&gt;&lt;/td&gt;

&lt;/tr&gt;

&lt;tr maxcol=&quot;6&quot; curcol=&quot;2&quot;&gt;
&lt;td colspan=&quot;5&quot;&gt;1 Except for undead and outsiders, which have their own entries on the table.&lt;br/&gt;&lt;/td&gt;

&lt;/tr&gt;

&lt;tr maxcol=&quot;6&quot; curcol=&quot;2&quot;&gt;
&lt;td colspan=&quot;5&quot;&gt;2 Some characters who are not clerics may radiate an aura of equivalent power. The class description will indicate whether this applies.&lt;br/&gt;&lt;/td&gt;

&lt;/tr&gt;
&lt;/table&gt;
      &lt;p&gt;&lt;i&gt;Lingering Aura:&lt;/i&gt; An evil aura lingers after its original source dissipates (in the case of a spell) or is destroyed (in the case of a creature or magic item). If &lt;i&gt;detect evil&lt;/i&gt; is cast and directed at such a location, the spell indicates an aura strength of dim (even weaker than a faint aura). How long the aura lingers at this dim level depends on its original power:&lt;/p&gt;
      &lt;table width=&quot;100%&quot; border=&quot;1&quot; cellpadding=&quot;2&quot; cellspacing=&quot;2&quot; frame=&quot;VOID&quot; rules=&quot;ROWS&quot;&gt;

&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;
&lt;td&gt;&lt;b&gt;Original Strength&lt;/b&gt;&lt;br/&gt;&lt;/td&gt; &lt;td&gt;&lt;b&gt;Duration of&lt;/b&gt;
 &lt;b&gt;Lingering Aura&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;

&lt;/tr&gt;

&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;
&lt;td&gt;Faint&lt;br/&gt;&lt;/td&gt;
&lt;td&gt;1d6 rounds&lt;br/&gt;&lt;/td&gt;

&lt;/tr&gt;
&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;
&lt;td&gt;Moderate&lt;br/&gt;&lt;/td&gt;
&lt;td&gt;1d6 minutes&lt;br/&gt;&lt;/td&gt;

&lt;/tr&gt;
&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;
&lt;td&gt;Strong&lt;br/&gt;&lt;/td&gt;
&lt;td&gt;1d6x10 minutes&lt;br/&gt;&lt;/td&gt;

&lt;/tr&gt;

&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;
&lt;td&gt;Overwhelming&lt;br/&gt;&lt;/td&gt;
&lt;td&gt;1d6 days&lt;br/&gt;&lt;/td&gt;

&lt;/tr&gt;
&lt;/table&gt;
      &lt;p&gt;Animals, traps, poisons, and other potential perils are not evil, and as such this spell does not detect them.&lt;/p&gt;
      &lt;p&gt;Each round, you can turn to detect evil in a new area. The spell can penetrate barriers, but 1 foot of stone, 1 inch of common metal, a thin sheet of lead, or 3 feet of wood or dirt blocks it.&lt;/p&gt;
      &lt;p/&gt;', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '&lt;div topic=&quot;Detect Evil&quot; level=&quot;5&quot;&gt;&lt;p&gt;&lt;h5&gt;Detect Evil&lt;/h5&gt;&lt;/p&gt;&lt;table width=&quot;100%&quot; border=&quot;1&quot; cellpadding=&quot;2&quot; cellspacing=&quot;2&quot; frame=&quot;VOID&quot; rules=&quot;ROWS&quot;&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;/&gt;&lt;td&gt;Divination&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Level:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Cleric 1&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Components:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;V, S, DF&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Casting Time:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;1 standard action&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Range:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;60 ft.&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Area:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Cone-shaped emanation&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Duration:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Concentration, up to 10 min./level (D)&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Saving Throw:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;None&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Spell Resistance:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;No&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;p&gt;You can sense the presence of evil. The amount of information revealed depends on how long you study a particular area or subject.&lt;/p&gt;&lt;p&gt;&lt;i&gt;1st Round:&lt;/i&gt; Presence or absence of evil.&lt;/p&gt;&lt;p&gt;&lt;i&gt;2nd Round:&lt;/i&gt; Number of evil auras (creatures, objects, or spells) in the area and the power of the most potent evil aura present.&lt;/p&gt;&lt;p&gt;If you are of good alignment, and the strongest evil aura''s power is overwhelming (see below), and the HD or level of the aura''s source is at least twice your character level, you are stunned for 1 round and the spell ends.&lt;/p&gt;&lt;p&gt;&lt;i&gt;3rd Round:&lt;/i&gt; The power and location of each aura. If an aura is outside your line of sight, then you discern its direction but not its exact location.&lt;/p&gt;&lt;p&gt;&lt;i&gt;Aura Power:&lt;/i&gt; An evil aura''s power depends on the type of evil creature or object that you''re detecting and its HD, caster level, or (in the case of a cleric) class level; see the accompanying table. If an aura falls into more than one strength category, the spell indicates the stronger of the two.&lt;/p&gt;&lt;table width=&quot;100%&quot; border=&quot;1&quot; cellpadding=&quot;2&quot; cellspacing=&quot;2&quot; frame=&quot;VOID&quot; rules=&quot;ROWS&quot;&gt;

&lt;tr maxcol=&quot;6&quot; curcol=&quot;3&quot;&gt;
&lt;td/&gt;
&lt;td colspan=&quot;4&quot; align=&quot;center&quot;&gt;&lt;b&gt;-------- Aura Power --------&lt;/b&gt;&lt;br/&gt;&lt;/td&gt; 
&lt;/tr&gt;

&lt;tr maxcol=&quot;6&quot; curcol=&quot;6&quot;&gt;
&lt;td&gt;&lt;b&gt;Creature/Object&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;
&lt;td&gt;&lt;b&gt;Faint&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;
&lt;td&gt;&lt;b&gt;Moderate&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;
&lt;td&gt;&lt;b&gt;Strong&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;
&lt;td&gt;&lt;b&gt;Overwhelming&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;

&lt;/tr&gt;
&lt;tr maxcol=&quot;6&quot; curcol=&quot;6&quot;&gt;
&lt;td&gt;Evil creature&lt;b&gt;1&lt;/b&gt; (HD)&lt;br/&gt;&lt;/td&gt;
&lt;td&gt;10 or lower&lt;br/&gt;&lt;/td&gt;
&lt;td&gt;11-25&lt;br/&gt;&lt;/td&gt;
&lt;td&gt;26-50&lt;br/&gt;&lt;/td&gt;
&lt;td&gt;51 or higher&lt;br/&gt;&lt;/td&gt;

&lt;/tr&gt;
&lt;tr maxcol=&quot;6&quot; curcol=&quot;6&quot;&gt;
&lt;td&gt;Undead (HD)&lt;br/&gt;&lt;/td&gt;
&lt;td&gt;2 or lower&lt;br/&gt;&lt;/td&gt;
&lt;td&gt;3-8&lt;br/&gt;&lt;/td&gt;
&lt;td&gt;9-20&lt;br/&gt;&lt;/td&gt;
&lt;td&gt;21 or higher&lt;br/&gt;&lt;/td&gt;

&lt;/tr&gt;
&lt;tr maxcol=&quot;6&quot; curcol=&quot;6&quot;&gt;
&lt;td&gt;Evil outsider (HD)&lt;br/&gt;&lt;/td&gt;
&lt;td&gt;1 or lower&lt;br/&gt;&lt;/td&gt;
&lt;td&gt;2-4&lt;br/&gt;&lt;/td&gt;
&lt;td&gt;5-10&lt;br/&gt;&lt;/td&gt;
&lt;td&gt;11 or higher&lt;br/&gt;&lt;/td&gt;

&lt;/tr&gt;
&lt;tr maxcol=&quot;6&quot; curcol=&quot;6&quot;&gt;
&lt;td&gt;Cleric of an evil deity &lt;b&gt;2&lt;/b&gt; (class levels)&lt;br/&gt;&lt;/td&gt;
&lt;td&gt;1&lt;br/&gt;&lt;/td&gt;
&lt;td&gt;2-4&lt;br/&gt;&lt;/td&gt;
&lt;td&gt;5-10&lt;br/&gt;&lt;/td&gt;
&lt;td&gt;11 or higher&lt;br/&gt;&lt;/td&gt;

&lt;/tr&gt;
&lt;tr maxcol=&quot;6&quot; curcol=&quot;6&quot;&gt;
&lt;td&gt;Evil magic item or spell (caster level)&lt;br/&gt;&lt;/td&gt;
&lt;td&gt;2nd or lower&lt;br/&gt;&lt;/td&gt;
&lt;td&gt;3rd-8th&lt;br/&gt;&lt;/td&gt;
&lt;td&gt;9th-20th&lt;br/&gt;&lt;/td&gt;
&lt;td&gt;21st or higher&lt;br/&gt;&lt;/td&gt;

&lt;/tr&gt;

&lt;tr maxcol=&quot;6&quot; curcol=&quot;2&quot;&gt;
&lt;td colspan=&quot;5&quot;&gt;1 Except for undead and outsiders, which have their own entries on the table.&lt;br/&gt;&lt;/td&gt;

&lt;/tr&gt;

&lt;tr maxcol=&quot;6&quot; curcol=&quot;2&quot;&gt;
&lt;td colspan=&quot;5&quot;&gt;2 Some characters who are not clerics may radiate an aura of equivalent power. The class description will indicate whether this applies.&lt;br/&gt;&lt;/td&gt;

&lt;/tr&gt;
&lt;/table&gt;&lt;p&gt;&lt;i&gt;Lingering Aura:&lt;/i&gt; An evil aura lingers after its original source dissipates (in the case of a spell) or is destroyed (in the case of a creature or magic item). If &lt;i&gt;detect evil&lt;/i&gt; is cast and directed at such a location, the spell indicates an aura strength of dim (even weaker than a faint aura). How long the aura lingers at this dim level depends on its original power:&lt;/p&gt;&lt;table width=&quot;100%&quot; border=&quot;1&quot; cellpadding=&quot;2&quot; cellspacing=&quot;2&quot; frame=&quot;VOID&quot; rules=&quot;ROWS&quot;&gt;

&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;
&lt;td&gt;&lt;b&gt;Original Strength&lt;/b&gt;&lt;br/&gt;&lt;/td&gt; &lt;td&gt;&lt;b&gt;Duration of&lt;/b&gt;
 &lt;b&gt;Lingering Aura&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;

&lt;/tr&gt;

&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;
&lt;td&gt;Faint&lt;br/&gt;&lt;/td&gt;
&lt;td&gt;1d6 rounds&lt;br/&gt;&lt;/td&gt;

&lt;/tr&gt;
&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;
&lt;td&gt;Moderate&lt;br/&gt;&lt;/td&gt;
&lt;td&gt;1d6 minutes&lt;br/&gt;&lt;/td&gt;

&lt;/tr&gt;
&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;
&lt;td&gt;Strong&lt;br/&gt;&lt;/td&gt;
&lt;td&gt;1d6x10 minutes&lt;br/&gt;&lt;/td&gt;

&lt;/tr&gt;

&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;
&lt;td&gt;Overwhelming&lt;br/&gt;&lt;/td&gt;
&lt;td&gt;1d6 days&lt;br/&gt;&lt;/td&gt;

&lt;/tr&gt;
&lt;/table&gt;&lt;p&gt;Animals, traps, poisons, and other potential perils are not evil, and as such this spell does not detect them.&lt;/p&gt;&lt;p&gt;Each round, you can turn to detect evil in a new area. The spell can penetrate barriers, but 1 foot of stone, 1 inch of common metal, a thin sheet of lead, or 3 feet of wood or dirt blocks it.&lt;/p&gt;&lt;p/&gt;
&lt;/div&gt;', 'SRD 3.5 SpellsD-E')
