INSERT INTO [spell] ([id], [name], [altname], [school], [subschool], [descriptor], [spellcraft_dc], [level], [components], [casting_time], [range], [target], [area], [effect], [duration], [saving_throw], [spell_resistance], [short_description], [to_develop], [material_components], [arcane_material_components], [focus], [description], [xp_cost], [arcane_focus], [wizard_focus], [verbal_components], [sorcerer_focus], [bard_focus], [cleric_focus], [druid_focus], [full_text], [reference])
VALUES 
  (246, 'Dispel Evil', 'dispel evil', 'Abjuration', NULL, 'Good', NULL, 'Cleric 5, Good 5, Paladin 4', 'V, S, DF', '1 standard action', 'Touch', 'You and a touched evil creature from another plane; or you and an enchantment or evil spell on a touched creature or object', NULL, NULL, '1 round/level or until discharged, whichever comes first', 'See Book', 'See Book', '+4 bonus against attacks by evil creatures.', NULL, NULL, NULL, NULL, '&lt;p&gt;Shimmering, white, holy energy surrounds you. This power has three effects.&lt;/p&gt;
      &lt;p&gt;First, you gain a +4 deflection bonus to AC against attacks by evil creatures.&lt;/p&gt;
      &lt;p&gt;Second, on making a successful melee touch attack against an evil creature from another plane, you can choose to drive that creature back to its home plane. The creature can negate the effects with a successful Will save (spell resistance applies). This use discharges and ends the spell.&lt;/p&gt;
      &lt;p&gt;Third, with a touch you can automatically dispel any one enchantment spell cast by an evil creature or any one evil spell. &lt;i&gt;Exception:&lt;/i&gt; Spells that can''t be dispelled by &lt;i&gt;dispel magic&lt;/i&gt; also can''t be dispelled by &lt;i&gt;dispel evil&lt;/i&gt;. Saving throws and spell resistance do not apply to this effect. This use discharges and ends the spell.&lt;/p&gt;
      &lt;p/&gt;', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '&lt;div topic=&quot;Dispel Evil&quot; level=&quot;5&quot;&gt;&lt;p&gt;&lt;h5&gt;Dispel Evil&lt;/h5&gt;&lt;/p&gt;&lt;table width=&quot;100%&quot; border=&quot;1&quot; cellpadding=&quot;2&quot; cellspacing=&quot;2&quot; frame=&quot;VOID&quot; rules=&quot;ROWS&quot;&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;/&gt;&lt;td&gt;Abjuration [Good]&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Level:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Cleric 5, Good 5, Paladin 4&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Components:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;V, S, DF&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Casting Time:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;1 standard action&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Range:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Touch&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Target or Targets:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;You and a touched evil creature from another plane; or you and an enchantment or evil spell on a touched creature or object&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Duration:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;1 round/level or until discharged, whichever comes first&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Saving Throw:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;See Book&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Spell Resistance:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;See Book&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;p&gt;Shimmering, white, holy energy surrounds you. This power has three effects.&lt;/p&gt;&lt;p&gt;First, you gain a +4 deflection bonus to AC against attacks by evil creatures.&lt;/p&gt;&lt;p&gt;Second, on making a successful melee touch attack against an evil creature from another plane, you can choose to drive that creature back to its home plane. The creature can negate the effects with a successful Will save (spell resistance applies). This use discharges and ends the spell.&lt;/p&gt;&lt;p&gt;Third, with a touch you can automatically dispel any one enchantment spell cast by an evil creature or any one evil spell. &lt;i&gt;Exception:&lt;/i&gt; Spells that can''t be dispelled by &lt;i&gt;dispel magic&lt;/i&gt; also can''t be dispelled by &lt;i&gt;dispel evil&lt;/i&gt;. Saving throws and spell resistance do not apply to this effect. This use discharges and ends the spell.&lt;/p&gt;&lt;p/&gt;
&lt;/div&gt;', 'SRD 3.5 SpellsD-E')
GO

