VALUES 
  (248, 'Dispel Law', 'dispel law', 'Abjuration', NULL, 'Chaotic', NULL, 'Chaos 5, Cleric 5', 'V, S, DF', '1 standard action', 'Touch', 'You and a touched evil creature from another plane; or you and an enchantment or evil spell on a touched creature or object', NULL, NULL, '1 round/level or until discharged, whichever comes first', 'See Book', 'See Book', '+4 bonus against attacks by lawful creatures.', NULL, NULL, NULL, NULL, '&lt;p&gt;This spell functions like &lt;i&gt;dispel evil&lt;/i&gt;, except that you are surrounded by flickering, yellow, chaotic energy, and the spell affects lawful creatures and spells rather than evil ones.&lt;/p&gt;
      &lt;p/&gt;', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '&lt;div topic=&quot;Dispel Law&quot; level=&quot;5&quot;&gt;&lt;p&gt;&lt;h5&gt;Dispel Law&lt;/h5&gt;&lt;/p&gt;&lt;table width=&quot;100%&quot; border=&quot;1&quot; cellpadding=&quot;2&quot; cellspacing=&quot;2&quot; frame=&quot;VOID&quot; rules=&quot;ROWS&quot;&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;/&gt;&lt;td&gt;Abjuration [Chaotic]&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Level:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Chaos 5, Cleric 5&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;p&gt;This spell functions like &lt;i&gt;dispel evil&lt;/i&gt;, except that you are surrounded by flickering, yellow, chaotic energy, and the spell affects lawful creatures and spells rather than evil ones.&lt;/p&gt;&lt;p/&gt;
&lt;/div&gt;', 'SRD 3.5 SpellsD-E')
