INSERT INTO [spell] ([id], [name], [altname], [school], [subschool], [descriptor], [spellcraft_dc], [level], [components], [casting_time], [range], [target], [area], [effect], [duration], [saving_throw], [spell_resistance], [short_description], [to_develop], [material_components], [arcane_material_components], [focus], [description], [xp_cost], [arcane_focus], [wizard_focus], [verbal_components], [sorcerer_focus], [bard_focus], [cleric_focus], [druid_focus], [full_text], [reference])
VALUES 
  (250, 'Dispel Magic, Greater', 'greater dispel magic', 'Abjuration', NULL, NULL, NULL, 'Bard 5, Cleric 6, Druid 6, Sorcerer/Wizard 6', 'V, S', '1 standard action', 'Medium (30 m (100 ft.) + 3 m (10 ft.)/level)', 'One spellcaster, creature, or object; or 6 m (20-ft.). -radius burst', 'One spellcaster, creature, or object; or 6 m (20-ft.). -radius burst', NULL, 'Instantaneous', 'None', 'No', 'As dispel magic, but +20 on check.', NULL, NULL, NULL, NULL, '&lt;p&gt;This spell functions like &lt;i&gt;dispel magic&lt;/i&gt;, except that the maximum caster level on your dispel check is +20 instead of +10.&lt;/p&gt;
      &lt;p&gt;Additionally, &lt;i&gt;greater dispel magic&lt;/i&gt; has a chance to dispel any effect that &lt;i&gt;remove curse&lt;/i&gt; can remove, even if &lt;i&gt;dispel magic&lt;/i&gt; can''t dispel that effect.&lt;/p&gt;
      &lt;p/&gt;', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '&lt;div topic=&quot;Dispel Magic, Greater&quot; level=&quot;5&quot;&gt;&lt;p&gt;&lt;h5&gt;Dispel Magic, Greater&lt;/h5&gt;&lt;/p&gt;&lt;table width=&quot;100%&quot; border=&quot;1&quot; cellpadding=&quot;2&quot; cellspacing=&quot;2&quot; frame=&quot;VOID&quot; rules=&quot;ROWS&quot;&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;/&gt;&lt;td&gt;Abjuration&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Level:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Bard 5, Cleric 6, Druid 6, Sorcerer/Wizard 6&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;p&gt;This spell functions like &lt;i&gt;dispel magic&lt;/i&gt;, except that the maximum caster level on your dispel check is +20 instead of +10.&lt;/p&gt;&lt;p&gt;Additionally, &lt;i&gt;greater dispel magic&lt;/i&gt; has a chance to dispel any effect that &lt;i&gt;remove curse&lt;/i&gt; can remove, even if &lt;i&gt;dispel magic&lt;/i&gt; can''t dispel that effect.&lt;/p&gt;&lt;p/&gt;
&lt;/div&gt;', 'SRD 3.5 SpellsD-E')
