INSERT INTO [spell] ([id], [name], [altname], [school], [subschool], [descriptor], [spellcraft_dc], [level], [components], [casting_time], [range], [target], [area], [effect], [duration], [saving_throw], [spell_resistance], [short_description], [to_develop], [material_components], [arcane_material_components], [focus], [description], [xp_cost], [arcane_focus], [wizard_focus], [verbal_components], [sorcerer_focus], [bard_focus], [cleric_focus], [druid_focus], [full_text], [reference])
VALUES 
  (251, 'Displacement', 'displacement', 'Illusion', 'Glamer', NULL, NULL, 'Bard 3, Sorcerer/Wizard 3', 'V, M', '1 standard action', 'Touch', 'Creature touched', NULL, NULL, '1 round/level (D)', 'Will negates (harmless)', 'Yes (harmless)', 'Attacks miss subject 50%.', NULL, 'A small strip of leather twisted into a loop.', NULL, NULL, '&lt;p&gt;The subject of this spell appears to be about 2 feet away from its true location. The creature benefits from a 50% miss chance as if it had total concealment. However, unlike actual total concealment, &lt;i&gt;displacement&lt;/i&gt; does not prevent enemies from targeting the creature normally. &lt;i&gt;True seeing&lt;/i&gt; reveals its true location.&lt;/p&gt;
      &lt;p/&gt;', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '&lt;div topic=&quot;Displacement&quot; level=&quot;5&quot;&gt;&lt;p&gt;&lt;h5&gt;Displacement&lt;/h5&gt;&lt;/p&gt;&lt;table width=&quot;100%&quot; border=&quot;1&quot; cellpadding=&quot;2&quot; cellspacing=&quot;2&quot; frame=&quot;VOID&quot; rules=&quot;ROWS&quot;&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;/&gt;&lt;td&gt;Illusion (Glamer)&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Level:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Bard 3, Sorcerer/Wizard 3&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Components:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;V, M&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Casting Time:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;1 standard action&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Range:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Touch&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Target:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Creature touched&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Duration:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;1 round/level (D)&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Saving Throw:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Will negates (harmless)&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Spell Resistance:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Yes (harmless)&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;p&gt;The subject of this spell appears to be about 2 feet away from its true location. The creature benefits from a 50% miss chance as if it had total concealment. However, unlike actual total concealment, &lt;i&gt;displacement&lt;/i&gt; does not prevent enemies from targeting the creature normally. &lt;i&gt;True seeing&lt;/i&gt; reveals its true location.&lt;/p&gt;&lt;p&gt;&lt;i&gt;Material Component:&lt;/i&gt; A small strip of leather twisted into a loop.&lt;/p&gt;&lt;p/&gt;
&lt;/div&gt;', 'SRD 3.5 SpellsD-E')
GO

INSERT INTO [spell] ([id], [name], [altname], [school], [subschool], [descriptor], [spellcraft_dc], [level], [components], [casting_time], [range], [target], [area], [effect], [duration], [saving_throw], [spell_resistance], [short_description], [to_develop], [material_components], [arcane_material_components], [focus], [description], [xp_cost], [arcane_focus], [wizard_focus], [verbal_components], [sorcerer_focus], [bard_focus], [cleric_focus], [druid_focus], [full_text], [reference])
VALUES 
  (252, 'Disrupt Undead', 'disrupt undead', 'Necromancy', NULL, NULL, NULL, 'Sorcerer/Wizard 0', 'V, S', '1 standard action', 'Close (7,5 m (25 ft.) + 1,5 m (5 ft.)/2 levels)', NULL, NULL, 'Ray', 'Instantaneous', 'None', 'Yes', 'Deals 1d6 damage to one undead.', NULL, NULL, NULL, NULL, '&lt;p&gt;You direct a ray of positive energy. You must make a ranged touch attack to hit, and if the ray hits an undead creature, it deals 1d6 points of damage to it.&lt;/p&gt;
      &lt;p/&gt;', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '&lt;div topic=&quot;Disrupt Undead&quot; level=&quot;5&quot;&gt;&lt;p&gt;&lt;h5&gt;Disrupt Undead&lt;/h5&gt;&lt;/p&gt;&lt;table width=&quot;100%&quot; border=&quot;1&quot; cellpadding=&quot;2&quot; cellspacing=&quot;2&quot; frame=&quot;VOID&quot; rules=&quot;ROWS&quot;&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;/&gt;&lt;td&gt;Necromancy&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Level:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Sorcerer/Wizard 0&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Components:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;V, S&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Casting Time:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;1 standard action&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Range:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Close (7,5 m (25 ft.) + 1,5 m (5 ft.)/2 levels)&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Effect:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Ray&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Duration:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Instantaneous&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Saving Throw:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;None&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Spell Resistance:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Yes&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;p&gt;You direct a ray of positive energy. You must make a ranged touch attack to hit, and if the ray hits an undead creature, it deals 1d6 points of damage to it.&lt;/p&gt;&lt;p/&gt;
&lt;/div&gt;', 'SRD 3.5 SpellsD-E')
GO

INSERT INTO [spell] ([id], [name], [altname], [school], [subschool], [descriptor], [spellcraft_dc], [level], [components], [casting_time], [range], [target], [area], [effect], [duration], [saving_throw], [spell_resistance], [short_description], [to_develop], [material_components], [arcane_material_components], [focus], [description], [xp_cost], [arcane_focus], [wizard_focus], [verbal_components], [sorcerer_focus], [bard_focus], [cleric_focus], [druid_focus], [full_text], [reference])
VALUES 
  (253, 'Disrupting Weapon', 'disrupting weapon', 'Transmutation', NULL, NULL, NULL, 'Cleric 5', 'V, S', '1 standard action', 'Touch', 'One melee weapon', NULL, NULL, '1 round/level', 'Will negates (harmless, object); See Book', 'Yes (harmless, object)', 'Melee weapon destroys undead.', NULL, NULL, NULL, NULL, '&lt;p&gt;This spell makes a melee weapon deadly to undead. Any undead creature with HD equal to or less than your caster level must succeed on a Will save or be destroyed utterly if struck in combat with this weapon. Spell resistance does not apply against the destruction effect.&lt;/p&gt;
      &lt;p/&gt;', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '&lt;div topic=&quot;Disrupting Weapon&quot; level=&quot;5&quot;&gt;&lt;p&gt;&lt;h5&gt;Disrupting Weapon&lt;/h5&gt;&lt;/p&gt;&lt;table width=&quot;100%&quot; border=&quot;1&quot; cellpadding=&quot;2&quot; cellspacing=&quot;2&quot; frame=&quot;VOID&quot; rules=&quot;ROWS&quot;&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;/&gt;&lt;td&gt;Transmutation&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Level:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Cleric 5&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Components:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;V, S&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Casting Time:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;1 standard action&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Range:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Touch&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Targets:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;One melee weapon&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Duration:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;1 round/level&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Saving Throw:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Will negates (harmless, object); See Book&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Spell Resistance:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Yes (harmless, object)&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;p&gt;This spell makes a melee weapon deadly to undead. Any undead creature with HD equal to or less than your caster level must succeed on a Will save or be destroyed utterly if struck in combat with this weapon. Spell resistance does not apply against the destruction effect.&lt;/p&gt;&lt;p/&gt;
&lt;/div&gt;', 'SRD 3.5 SpellsD-E')
GO

INSERT INTO [spell] ([id], [name], [altname], [school], [subschool], [descriptor], [spellcraft_dc], [level], [components], [casting_time], [range], [target], [area], [effect], [duration], [saving_throw], [spell_resistance], [short_description], [to_develop], [material_components], [arcane_material_components], [focus], [description], [xp_cost], [arcane_focus], [wizard_focus], [verbal_components], [sorcerer_focus], [bard_focus], [cleric_focus], [druid_focus], [full_text], [reference])
VALUES 
  (254, 'Divination', 'divination', 'Divination', NULL, NULL, NULL, 'Cleric 4, Knowledge 4', 'V, S, M', '10 minutes', 'Personal', 'You', NULL, NULL, 'Instantaneous', NULL, NULL, 'Provides useful advice for specific proposed actions.', NULL, 'Incense and a sacrificial offering appropriate to your religion, together worth at least 25 gp.', NULL, NULL, '&lt;p&gt;Similar to &lt;i&gt;augury&lt;/i&gt; but more powerful, a &lt;i&gt;divination&lt;/i&gt; spell can provide you with a useful piece of advice in reply to a question concerning a specific goal, event, or activity that is to occur within one week. The advice can be as simple as a short phrase, or it might take the form of a cryptic rhyme or omen. If your party doesn''t act on the information, the conditions may change so that the information is no longer useful. The base chance for a correct &lt;i&gt;divination&lt;/i&gt; is 70% + 1% per caster level, to a maximum of 90%. If the dice roll fails, you know the spell failed, unless specific magic yielding false information is at work.&lt;/p&gt;
      &lt;p&gt;As with &lt;i&gt;augury&lt;/i&gt;, multiple &lt;i&gt;divinations&lt;/i&gt; about the same topic by the same caster use the same dice result as the first &lt;i&gt;divination&lt;/i&gt; spell and yield the same answer each time.&lt;/p&gt;
      &lt;p/&gt;', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '&lt;div topic=&quot;Divination&quot; level=&quot;5&quot;&gt;&lt;p&gt;&lt;h5&gt;Divination&lt;/h5&gt;&lt;/p&gt;&lt;table width=&quot;100%&quot; border=&quot;1&quot; cellpadding=&quot;2&quot; cellspacing=&quot;2&quot; frame=&quot;VOID&quot; rules=&quot;ROWS&quot;&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;/&gt;&lt;td&gt;Divination&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Level:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Cleric 4, Knowledge 4&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Components:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;V, S, M&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Casting Time:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;10 minutes&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Range:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Personal&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Target:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;You&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Duration:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Instantaneous&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;p&gt;Similar to &lt;i&gt;augury&lt;/i&gt; but more powerful, a &lt;i&gt;divination&lt;/i&gt; spell can provide you with a useful piece of advice in reply to a question concerning a specific goal, event, or activity that is to occur within one week. The advice can be as simple as a short phrase, or it might take the form of a cryptic rhyme or omen. If your party doesn''t act on the information, the conditions may change so that the information is no longer useful. The base chance for a correct &lt;i&gt;divination&lt;/i&gt; is 70% + 1% per caster level, to a maximum of 90%. If the dice roll fails, you know the spell failed, unless specific magic yielding false information is at work.&lt;/p&gt;&lt;p&gt;As with &lt;i&gt;augury&lt;/i&gt;, multiple &lt;i&gt;divinations&lt;/i&gt; about the same topic by the same caster use the same dice result as the first &lt;i&gt;divination&lt;/i&gt; spell and yield the same answer each time.&lt;/p&gt;&lt;p&gt;&lt;i&gt;Material Component:&lt;/i&gt; Incense and a sacrificial offering appropriate to your religion, together worth at least 25 gp.&lt;/p&gt;&lt;p/&gt;
&lt;/div&gt;', 'SRD 3.5 SpellsD-E')
GO

INSERT INTO [spell] ([id], [name], [altname], [school], [subschool], [descriptor], [spellcraft_dc], [level], [components], [casting_time], [range], [target], [area], [effect], [duration], [saving_throw], [spell_resistance], [short_description], [to_develop], [material_components], [arcane_material_components], [focus], [description], [xp_cost], [arcane_focus], [wizard_focus], [verbal_components], [sorcerer_focus], [bard_focus], [cleric_focus], [druid_focus], [full_text], [reference])
VALUES 
  (255, 'Divine Favor', 'divine favor', 'Evocation', NULL, NULL, NULL, 'Cleric 1, Paladin 1', 'V, S, DF', '1 standard action', 'Personal', 'You', NULL, NULL, '1 minute', NULL, NULL, 'You gain +1 per three levels on attack and damage rolls.', NULL, NULL, NULL, NULL, '&lt;p&gt;Calling upon the strength and wisdom of a deity, you gain a +1 luck bonus on attack and weapon damage rolls for every three caster levels you have (at least +1, maximum +6). The bonus doesn''t apply to spell damage.&lt;/p&gt;
      &lt;p/&gt;', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '&lt;div topic=&quot;Divine Favor&quot; level=&quot;5&quot;&gt;&lt;p&gt;&lt;h5&gt;Divine Favor&lt;/h5&gt;&lt;/p&gt;&lt;table width=&quot;100%&quot; border=&quot;1&quot; cellpadding=&quot;2&quot; cellspacing=&quot;2&quot; frame=&quot;VOID&quot; rules=&quot;ROWS&quot;&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;/&gt;&lt;td&gt;Evocation&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Level:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Cleric 1, Paladin 1&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Components:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;V, S, DF&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Casting Time:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;1 standard action&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Range:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Personal&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Target:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;You&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Duration:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;1 minute&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;p&gt;Calling upon the strength and wisdom of a deity, you gain a +1 luck bonus on attack and weapon damage rolls for every three caster levels you have (at least +1, maximum +6). The bonus doesn''t apply to spell damage.&lt;/p&gt;&lt;p/&gt;
&lt;/div&gt;', 'SRD 3.5 SpellsD-E')
GO

INSERT INTO [spell] ([id], [name], [altname], [school], [subschool], [descriptor], [spellcraft_dc], [level], [components], [casting_time], [range], [target], [area], [effect], [duration], [saving_throw], [spell_resistance], [short_description], [to_develop], [material_components], [arcane_material_components], [focus], [description], [xp_cost], [arcane_focus], [wizard_focus], [verbal_components], [sorcerer_focus], [bard_focus], [cleric_focus], [druid_focus], [full_text], [reference])
VALUES 
  (256, 'Divine Power', 'divine power', 'Evocation', NULL, NULL, NULL, 'Cleric 4, War 4', 'V, S, DF', '1 standard action', 'Personal', 'You', NULL, NULL, '1 round/level', NULL, NULL, 'You gain attack bonus, +6 to Str, and 1 hp/level.', NULL, NULL, NULL, NULL, '&lt;p&gt;Calling upon the divine power of your patron, you imbue yourself with strength and skill in combat. Your base attack bonus becomes equal to your character level (which may give you additional attacks), you gain a +6 enhancement bonus to Strength, and you gain 1 temporary hit point per caster level.&lt;/p&gt;
      &lt;p/&gt;', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '&lt;div topic=&quot;Divine Power&quot; level=&quot;5&quot;&gt;&lt;p&gt;&lt;h5&gt;Divine Power&lt;/h5&gt;&lt;/p&gt;&lt;table width=&quot;100%&quot; border=&quot;1&quot; cellpadding=&quot;2&quot; cellspacing=&quot;2&quot; frame=&quot;VOID&quot; rules=&quot;ROWS&quot;&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;/&gt;&lt;td&gt;Evocation&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Level:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Cleric 4, War 4&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Components:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;V, S, DF&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Casting Time:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;1 standard action&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Range:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Personal&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Target:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;You&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Duration:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;1 round/level&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;p&gt;Calling upon the divine power of your patron, you imbue yourself with strength and skill in combat. Your base attack bonus becomes equal to your character level (which may give you additional attacks), you gain a +6 enhancement bonus to Strength, and you gain 1 temporary hit point per caster level.&lt;/p&gt;&lt;p/&gt;
&lt;/div&gt;', 'SRD 3.5 SpellsD-E')
