INSERT INTO [spell] ([id], [name], [altname], [school], [subschool], [descriptor], [spellcraft_dc], [level], [components], [casting_time], [range], [target], [area], [effect], [duration], [saving_throw], [spell_resistance], [short_description], [to_develop], [material_components], [arcane_material_components], [focus], [description], [xp_cost], [arcane_focus], [wizard_focus], [verbal_components], [sorcerer_focus], [bard_focus], [cleric_focus], [druid_focus], [full_text], [reference])
VALUES 
  (261, 'Dream', 'dream', 'Illusion', 'Phantasm', 'Mind-Affecting', NULL, 'Bard 5, Sorcerer/Wizard 5', 'V, S', '1 minute', 'Unlimited', 'One living creature touched', NULL, NULL, 'See Book', 'None', 'Yes', 'Sends message to anyone sleeping.', NULL, NULL, NULL, NULL, '&lt;p&gt;You, or a messenger touched by you, sends a phantasmal message to others in the form of a dream. At the beginning of the spell, you must name the recipient or identify him or her by some title that leaves no doubt as to identity. The messenger then enters a trance, appears in the intended recipient''s dream, and delivers the message. The message can be of any length, and the recipient remembers it perfectly upon waking. The communication is one-way. The recipient cannot ask questions or offer information, nor can the messenger gain any information by observing the dreams of the recipient.&lt;/p&gt;
      &lt;p&gt;Once the message is delivered, the messenger''s mind returns instantly to its body. The duration of the spell is the time required for the messenger to enter the recipient''s dream and deliver the message.&lt;/p&gt;
      &lt;p&gt;If the recipient is awake when the spell begins, the messenger can choose to wake up (ending the spell) or remain in the trance. The messenger can remain in the trance until the recipient goes to sleep, then enter the recipient''s dream and deliver the message as normal. A messenger that is disturbed during the trance comes awake, ending the spell.&lt;/p&gt;
      &lt;p&gt;Creatures who don''t sleep (such as elves, but not half-elves) or don''t dream cannot be contacted by this spell.&lt;/p&gt;
      &lt;p&gt;The messenger is unaware of its own surroundings or of the activities around it while in the trance. It is defenseless both physically and mentally (always fails any saving throw) while in the trance.&lt;/p&gt;
      &lt;p/&gt;', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '&lt;div topic=&quot;Dream&quot; level=&quot;5&quot;&gt;&lt;p&gt;&lt;h5&gt;Dream&lt;/h5&gt;&lt;/p&gt;&lt;table width=&quot;100%&quot; border=&quot;1&quot; cellpadding=&quot;2&quot; cellspacing=&quot;2&quot; frame=&quot;VOID&quot; rules=&quot;ROWS&quot;&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;/&gt;&lt;td&gt;Illusion (Phantasm) [Mind-Affecting]&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Level:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Bard 5, Sorcerer/Wizard 5&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Components:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;V, S&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Casting Time:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;1 minute&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Range:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Unlimited&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Target:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;One living creature touched&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Duration:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;See Book&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Saving Throw:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;None&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Spell Resistance:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Yes&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;p&gt;You, or a messenger touched by you, sends a phantasmal message to others in the form of a dream. At the beginning of the spell, you must name the recipient or identify him or her by some title that leaves no doubt as to identity. The messenger then enters a trance, appears in the intended recipient''s dream, and delivers the message. The message can be of any length, and the recipient remembers it perfectly upon waking. The communication is one-way. The recipient cannot ask questions or offer information, nor can the messenger gain any information by observing the dreams of the recipient.&lt;/p&gt;&lt;p&gt;Once the message is delivered, the messenger''s mind returns instantly to its body. The duration of the spell is the time required for the messenger to enter the recipient''s dream and deliver the message.&lt;/p&gt;&lt;p&gt;If the recipient is awake when the spell begins, the messenger can choose to wake up (ending the spell) or remain in the trance. The messenger can remain in the trance until the recipient goes to sleep, then enter the recipient''s dream and deliver the message as normal. A messenger that is disturbed during the trance comes awake, ending the spell.&lt;/p&gt;&lt;p&gt;Creatures who don''t sleep (such as elves, but not half-elves) or don''t dream cannot be contacted by this spell.&lt;/p&gt;&lt;p&gt;The messenger is unaware of its own surroundings or of the activities around it while in the trance. It is defenseless both physically and mentally (always fails any saving throw) while in the trance.&lt;/p&gt;&lt;p/&gt;
&lt;/div&gt;', 'SRD 3.5 SpellsD-E')
GO

INSERT INTO [spell] ([id], [name], [altname], [school], [subschool], [descriptor], [spellcraft_dc], [level], [components], [casting_time], [range], [target], [area], [effect], [duration], [saving_throw], [spell_resistance], [short_description], [to_develop], [material_components], [arcane_material_components], [focus], [description], [xp_cost], [arcane_focus], [wizard_focus], [verbal_components], [sorcerer_focus], [bard_focus], [cleric_focus], [druid_focus], [full_text], [reference])
VALUES 
  (262, 'Eagle''s Splendor', 'eagle''s splendor', 'Transmutation', NULL, NULL, NULL, 'Bard 2, Cleric 2, Paladin 2, Sorcerer/Wizard 2', 'V, S, M/DF', '1 standard action', 'Touch', 'Creature touched', NULL, NULL, '1 min./level', 'Will negates (harmless)', 'Yes', 'Subject gains +4 to Cha for 1 min./level.', NULL, NULL, 'A few feathers or a pinch of droppings from an eagle.', NULL, '&lt;p&gt;The transmuted creature becomes more poised, articulate, and personally forceful. The spell grants a +4 enhancement bonus to Charisma, adding the usual benefits to Charisma-based skill checks and other uses of the Charisma modifier. Sorcerers and bards (and other spellcasters who rely on Charisma) affected by this spell do not gain any additional bonus spells for the increased Charisma, but the save DCs for spells they cast while under this spell''s effect do increase.&lt;/p&gt;
      &lt;p/&gt;', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '&lt;div topic=&quot;Eagle''s Splendor&quot; level=&quot;5&quot;&gt;&lt;p&gt;&lt;h5&gt;Eagle''s Splendor&lt;/h5&gt;&lt;/p&gt;&lt;table width=&quot;100%&quot; border=&quot;1&quot; cellpadding=&quot;2&quot; cellspacing=&quot;2&quot; frame=&quot;VOID&quot; rules=&quot;ROWS&quot;&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;/&gt;&lt;td&gt;Transmutation&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Level:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Bard 2, Cleric 2, Paladin 2, Sorcerer/Wizard 2&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Components:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;V, S, M/DF&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Casting Time:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;1 standard action&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Range:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Touch&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Target:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Creature touched&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Duration:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;1 min./level&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Saving Throw:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Will negates (harmless)&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Spell Resistance:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Yes&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;p&gt;The transmuted creature becomes more poised, articulate, and personally forceful. The spell grants a +4 enhancement bonus to Charisma, adding the usual benefits to Charisma-based skill checks and other uses of the Charisma modifier. Sorcerers and bards (and other spellcasters who rely on Charisma) affected by this spell do not gain any additional bonus spells for the increased Charisma, but the save DCs for spells they cast while under this spell''s effect do increase.&lt;/p&gt;&lt;p&gt;&lt;i&gt;Arcane Material Component:&lt;/i&gt; A few feathers or a pinch of droppings from an eagle.&lt;/p&gt;&lt;p/&gt;
&lt;/div&gt;', 'SRD 3.5 SpellsD-E')
GO

INSERT INTO [spell] ([id], [name], [altname], [school], [subschool], [descriptor], [spellcraft_dc], [level], [components], [casting_time], [range], [target], [area], [effect], [duration], [saving_throw], [spell_resistance], [short_description], [to_develop], [material_components], [arcane_material_components], [focus], [description], [xp_cost], [arcane_focus], [wizard_focus], [verbal_components], [sorcerer_focus], [bard_focus], [cleric_focus], [druid_focus], [full_text], [reference])
VALUES 
  (263, 'Eagle''s Splendor, Mass', 'mass eagle''s splendor', 'Transmutation', NULL, NULL, NULL, 'Bard 6, Cleric 6, Sorcerer/Wizard 6', 'V, S, M/DF', '1 standard action', 'Close (7,5 m (25 ft.) + 1,5 m (5 ft.)/2 levels)', 'One creature/level, no two of which can be more than 9 m (30 ft.) apart', NULL, NULL, '1 min./level', 'Will negates (harmless)', 'Yes', 'As eagle''s splendor, affects one subject/level.', NULL, NULL, 'A few feathers or a pinch of droppings from an eagle.', NULL, '&lt;p&gt;This spell functions like &lt;i&gt;eagle''s splendor&lt;/i&gt;, except that it affects multiple creatures.&lt;/p&gt;
      &lt;p/&gt;', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '&lt;div topic=&quot;Eagle''s Splendor, Mass&quot; level=&quot;5&quot;&gt;&lt;p&gt;&lt;h5&gt;Eagle''s Splendor, Mass&lt;/h5&gt;&lt;/p&gt;&lt;table width=&quot;100%&quot; border=&quot;1&quot; cellpadding=&quot;2&quot; cellspacing=&quot;2&quot; frame=&quot;VOID&quot; rules=&quot;ROWS&quot;&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;/&gt;&lt;td&gt;Transmutation&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Level:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Bard 6, Cleric 6, Sorcerer/Wizard 6&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Range:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Close (7,5 m (25 ft.) + 1,5 m (5 ft.)/2 levels)&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Target:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;One creature/level, no two of which can be more than 9 m (30 ft.) apart&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;p&gt;This spell functions like &lt;i&gt;eagle''s splendor&lt;/i&gt;, except that it affects multiple creatures.&lt;/p&gt;&lt;p/&gt;
&lt;/div&gt;', 'SRD 3.5 SpellsD-E')
