INSERT INTO [spell] ([id], [name], [altname], [school], [subschool], [descriptor], [spellcraft_dc], [level], [components], [casting_time], [range], [target], [area], [effect], [duration], [saving_throw], [spell_resistance], [short_description], [to_develop], [material_components], [arcane_material_components], [focus], [description], [xp_cost], [arcane_focus], [wizard_focus], [verbal_components], [sorcerer_focus], [bard_focus], [cleric_focus], [druid_focus], [full_text], [reference])
VALUES 
  (321, 'Glibness', 'glibness', 'Transmutation', NULL, NULL, NULL, 'Bard 3', 'S', '1 standard action', 'Personal', 'You', NULL, NULL, '10 min./level (D)', NULL, NULL, 'You gain +30 bonus on Bluff checks, and your lies can escape magical discernment.', NULL, NULL, NULL, NULL, '&lt;p&gt;Your speech becomes fluent and more believable. You gain a +30 bonus on Bluff checks made to convince another of the truth of your words. (This bonus doesn''t apply to other uses of the Bluff skill, such as feinting in combat, creating a diversion to hide, or communicating a hidden message via innuendo.)&lt;/p&gt;
      &lt;p&gt;If a magical effect is used against you that would detect your lies or force you to speak the truth the user of the effect must succeed on a caster level check (1d20 + caster level) against a DC of 15 + your caster level to succeed. Failure means the effect does not detect your lies or force you to speak only the truth.&lt;/p&gt;
      &lt;p/&gt;', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '&lt;div topic=&quot;Glibness&quot; level=&quot;5&quot;&gt;&lt;p&gt;&lt;h5&gt;Glibness&lt;/h5&gt;&lt;/p&gt;&lt;table width=&quot;100%&quot; border=&quot;1&quot; cellpadding=&quot;2&quot; cellspacing=&quot;2&quot; frame=&quot;VOID&quot; rules=&quot;ROWS&quot;&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;/&gt;&lt;td&gt;Transmutation&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Level:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Bard 3&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Components:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;S&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Casting Time:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;1 standard action&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Range:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Personal&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Target:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;You&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Duration:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;10 min./level (D)&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;p&gt;Your speech becomes fluent and more believable. You gain a +30 bonus on Bluff checks made to convince another of the truth of your words. (This bonus doesn''t apply to other uses of the Bluff skill, such as feinting in combat, creating a diversion to hide, or communicating a hidden message via innuendo.)&lt;/p&gt;&lt;p&gt;If a magical effect is used against you that would detect your lies or force you to speak the truth the user of the effect must succeed on a caster level check (1d20 + caster level) against a DC of 15 + your caster level to succeed. Failure means the effect does not detect your lies or force you to speak only the truth.&lt;/p&gt;&lt;p/&gt;
&lt;/div&gt;', 'SRD 3.5 SpellsF-G')
GO

INSERT INTO [spell] ([id], [name], [altname], [school], [subschool], [descriptor], [spellcraft_dc], [level], [components], [casting_time], [range], [target], [area], [effect], [duration], [saving_throw], [spell_resistance], [short_description], [to_develop], [material_components], [arcane_material_components], [focus], [description], [xp_cost], [arcane_focus], [wizard_focus], [verbal_components], [sorcerer_focus], [bard_focus], [cleric_focus], [druid_focus], [full_text], [reference])
VALUES 
  (322, 'Glitterdust', 'glitterdust', 'Conjuration', 'Creation', NULL, NULL, 'Bard 2, Sorcerer/Wizard 2', 'V, S, M', '1 standard action', 'Medium (30 m (100 ft.) + 3 m (10 ft.)/level)', NULL, 'Creatures and objects within 3 m (10-ft.) -radius spread', NULL, '1 round/level', 'Will negates (blinding only)', 'No', 'Blinds creatures, outlines invisible creatures.', NULL, 'Ground mica.', NULL, NULL, '&lt;p&gt;A cloud of golden particles covers everyone and everything in the area, causing creatures to become blinded and visibly outlining invisible things for the duration of the spell. All within the area are covered by the dust, which cannot be removed and continues to sparkle until it fades.&lt;/p&gt;
      &lt;p&gt;Any creature covered by the dust takes a -40 penalty on Hide checks.&lt;/p&gt;
      &lt;p/&gt;', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '&lt;div topic=&quot;Glitterdust&quot; level=&quot;5&quot;&gt;&lt;p&gt;&lt;h5&gt;Glitterdust&lt;/h5&gt;&lt;/p&gt;&lt;table width=&quot;100%&quot; border=&quot;1&quot; cellpadding=&quot;2&quot; cellspacing=&quot;2&quot; frame=&quot;VOID&quot; rules=&quot;ROWS&quot;&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;/&gt;&lt;td&gt;Conjuration (Creation)&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Level:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Bard 2, Sorcerer/Wizard 2&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Components:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;V, S, M&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Casting Time:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;1 standard action&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Range:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Medium (30 m (100 ft.) + 3 m (10 ft.)/level)&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Area:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Creatures and objects within 3 m (10-ft.) -radius spread&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Duration:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;1 round/level&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Saving Throw:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Will negates (blinding only)&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Spell Resistance:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;No&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;p&gt;A cloud of golden particles covers everyone and everything in the area, causing creatures to become blinded and visibly outlining invisible things for the duration of the spell. All within the area are covered by the dust, which cannot be removed and continues to sparkle until it fades.&lt;/p&gt;&lt;p&gt;Any creature covered by the dust takes a -40 penalty on Hide checks.&lt;/p&gt;&lt;p&gt;&lt;i&gt;Material Component:&lt;/i&gt; Ground mica.&lt;/p&gt;&lt;p/&gt;
&lt;/div&gt;', 'SRD 3.5 SpellsF-G')
GO

INSERT INTO [spell] ([id], [name], [altname], [school], [subschool], [descriptor], [spellcraft_dc], [level], [components], [casting_time], [range], [target], [area], [effect], [duration], [saving_throw], [spell_resistance], [short_description], [to_develop], [material_components], [arcane_material_components], [focus], [description], [xp_cost], [arcane_focus], [wizard_focus], [verbal_components], [sorcerer_focus], [bard_focus], [cleric_focus], [druid_focus], [full_text], [reference])
VALUES 
  (323, 'Globe of Invulnerability', 'globe of invulnerability', 'Abjuration', NULL, NULL, NULL, 'Sorcerer/Wizard 6', 'V, S, M', '1 standard action', '10 ft.', NULL, '3 m (10-ft.) -radius spherical emanation, centered on you', NULL, '1 round/level (D)', 'None', 'No', 'As lesser globe of invulnerability, plus 4th-level spell effects.', NULL, 'A glass or crystal bead that shatters at the expiration of the spell.', NULL, NULL, '&lt;p&gt;This spell functions like &lt;i&gt;lesser globe of invulnerability&lt;/i&gt;,  except that it also excludes 4th-level spells and spell-like effects.&lt;/p&gt;
      &lt;p/&gt;', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '&lt;div topic=&quot;Globe of Invulnerability&quot; level=&quot;5&quot;&gt;&lt;p&gt;&lt;h5&gt;Globe of Invulnerability&lt;/h5&gt;&lt;/p&gt;&lt;table width=&quot;100%&quot; border=&quot;1&quot; cellpadding=&quot;2&quot; cellspacing=&quot;2&quot; frame=&quot;VOID&quot; rules=&quot;ROWS&quot;&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;/&gt;&lt;td&gt;Abjuration&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Level:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Sorcerer/Wizard 6&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;p&gt;This spell functions like &lt;i&gt;lesser globe of invulnerability&lt;/i&gt;,  except that it also excludes 4th-level spells and spell-like effects.&lt;/p&gt;&lt;p/&gt;
&lt;/div&gt;', 'SRD 3.5 SpellsF-G')
GO

INSERT INTO [spell] ([id], [name], [altname], [school], [subschool], [descriptor], [spellcraft_dc], [level], [components], [casting_time], [range], [target], [area], [effect], [duration], [saving_throw], [spell_resistance], [short_description], [to_develop], [material_components], [arcane_material_components], [focus], [description], [xp_cost], [arcane_focus], [wizard_focus], [verbal_components], [sorcerer_focus], [bard_focus], [cleric_focus], [druid_focus], [full_text], [reference])
VALUES 
  (324, 'Globe of Invulnerability, Lesser', 'lesser globe of invulnerability', 'Abjuration', NULL, NULL, NULL, 'Sorcerer/Wizard 4', 'V, S, M', '1 standard action', '10 ft.', NULL, '3 m (10-ft.) -radius spherical emanation, centered on you', NULL, '1 round/level (D)', 'None', 'No', 'Stops 1st- through 3rd-level spell effects.', NULL, 'A glass or crystal bead that shatters at the expiration of the spell.', NULL, NULL, '&lt;p&gt;An immobile, faintly shimmering magical sphere surrounds you and excludes all spell effects of 3rd level or lower. The area or effect of any such spells does not include the area of the &lt;i&gt;lesser globe of invulnerability&lt;/i&gt;. Such spells fail to affect any target located within the globe. Excluded effects include spell-like abilities and spells or spell-like effects from items. However, any type of spell can be cast through or out of the magical globe. Spells of 4th level and higher are not affected by the globe, nor are spells already in effect when the globe is cast. The globe can be brought down by a targeted &lt;i&gt;dispel magic&lt;/i&gt; spell, but not by an area &lt;i&gt;dispel magic&lt;/i&gt;. You can leave and return to the globe without penalty.&lt;/p&gt;
      &lt;p&gt;Note that spell effects are not disrupted unless their effects enter the globe, and even then they are merely suppressed, not dispelled.&lt;/p&gt;
      &lt;p&gt;If a given spell has more than one level depending on which character class is casting it, use the level appropriate to the caster to determine whether &lt;i&gt;lesser globe of invulnerability&lt;/i&gt; stops it.&lt;/p&gt;
      &lt;p/&gt;', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '&lt;div topic=&quot;Globe of Invulnerability, Lesser&quot; level=&quot;5&quot;&gt;&lt;p&gt;&lt;h5&gt;Globe of Invulnerability, Lesser&lt;/h5&gt;&lt;/p&gt;&lt;table width=&quot;100%&quot; border=&quot;1&quot; cellpadding=&quot;2&quot; cellspacing=&quot;2&quot; frame=&quot;VOID&quot; rules=&quot;ROWS&quot;&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;/&gt;&lt;td&gt;Abjuration&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Level:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Sorcerer/Wizard 4&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Components:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;V, S, M&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Casting Time:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;1 standard action&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Range:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;10 ft.&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Area:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;3 m (10-ft.) -radius spherical emanation, centered on you&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Duration:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;1 round/level (D)&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Saving Throw:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;None&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Spell Resistance:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;No&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;p&gt;An immobile, faintly shimmering magical sphere surrounds you and excludes all spell effects of 3rd level or lower. The area or effect of any such spells does not include the area of the &lt;i&gt;lesser globe of invulnerability&lt;/i&gt;. Such spells fail to affect any target located within the globe. Excluded effects include spell-like abilities and spells or spell-like effects from items. However, any type of spell can be cast through or out of the magical globe. Spells of 4th level and higher are not affected by the globe, nor are spells already in effect when the globe is cast. The globe can be brought down by a targeted &lt;i&gt;dispel magic&lt;/i&gt; spell, but not by an area &lt;i&gt;dispel magic&lt;/i&gt;. You can leave and return to the globe without penalty.&lt;/p&gt;&lt;p&gt;Note that spell effects are not disrupted unless their effects enter the globe, and even then they are merely suppressed, not dispelled.&lt;/p&gt;&lt;p&gt;If a given spell has more than one level depending on which character class is casting it, use the level appropriate to the caster to determine whether &lt;i&gt;lesser globe of invulnerability&lt;/i&gt; stops it.&lt;/p&gt;&lt;p&gt;&lt;i&gt;Material Component:&lt;/i&gt; A glass or crystal bead that shatters at the expiration of the spell.&lt;/p&gt;&lt;p/&gt;
&lt;/div&gt;', 'SRD 3.5 SpellsF-G')
GO

INSERT INTO [spell] ([id], [name], [altname], [school], [subschool], [descriptor], [spellcraft_dc], [level], [components], [casting_time], [range], [target], [area], [effect], [duration], [saving_throw], [spell_resistance], [short_description], [to_develop], [material_components], [arcane_material_components], [focus], [description], [xp_cost], [arcane_focus], [wizard_focus], [verbal_components], [sorcerer_focus], [bard_focus], [cleric_focus], [druid_focus], [full_text], [reference])
VALUES 
  (325, 'Glyph of Warding', 'glyph of warding', 'Abjuration', NULL, NULL, NULL, 'Cleric 3', 'V, S, M', '10 minutes', 'Touch', 'Object touched or up to 5 sq. ft./level', 'Object touched or up to 5 sq. ft./level', NULL, 'Permanent until discharged (D)', 'See Book', 'No (object) and Yes; See Book', 'Inscription harms those who pass it.', NULL, 'You trace the glyph with incense, which must first be sprinkled with powdered diamond worth at least 200 gp.', NULL, NULL, '&lt;p&gt;This powerful inscription harms those who enter, pass, or open the warded area or object. A &lt;i&gt;glyph of warding&lt;/i&gt; can guard a bridge or passage, ward a portal, trap a chest or box, and so on.&lt;/p&gt;
      &lt;p&gt;You set the conditions of the ward. Typically, any creature entering the warded area or opening the warded object without speaking a password (which you set when casting the spell) is subject to the magic it stores. Alternatively or in addition to a password trigger, &lt;i&gt;glyphs&lt;/i&gt; can be set according to physical characteristics (such as height or weight) or creature type, subtype, or kind. &lt;i&gt;glyphs&lt;/i&gt; can also be set with respect to good, evil, law, or chaos, or to pass those of your religion. They cannot be set according to class, Hit Dice, or level. &lt;i&gt;glyphs&lt;/i&gt; respond to invisible creatures normally but are not triggered by those who travel past them ethereally. Multiple &lt;i&gt;glyphs&lt;/i&gt; cannot be cast on the same area. However, if a cabinet has three drawers, each can be separately warded.&lt;/p&gt;
      &lt;p&gt;When casting the spell, you weave a tracery of faintly glowing lines around the warding sigil. A &lt;i&gt;glyph&lt;/i&gt; can be placed to conform to any shape up to the limitations of your total square footage. When the spell is completed, the &lt;i&gt;glyph&lt;/i&gt; and tracery become nearly invisible.&lt;/p&gt;
      &lt;p&gt;&lt;i&gt;glyphs&lt;/i&gt; cannot be affected or bypassed by such means as physical or magical probing, though they can be dispelled. &lt;i&gt;Mislead&lt;/i&gt;, &lt;i&gt;polymorph&lt;/i&gt;, and &lt;i&gt;nondetection&lt;/i&gt; (and similar magical effects) can fool a &lt;i&gt;glyph&lt;/i&gt;, though nonmagical disguises and the like can''t. &lt;i&gt;Read magic&lt;/i&gt; allows you to identify a &lt;i&gt;glyph of warding&lt;/i&gt; with a DC 13 Spellcraft check. Identifying the &lt;i&gt;glyph&lt;/i&gt; does not discharge it and allows you to know the basic nature of the &lt;i&gt;glyph&lt;/i&gt; (version, type of damage caused, what spell is stored).&lt;/p&gt;
      &lt;p&gt;&lt;i&gt;Note:&lt;/i&gt; Magic traps such as &lt;i&gt;glyph of warding&lt;/i&gt; are hard to detect and disable. A rogue (only) can use the Search skill to find the &lt;i&gt;glyph&lt;/i&gt; and Disable Device to thwart it. The DC in each case is 25 + spell level, or 28 for &lt;i&gt;glyph of warding&lt;/i&gt;.&lt;/p&gt;
      &lt;p&gt;Depending on the version selected, a &lt;i&gt;glyph&lt;/i&gt; either blasts the intruder or activates a spell.&lt;/p&gt;
      &lt;p&gt;&lt;i&gt;Blast Glyph:&lt;/i&gt; A &lt;i&gt;blast glyph&lt;/i&gt; deals 1d8 points of damage per two caster levels (maximum 5d8) to the intruder and to all within 5 feet of him or her. This damage is acid, cold, fire, electricity, or sonic (caster''s choice, made at time of casting). Each creature affected can attempt a Reflex save to take half damage. Spell resistance applies against this effect.&lt;/p&gt;
      &lt;p&gt;&lt;i&gt;Spell Glyph:&lt;/i&gt; You can store any harmful spell of 3rd level or lower that you know. All level-dependent features of the spell are based on your caster level at the time of casting the &lt;i&gt;glyph&lt;/i&gt;. If the spell has a target, it targets the intruder. If the spell has an area or an amorphous effect the area or effect is centered on the intruder. If the spell summons creatures, they appear as close as possible to the intruder and attack. Saving throws and spell resistance operate as normal, except that the DC is based on the level of the spell stored in the &lt;i&gt;glyph&lt;/i&gt;.&lt;/p&gt;
      &lt;p/&gt;', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '&lt;div topic=&quot;Glyph of Warding&quot; level=&quot;5&quot;&gt;&lt;p&gt;&lt;h5&gt;Glyph of Warding&lt;/h5&gt;&lt;/p&gt;&lt;table width=&quot;100%&quot; border=&quot;1&quot; cellpadding=&quot;2&quot; cellspacing=&quot;2&quot; frame=&quot;VOID&quot; rules=&quot;ROWS&quot;&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;/&gt;&lt;td&gt;Abjuration&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Level:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Cleric 3&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Components:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;V, S, M&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Casting Time:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;10 minutes&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Range:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Touch&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Target or Area:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Object touched or up to 5 sq. ft./level&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Duration:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Permanent until discharged (D)&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Saving Throw:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;See Book&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Spell Resistance:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;No (object) and Yes; See Book&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;p&gt;This powerful inscription harms those who enter, pass, or open the warded area or object. A &lt;i&gt;glyph of warding&lt;/i&gt; can guard a bridge or passage, ward a portal, trap a chest or box, and so on.&lt;/p&gt;&lt;p&gt;You set the conditions of the ward. Typically, any creature entering the warded area or opening the warded object without speaking a password (which you set when casting the spell) is subject to the magic it stores. Alternatively or in addition to a password trigger, &lt;i&gt;glyphs&lt;/i&gt; can be set according to physical characteristics (such as height or weight) or creature type, subtype, or kind. &lt;i&gt;glyphs&lt;/i&gt; can also be set with respect to good, evil, law, or chaos, or to pass those of your religion. They cannot be set according to class, Hit Dice, or level. &lt;i&gt;glyphs&lt;/i&gt; respond to invisible creatures normally but are not triggered by those who travel past them ethereally. Multiple &lt;i&gt;glyphs&lt;/i&gt; cannot be cast on the same area. However, if a cabinet has three drawers, each can be separately warded.&lt;/p&gt;&lt;p&gt;When casting the spell, you weave a tracery of faintly glowing lines around the warding sigil. A &lt;i&gt;glyph&lt;/i&gt; can be placed to conform to any shape up to the limitations of your total square footage. When the spell is completed, the &lt;i&gt;glyph&lt;/i&gt; and tracery become nearly invisible.&lt;/p&gt;&lt;p&gt;&lt;i&gt;glyphs&lt;/i&gt; cannot be affected or bypassed by such means as physical or magical probing, though they can be dispelled. &lt;i&gt;Mislead&lt;/i&gt;, &lt;i&gt;polymorph&lt;/i&gt;, and &lt;i&gt;nondetection&lt;/i&gt; (and similar magical effects) can fool a &lt;i&gt;glyph&lt;/i&gt;, though nonmagical disguises and the like can''t. &lt;i&gt;Read magic&lt;/i&gt; allows you to identify a &lt;i&gt;glyph of warding&lt;/i&gt; with a DC 13 Spellcraft check. Identifying the &lt;i&gt;glyph&lt;/i&gt; does not discharge it and allows you to know the basic nature of the &lt;i&gt;glyph&lt;/i&gt; (version, type of damage caused, what spell is stored).&lt;/p&gt;&lt;p&gt;&lt;i&gt;Note:&lt;/i&gt; Magic traps such as &lt;i&gt;glyph of warding&lt;/i&gt; are hard to detect and disable. A rogue (only) can use the Search skill to find the &lt;i&gt;glyph&lt;/i&gt; and Disable Device to thwart it. The DC in each case is 25 + spell level, or 28 for &lt;i&gt;glyph of warding&lt;/i&gt;.&lt;/p&gt;&lt;p&gt;Depending on the version selected, a &lt;i&gt;glyph&lt;/i&gt; either blasts the intruder or activates a spell.&lt;/p&gt;&lt;p&gt;&lt;i&gt;Blast Glyph:&lt;/i&gt; A &lt;i&gt;blast glyph&lt;/i&gt; deals 1d8 points of damage per two caster levels (maximum 5d8) to the intruder and to all within 5 feet of him or her. This damage is acid, cold, fire, electricity, or sonic (caster''s choice, made at time of casting). Each creature affected can attempt a Reflex save to take half damage. Spell resistance applies against this effect.&lt;/p&gt;&lt;p&gt;&lt;i&gt;Spell Glyph:&lt;/i&gt; You can store any harmful spell of 3rd level or lower that you know. All level-dependent features of the spell are based on your caster level at the time of casting the &lt;i&gt;glyph&lt;/i&gt;. If the spell has a target, it targets the intruder. If the spell has an area or an amorphous effect the area or effect is centered on the intruder. If the spell summons creatures, they appear as close as possible to the intruder and attack. Saving throws and spell resistance operate as normal, except that the DC is based on the level of the spell stored in the &lt;i&gt;glyph&lt;/i&gt;.&lt;/p&gt;&lt;p&gt;&lt;i&gt;Material Component:&lt;/i&gt; You trace the &lt;i&gt;glyph&lt;/i&gt; with incense, which must first be sprinkled with powdered diamond worth at least 200 gp.&lt;/p&gt;&lt;p/&gt;
&lt;/div&gt;', 'SRD 3.5 SpellsF-G')
GO

INSERT INTO [spell] ([id], [name], [altname], [school], [subschool], [descriptor], [spellcraft_dc], [level], [components], [casting_time], [range], [target], [area], [effect], [duration], [saving_throw], [spell_resistance], [short_description], [to_develop], [material_components], [arcane_material_components], [focus], [description], [xp_cost], [arcane_focus], [wizard_focus], [verbal_components], [sorcerer_focus], [bard_focus], [cleric_focus], [druid_focus], [full_text], [reference])
VALUES 
  (326, 'Glyph of Warding, Greater', 'greater glyph of warding', 'Abjuration', NULL, NULL, NULL, 'Cleric 6', 'V, S, M', '10 minutes', 'Touch', 'Object touched or up to 5 sq. ft./level', 'Object touched or up to 5 sq. ft./level', NULL, 'Permanent until discharged (D)', 'See Book', 'No (object) and Yes; See Book', 'As glyph of warding, but up to 10d8 damage or 6th-level spell.', NULL, 'You trace the glyph with incense, which must first be sprinkled with powdered diamond worth at least 400 gp.', NULL, NULL, '&lt;p&gt;This spell functions like &lt;i&gt;glyph of warding&lt;/i&gt;,  except that a &lt;i&gt;greater blast glyph&lt;/i&gt; deals up to 10d8 points of damage, and a &lt;i&gt;greater spell glyph&lt;/i&gt; can store a spell of 6th level or lower.&lt;/p&gt;
      &lt;p/&gt;', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '&lt;div topic=&quot;Glyph of Warding, Greater&quot; level=&quot;5&quot;&gt;&lt;p&gt;&lt;h5&gt;Glyph of Warding, Greater&lt;/h5&gt;&lt;/p&gt;&lt;table width=&quot;100%&quot; border=&quot;1&quot; cellpadding=&quot;2&quot; cellspacing=&quot;2&quot; frame=&quot;VOID&quot; rules=&quot;ROWS&quot;&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;/&gt;&lt;td&gt;Abjuration&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Level:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Cleric 6&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;p&gt;This spell functions like &lt;i&gt;glyph of warding&lt;/i&gt;,  except that a &lt;i&gt;greater blast glyph&lt;/i&gt; deals up to 10d8 points of damage, and a &lt;i&gt;greater spell glyph&lt;/i&gt; can store a spell of 6th level or lower.&lt;/p&gt;&lt;p&gt;&lt;i&gt;Material Component:&lt;/i&gt; You trace the &lt;i&gt;glyph&lt;/i&gt; with incense, which must first be sprinkled with powdered diamond worth at least 400 gp.&lt;/p&gt;&lt;p/&gt;
&lt;/div&gt;', 'SRD 3.5 SpellsF-G')
GO

INSERT INTO [spell] ([id], [name], [altname], [school], [subschool], [descriptor], [spellcraft_dc], [level], [components], [casting_time], [range], [target], [area], [effect], [duration], [saving_throw], [spell_resistance], [short_description], [to_develop], [material_components], [arcane_material_components], [focus], [description], [xp_cost], [arcane_focus], [wizard_focus], [verbal_components], [sorcerer_focus], [bard_focus], [cleric_focus], [druid_focus], [full_text], [reference])
VALUES 
  (327, 'Goodberry', 'goodberry', 'Transmutation', NULL, NULL, NULL, 'Druid 1', 'V, S, DF', '1 standard action', 'Touch', '2d4 fresh berries touched', NULL, NULL, 'One day/level', 'None', 'Yes', '2d4 berries each cure 1 hp (max 8 hp/24 hours).', NULL, NULL, NULL, NULL, '&lt;p&gt;Casting &lt;i&gt;goodberry&lt;/i&gt; upon a handful of freshly picked berries makes 2d4 of them magical. You (as well as any other druid of 3rd or higher level) can immediately discern which berries are affected. Each transmuted berry provides nourishment as if it were a normal meal for a Medium creature. The berry also cures 1 point of damage when eaten, subject to a maximum of 8 points of such curing in any 24-hour period.&lt;/p&gt;
      &lt;p/&gt;', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '&lt;div topic=&quot;Goodberry&quot; level=&quot;5&quot;&gt;&lt;p&gt;&lt;h5&gt;Goodberry&lt;/h5&gt;&lt;/p&gt;&lt;table width=&quot;100%&quot; border=&quot;1&quot; cellpadding=&quot;2&quot; cellspacing=&quot;2&quot; frame=&quot;VOID&quot; rules=&quot;ROWS&quot;&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;/&gt;&lt;td&gt;Transmutation&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Level:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Druid 1&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Components:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;V, S, DF&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Casting Time:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;1 standard action&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Range:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Touch&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Targets:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;2d4 fresh berries touched&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Duration:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;One day/level&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Saving Throw:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;None&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Spell Resistance:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Yes&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;p&gt;Casting &lt;i&gt;goodberry&lt;/i&gt; upon a handful of freshly picked berries makes 2d4 of them magical. You (as well as any other druid of 3rd or higher level) can immediately discern which berries are affected. Each transmuted berry provides nourishment as if it were a normal meal for a Medium creature. The berry also cures 1 point of damage when eaten, subject to a maximum of 8 points of such curing in any 24-hour period.&lt;/p&gt;&lt;p/&gt;
&lt;/div&gt;', 'SRD 3.5 SpellsF-G')
GO

INSERT INTO [spell] ([id], [name], [altname], [school], [subschool], [descriptor], [spellcraft_dc], [level], [components], [casting_time], [range], [target], [area], [effect], [duration], [saving_throw], [spell_resistance], [short_description], [to_develop], [material_components], [arcane_material_components], [focus], [description], [xp_cost], [arcane_focus], [wizard_focus], [verbal_components], [sorcerer_focus], [bard_focus], [cleric_focus], [druid_focus], [full_text], [reference])
VALUES 
  (328, 'Good Hope', 'good hope', 'Enchantment', 'Compulsion', 'Mind-Affecting', NULL, 'Bard 3', 'V, S', '1 standard action', 'Medium (30 m (100 ft.) + 3 m (10 ft.)/level)', 'One living creature/level, no two of which may be more than 9 m (30 ft.) apart', NULL, NULL, '1 min./level', 'Will negates (harmless)', 'Yes (harmless)', 'Subjects gain +2 on attack rolls, damage rolls, saves, and checks.', NULL, NULL, NULL, NULL, '&lt;p&gt;This spell instills powerful hope in the subjects. Each affected creature gains a +2 morale bonus on saving throws, attack rolls, ability checks, skill checks, and weapon damage rolls.&lt;/p&gt;
      &lt;p&gt;&lt;i&gt;Good hope&lt;/i&gt; counters and dispels &lt;i&gt;crushing despair&lt;/i&gt;.&lt;/p&gt;
      &lt;p/&gt;', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '&lt;div topic=&quot;Good Hope&quot; level=&quot;5&quot;&gt;&lt;p&gt;&lt;h5&gt;Good Hope&lt;/h5&gt;&lt;/p&gt;&lt;table width=&quot;100%&quot; border=&quot;1&quot; cellpadding=&quot;2&quot; cellspacing=&quot;2&quot; frame=&quot;VOID&quot; rules=&quot;ROWS&quot;&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;/&gt;&lt;td&gt;Enchantment (Compulsion) [Mind-Affecting]&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Level:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Bard 3&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Components:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;V, S&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Casting Time:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;1 standard action&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Range:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Medium (30 m (100 ft.) + 3 m (10 ft.)/level)&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Targets:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;One living creature/level, no two of which may be more than 9 m (30 ft.) apart&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Duration:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;1 min./level&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Saving Throw:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Will negates (harmless)&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Spell Resistance:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Yes (harmless)&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;p&gt;This spell instills powerful hope in the subjects. Each affected creature gains a +2 morale bonus on saving throws, attack rolls, ability checks, skill checks, and weapon damage rolls.&lt;/p&gt;&lt;p&gt;&lt;i&gt;Good hope&lt;/i&gt; counters and dispels &lt;i&gt;crushing despair&lt;/i&gt;.&lt;/p&gt;&lt;p/&gt;
&lt;/div&gt;', 'SRD 3.5 SpellsF-G')
GO

INSERT INTO [spell] ([id], [name], [altname], [school], [subschool], [descriptor], [spellcraft_dc], [level], [components], [casting_time], [range], [target], [area], [effect], [duration], [saving_throw], [spell_resistance], [short_description], [to_develop], [material_components], [arcane_material_components], [focus], [description], [xp_cost], [arcane_focus], [wizard_focus], [verbal_components], [sorcerer_focus], [bard_focus], [cleric_focus], [druid_focus], [full_text], [reference])
VALUES 
  (329, 'Grasping Hand', 'grasping hand', 'Evocation', NULL, 'Force', NULL, 'Sorcerer/Wizard 7, Strength 7', 'V, S, F/DF', '1 standard action', 'Medium (30 m (100 ft.) + 3 m (10 ft.)/level)', NULL, NULL, '3 m (10-ft.) hand', '1 round/level (D)', 'None', 'Yes', 'Hand provides cover, pushes, or grapples.', NULL, NULL, NULL, 'A soft glove.', '&lt;p&gt;This spell functions like &lt;i&gt;interposing hand&lt;/i&gt;,  except the hand can also grapple one opponent that you select. The &lt;i&gt;grasping hand&lt;/i&gt; gets one grapple attack per round.&lt;/p&gt;
      &lt;p&gt;Its attack bonus to make contact equals your caster level + your Intelligence, Wisdom, or Charisma modifier (for wizards, clerics, and sorcerers, respectively), +10 for the hand''s Strength score (31), -1 for being Large. Its grapple bonus is this same figure, except with a +4 modifier for being Large instead of -1. The hand holds but does not harm creatures it grapples.&lt;/p&gt;
      &lt;p&gt;Directing the spell to a new target is a move action.&lt;/p&gt;
      &lt;p&gt;The &lt;i&gt;grasping hand&lt;/i&gt; can also bull rush an opponent as &lt;i&gt;forceful hand&lt;/i&gt; does, but at a +16 bonus on the Strength check (+10 for Strength 35, +4 for being Large, and a +2 bonus for charging, which it always gets), or interpose itself as &lt;i&gt;interposing hand&lt;/i&gt; does.&lt;/p&gt;
      &lt;p&gt;Clerics who cast this spell name it for their deities.&lt;/p&gt;
      &lt;p/&gt;', NULL, 'A leather glove.', NULL, NULL, NULL, NULL, NULL, NULL, '&lt;div topic=&quot;Grasping Hand&quot; level=&quot;5&quot;&gt;&lt;p&gt;&lt;h5&gt;Grasping Hand&lt;/h5&gt;&lt;/p&gt;&lt;table width=&quot;100%&quot; border=&quot;1&quot; cellpadding=&quot;2&quot; cellspacing=&quot;2&quot; frame=&quot;VOID&quot; rules=&quot;ROWS&quot;&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;/&gt;&lt;td&gt;Evocation [Force]&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Level:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Sorcerer/Wizard 7, Strength 7&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Components:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;V, S, F/DF&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;p&gt;This spell functions like &lt;i&gt;interposing hand&lt;/i&gt;,  except the hand can also grapple one opponent that you select. The &lt;i&gt;grasping hand&lt;/i&gt; gets one grapple attack per round.&lt;/p&gt;&lt;p&gt;Its attack bonus to make contact equals your caster level + your Intelligence, Wisdom, or Charisma modifier (for wizards, clerics, and sorcerers, respectively), +10 for the hand''s Strength score (31), -1 for being Large. Its grapple bonus is this same figure, except with a +4 modifier for being Large instead of -1. The hand holds but does not harm creatures it grapples.&lt;/p&gt;&lt;p&gt;Directing the spell to a new target is a move action.&lt;/p&gt;&lt;p&gt;The &lt;i&gt;grasping hand&lt;/i&gt; can also bull rush an opponent as &lt;i&gt;forceful hand&lt;/i&gt; does, but at a +16 bonus on the Strength check (+10 for Strength 35, +4 for being Large, and a +2 bonus for charging, which it always gets), or interpose itself as &lt;i&gt;interposing hand&lt;/i&gt; does.&lt;/p&gt;&lt;p&gt;Clerics who cast this spell name it for their deities.&lt;/p&gt;&lt;p&gt;&lt;i&gt;Arcane Focus:&lt;/i&gt; A leather glove.&lt;/p&gt;&lt;p/&gt;
&lt;/div&gt;', 'SRD 3.5 SpellsF-G')
GO

INSERT INTO [spell] ([id], [name], [altname], [school], [subschool], [descriptor], [spellcraft_dc], [level], [components], [casting_time], [range], [target], [area], [effect], [duration], [saving_throw], [spell_resistance], [short_description], [to_develop], [material_components], [arcane_material_components], [focus], [description], [xp_cost], [arcane_focus], [wizard_focus], [verbal_components], [sorcerer_focus], [bard_focus], [cleric_focus], [druid_focus], [full_text], [reference])
VALUES 
  (330, 'Grease', 'grease', 'Conjuration', 'Creation', NULL, NULL, 'Bard 1, Sorcerer/Wizard 1', 'V, S, M', '1 standard action', 'Close (7,5 m (25 ft.) + 1,5 m (5 ft.)/2 levels)', 'One object or a 3 m (10-ft.) square', 'One object or a 3 m (10-ft.) square', NULL, '1 round/level (D)', 'See Book', 'No', 'Makes 3 m (10-ft.) square or one object slippery.', NULL, 'A bit of pork rind or butter.', NULL, NULL, '&lt;p&gt;A &lt;i&gt;grease&lt;/i&gt; spell covers a solid surface with a layer of slippery grease. Any creature in the area when the spell is cast must make a successful Reflex save or fall. This save is repeated on your turn each round that the creature remains within the area. A creature can walk within or through the area of grease at half normal speed with a DC 10 Balance check. Failure means it can''t move that round (and must then make a Reflex save or fall), while failure by 5 or more means it falls (see the Balance skill for details).&lt;/p&gt;
      &lt;p&gt;The spell can also be used to create a greasy coating on an item. Material objects not in use are always affected by this spell, while an object wielded or employed by a creature receives a Reflex saving throw to avoid the effect. If the initial saving throw fails, the creature immediately drops the item. A saving throw must be made in each round that the creature attempts to pick up or use the &lt;i&gt;greased&lt;/i&gt; item. A creature wearing &lt;i&gt;greased&lt;/i&gt; armor or clothing gains a +10 circumstance bonus on Escape Artist checks and on grapple checks made to resist or escape a grapple or to escape a pin.&lt;/p&gt;
      &lt;p/&gt;', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '&lt;div topic=&quot;Grease&quot; level=&quot;5&quot;&gt;&lt;p&gt;&lt;h5&gt;Grease&lt;/h5&gt;&lt;/p&gt;&lt;table width=&quot;100%&quot; border=&quot;1&quot; cellpadding=&quot;2&quot; cellspacing=&quot;2&quot; frame=&quot;VOID&quot; rules=&quot;ROWS&quot;&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;/&gt;&lt;td&gt;Conjuration (Creation)&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Level:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Bard 1, Sorcerer/Wizard 1&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Components:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;V, S, M&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Casting Time:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;1 standard action&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Range:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Close (7,5 m (25 ft.) + 1,5 m (5 ft.)/2 levels)&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Target or Area:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;One object or a 3 m (10-ft.) square&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Duration:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;1 round/level (D)&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Saving Throw:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;See Book&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Spell Resistance:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;No&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;p&gt;A &lt;i&gt;grease&lt;/i&gt; spell covers a solid surface with a layer of slippery grease. Any creature in the area when the spell is cast must make a successful Reflex save or fall. This save is repeated on your turn each round that the creature remains within the area. A creature can walk within or through the area of grease at half normal speed with a DC 10 Balance check. Failure means it can''t move that round (and must then make a Reflex save or fall), while failure by 5 or more means it falls (see the Balance skill for details).&lt;/p&gt;&lt;p&gt;The spell can also be used to create a greasy coating on an item. Material objects not in use are always affected by this spell, while an object wielded or employed by a creature receives a Reflex saving throw to avoid the effect. If the initial saving throw fails, the creature immediately drops the item. A saving throw must be made in each round that the creature attempts to pick up or use the &lt;i&gt;greased&lt;/i&gt; item. A creature wearing &lt;i&gt;greased&lt;/i&gt; armor or clothing gains a +10 circumstance bonus on Escape Artist checks and on grapple checks made to resist or escape a grapple or to escape a pin.&lt;/p&gt;&lt;p&gt;&lt;i&gt;Material Component:&lt;/i&gt; A bit of pork rind or butter.&lt;/p&gt;&lt;p/&gt;
&lt;/div&gt;', 'SRD 3.5 SpellsF-G')
GO

INSERT INTO [spell] ([id], [name], [altname], [school], [subschool], [descriptor], [spellcraft_dc], [level], [components], [casting_time], [range], [target], [area], [effect], [duration], [saving_throw], [spell_resistance], [short_description], [to_develop], [material_components], [arcane_material_components], [focus], [description], [xp_cost], [arcane_focus], [wizard_focus], [verbal_components], [sorcerer_focus], [bard_focus], [cleric_focus], [druid_focus], [full_text], [reference])
VALUES 
  (331, 'Guards and Wards', 'guards and wards', 'Abjuration', NULL, NULL, NULL, 'Sorcerer/Wizard 6', 'V, S, M, F', '30 minutes', 'Anywhere within the area to be warded', NULL, 'Up to 200 sq. ft./level (S)', NULL, '2 hours/level (D)', 'See Book', 'See Book', 'Array of magic effects protect area.', NULL, 'Burning incense, a small measure of brimstone and oil, a knotted string, and a small amount of blood.', NULL, 'A small silver rod.', '&lt;p&gt;This powerful spell is primarily used to defend your stronghold. The ward protects 200 square feet per caster level. The warded area can be as much as 20 feet high, and shaped as you desire. You can ward several stories of a stronghold by dividing the area among them; you must be somewhere within the area to be warded to cast the spell. The spell creates the following magical effects within the warded area.&lt;/p&gt;
      &lt;p&gt;&lt;i&gt;Fog:&lt;/i&gt; Fog fills all corridors, obscuring all sight, including darkvision, beyond 5 feet. A creature within 5 feet has concealment (attacks have a 20% miss chance). Creatures farther away have total concealment (50% miss chance, and the attacker cannot use sight to locate the target). Saving Throw: None. Spell Resistance: No.&lt;/p&gt;
      &lt;p&gt;&lt;i&gt;Arcane Locks:&lt;/i&gt; All doors in the warded area are &lt;i&gt;arcane locked&lt;/i&gt;. Saving Throw: None. Spell Resistance: No.&lt;/p&gt;
      &lt;p&gt;&lt;i&gt;Webs:&lt;/i&gt; Webs fill all stairs from top to bottom. These strands are identical with those created by the &lt;i&gt;web&lt;/i&gt; spell, except that they regrow in 10 minutes if they are burned or torn away while the &lt;i&gt;guards and wards&lt;/i&gt; spell lasts. Saving Throw: Reflex negates; See Book for &lt;i&gt;web&lt;/i&gt;. Spell Resistance: No.&lt;/p&gt;
      &lt;p&gt;&lt;i&gt;Confusion:&lt;/i&gt; Where there are choices in direction-such as a corridor intersection or side passage-a minor &lt;i&gt;confusion-&lt;/i&gt;type effect functions so as to make it 50% probable that intruders believe they are going in the opposite direction from the one they actually chose. This is an enchantment, mind-affecting effect. Saving Throw: None. Spell Resistance: Yes.&lt;/p&gt;
      &lt;p&gt;&lt;i&gt;Lost Doors:&lt;/i&gt; One door per caster level is covered by a &lt;i&gt;silent image&lt;/i&gt; to appear as if it were a plain wall. Saving Throw: Will disbelief (if interacted with). Spell Resistance: No.&lt;/p&gt;
      &lt;p&gt;In addition, you can place your choice of one of the following five magical effects.&lt;/p&gt;
      &lt;p&gt;1. &lt;i&gt;Dancing lights&lt;/i&gt; in four corridors. You can designate a simple program that causes the lights to repeat as long as the &lt;i&gt;guards and wards&lt;/i&gt; spell lasts. Saving Throw: None. Spell Resistance: No.&lt;/p&gt;
      &lt;p&gt;2. A &lt;i&gt;magic mouth&lt;/i&gt; in two places. Saving Throw: None. Spell Resistance: No.&lt;/p&gt;
      &lt;p&gt;3. A &lt;i&gt;stinking cloud&lt;/i&gt; in two places. The vapors appear in the places you designate; they return within 10 minutes if dispersed by wind while the &lt;i&gt;guards and wards&lt;/i&gt; spell lasts. Saving Throw: Fortitude negates; See Book for &lt;i&gt;stinking cloud&lt;/i&gt;. Spell Resistance: No.&lt;/p&gt;
      &lt;p&gt;4. A &lt;i&gt;gust of wind&lt;/i&gt; in one corridor or room. Saving Throw: Fortitude negates. Spell Resistance: Yes.&lt;/p&gt;
      &lt;p&gt;5. A &lt;i&gt;suggestion&lt;/i&gt; in one place. You select an area of up to 5 feet square, and any creature who enters or passes through the area receives the &lt;i&gt;suggestion&lt;/i&gt; mentally. Saving Throw: Will negates. Spell Resistance: Yes.&lt;/p&gt;
      &lt;p&gt;The whole warded area radiates strong magic of the abjuration school. A &lt;i&gt;dispel magic&lt;/i&gt; cast on a specific effect, if successful, removes only that effect. A successful &lt;i&gt;Mage''s disjunction&lt;/i&gt; destroys the entire &lt;i&gt;guards and wards&lt;/i&gt; effect.&lt;/p&gt;
      &lt;p/&gt;', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '&lt;div topic=&quot;Guards and Wards&quot; level=&quot;5&quot;&gt;&lt;p&gt;&lt;h5&gt;Guards and Wards&lt;/h5&gt;&lt;/p&gt;&lt;table width=&quot;100%&quot; border=&quot;1&quot; cellpadding=&quot;2&quot; cellspacing=&quot;2&quot; frame=&quot;VOID&quot; rules=&quot;ROWS&quot;&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;/&gt;&lt;td&gt;Abjuration&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Level:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Sorcerer/Wizard 6&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Components:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;V, S, M, F&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Casting Time:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;30 minutes&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Range:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Anywhere within the area to be warded&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Area:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Up to 200 sq. ft./level (S)&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Duration:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;2 hours/level (D)&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Saving Throw:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;See Book&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Spell Resistance:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;See Book&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;p&gt;This powerful spell is primarily used to defend your stronghold. The ward protects 200 square feet per caster level. The warded area can be as much as 20 feet high, and shaped as you desire. You can ward several stories of a stronghold by dividing the area among them; you must be somewhere within the area to be warded to cast the spell. The spell creates the following magical effects within the warded area.&lt;/p&gt;&lt;p&gt;&lt;i&gt;Fog:&lt;/i&gt; Fog fills all corridors, obscuring all sight, including darkvision, beyond 5 feet. A creature within 5 feet has concealment (attacks have a 20% miss chance). Creatures farther away have total concealment (50% miss chance, and the attacker cannot use sight to locate the target). Saving Throw: None. Spell Resistance: No.&lt;/p&gt;&lt;p&gt;&lt;i&gt;Arcane Locks:&lt;/i&gt; All doors in the warded area are &lt;i&gt;arcane locked&lt;/i&gt;. Saving Throw: None. Spell Resistance: No.&lt;/p&gt;&lt;p&gt;&lt;i&gt;Webs:&lt;/i&gt; Webs fill all stairs from top to bottom. These strands are identical with those created by the &lt;i&gt;web&lt;/i&gt; spell, except that they regrow in 10 minutes if they are burned or torn away while the &lt;i&gt;guards and wards&lt;/i&gt; spell lasts. Saving Throw: Reflex negates; See Book for &lt;i&gt;web&lt;/i&gt;. Spell Resistance: No.&lt;/p&gt;&lt;p&gt;&lt;i&gt;Confusion:&lt;/i&gt; Where there are choices in direction-such as a corridor intersection or side passage-a minor &lt;i&gt;confusion-&lt;/i&gt;type effect functions so as to make it 50% probable that intruders believe they are going in the opposite direction from the one they actually chose. This is an enchantment, mind-affecting effect. Saving Throw: None. Spell Resistance: Yes.&lt;/p&gt;&lt;p&gt;&lt;i&gt;Lost Doors:&lt;/i&gt; One door per caster level is covered by a &lt;i&gt;silent image&lt;/i&gt; to appear as if it were a plain wall. Saving Throw: Will disbelief (if interacted with). Spell Resistance: No.&lt;/p&gt;&lt;p&gt;In addition, you can place your choice of one of the following five magical effects.&lt;/p&gt;&lt;p&gt;1. &lt;i&gt;Dancing lights&lt;/i&gt; in four corridors. You can designate a simple program that causes the lights to repeat as long as the &lt;i&gt;guards and wards&lt;/i&gt; spell lasts. Saving Throw: None. Spell Resistance: No.&lt;/p&gt;&lt;p&gt;2. A &lt;i&gt;magic mouth&lt;/i&gt; in two places. Saving Throw: None. Spell Resistance: No.&lt;/p&gt;&lt;p&gt;3. A &lt;i&gt;stinking cloud&lt;/i&gt; in two places. The vapors appear in the places you designate; they return within 10 minutes if dispersed by wind while the &lt;i&gt;guards and wards&lt;/i&gt; spell lasts. Saving Throw: Fortitude negates; See Book for &lt;i&gt;stinking cloud&lt;/i&gt;. Spell Resistance: No.&lt;/p&gt;&lt;p&gt;4. A &lt;i&gt;gust of wind&lt;/i&gt; in one corridor or room. Saving Throw: Fortitude negates. Spell Resistance: Yes.&lt;/p&gt;&lt;p&gt;5. A &lt;i&gt;suggestion&lt;/i&gt; in one place. You select an area of up to 5 feet square, and any creature who enters or passes through the area receives the &lt;i&gt;suggestion&lt;/i&gt; mentally. Saving Throw: Will negates. Spell Resistance: Yes.&lt;/p&gt;&lt;p&gt;The whole warded area radiates strong magic of the abjuration school. A &lt;i&gt;dispel magic&lt;/i&gt; cast on a specific effect, if successful, removes only that effect. A successful &lt;i&gt;Mage''s disjunction&lt;/i&gt; destroys the entire &lt;i&gt;guards and wards&lt;/i&gt; effect.&lt;/p&gt;&lt;p&gt;&lt;i&gt;Material Component:&lt;/i&gt; Burning incense, a small measure of brimstone and oil, a knotted string, and a small amount of blood.&lt;/p&gt;&lt;p&gt;&lt;i&gt;Focus:&lt;/i&gt; A small silver rod.&lt;/p&gt;&lt;p/&gt;
&lt;/div&gt;', 'SRD 3.5 SpellsF-G')
GO

INSERT INTO [spell] ([id], [name], [altname], [school], [subschool], [descriptor], [spellcraft_dc], [level], [components], [casting_time], [range], [target], [area], [effect], [duration], [saving_throw], [spell_resistance], [short_description], [to_develop], [material_components], [arcane_material_components], [focus], [description], [xp_cost], [arcane_focus], [wizard_focus], [verbal_components], [sorcerer_focus], [bard_focus], [cleric_focus], [druid_focus], [full_text], [reference])
VALUES 
  (332, 'Guidance', 'guidance', 'Divination', NULL, NULL, NULL, 'Cleric 0, Druid 0', 'V, S', '1 standard action', 'Touch', 'Creature touched', NULL, NULL, '1 minute or until discharged', 'Will negates (harmless)', 'Yes', '+1 on one attack roll, saving throw, or skill check.', NULL, NULL, NULL, NULL, '&lt;p&gt;This spell imbues the subject with a touch of divine guidance. The creature gets a +1 competence bonus on a single attack roll, saving throw, or skill check. It must choose to use the bonus before making the roll to which it applies.&lt;/p&gt;
      &lt;p/&gt;', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '&lt;div topic=&quot;Guidance&quot; level=&quot;5&quot;&gt;&lt;p&gt;&lt;h5&gt;Guidance&lt;/h5&gt;&lt;/p&gt;&lt;table width=&quot;100%&quot; border=&quot;1&quot; cellpadding=&quot;2&quot; cellspacing=&quot;2&quot; frame=&quot;VOID&quot; rules=&quot;ROWS&quot;&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;/&gt;&lt;td&gt;Divination&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Level:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Cleric 0, Druid 0&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Components:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;V, S&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Casting Time:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;1 standard action&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Range:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Touch&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Target:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Creature touched&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Duration:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;1 minute or until discharged&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Saving Throw:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Will negates (harmless)&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Spell Resistance:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Yes&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;p&gt;This spell imbues the subject with a touch of divine guidance. The creature gets a +1 competence bonus on a single attack roll, saving throw, or skill check. It must choose to use the bonus before making the roll to which it applies.&lt;/p&gt;&lt;p/&gt;
&lt;/div&gt;', 'SRD 3.5 SpellsF-G')
GO

INSERT INTO [spell] ([id], [name], [altname], [school], [subschool], [descriptor], [spellcraft_dc], [level], [components], [casting_time], [range], [target], [area], [effect], [duration], [saving_throw], [spell_resistance], [short_description], [to_develop], [material_components], [arcane_material_components], [focus], [description], [xp_cost], [arcane_focus], [wizard_focus], [verbal_components], [sorcerer_focus], [bard_focus], [cleric_focus], [druid_focus], [full_text], [reference])
VALUES 
  (333, 'Gust of Wind', 'gust of wind', 'Evocation', NULL, 'Air', NULL, 'Druid 2, Sorcerer/Wizard 2', 'V, S', '1 standard action', '60 ft.', NULL, NULL, 'Line-shaped gust of severe wind emanating out from you to the extreme of the range', '1 round', 'Fortitude negates', 'Yes', 'Blows away or knocks down smaller creatures.', NULL, NULL, NULL, NULL, '&lt;p&gt;This spell creates a severe blast of air (approximately 50 mph) that originates from you, affecting all creatures in its path.&lt;/p&gt;
      &lt;p&gt;A Tiny or smaller creature on the ground is knocked down and rolled 1d4x10 feet, taking 1d4 points of nonlethal damage per 10 feet. If flying, a Tiny or smaller creature is blown back 2d6x10 feet and takes 2d6 points of nonlethal damage due to battering and buffeting.&lt;/p&gt;
      &lt;p&gt;Small creatures are knocked prone by the force of the wind, or if flying are blown back 1d6x10 feet.&lt;/p&gt;
      &lt;p&gt;Medium creatures are unable to move forward against the force of the wind, or if flying are blown back 1d6x5 feet.&lt;/p&gt;
      &lt;p&gt;Large or larger creatures may move normally within a &lt;i&gt;gust of wind&lt;/i&gt; effect.&lt;/p&gt;
      &lt;p&gt;A &lt;i&gt;gust of wind&lt;/i&gt; can''t move a creature beyond the limit of its range.&lt;/p&gt;
      &lt;p&gt;Any creature, regardless of size, takes a -4 penalty on ranged attacks and Listen checks in the area of a &lt;i&gt;gust of wind&lt;/i&gt;.&lt;/p&gt;
      &lt;p&gt;The force of the &lt;i&gt;gust&lt;/i&gt; automatically extinguishes candles, torches, and similar unprotected flames. It causes protected flames, such as those of lanterns, to dance wildly and has a 50% chance to extinguish those lights.&lt;/p&gt;
      &lt;p&gt;In addition to the effects noted, a &lt;i&gt;gust of wind&lt;/i&gt; can do anything that a sudden blast of wind would be expected to do. It can create a stinging spray of sand or dust, fan a large fire, overturn delicate awnings or hangings, heel over a small boat, and blow gases or vapors to the edge of its range.&lt;/p&gt;
      &lt;p&gt;&lt;i&gt;gust of wind&lt;/i&gt; can be made permanent with a &lt;i&gt;permanency&lt;/i&gt; spell.&lt;/p&gt;
      &lt;p/&gt;', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '&lt;div topic=&quot;Gust of Wind&quot; level=&quot;5&quot;&gt;&lt;p&gt;&lt;h5&gt;Gust of Wind&lt;/h5&gt;&lt;/p&gt;&lt;table width=&quot;100%&quot; border=&quot;1&quot; cellpadding=&quot;2&quot; cellspacing=&quot;2&quot; frame=&quot;VOID&quot; rules=&quot;ROWS&quot;&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;/&gt;&lt;td&gt;Evocation [Air]&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Level:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Druid 2, Sorcerer/Wizard 2&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Components:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;V, S&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Casting Time:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;1 standard action&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Range:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;60 ft.&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Effect:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Line-shaped gust of severe wind emanating out from you to the extreme of the range&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Duration:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;1 round&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Saving Throw:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Fortitude negates&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Spell Resistance:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Yes&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;p&gt;This spell creates a severe blast of air (approximately 50 mph) that originates from you, affecting all creatures in its path.&lt;/p&gt;&lt;p&gt;A Tiny or smaller creature on the ground is knocked down and rolled 1d4x10 feet, taking 1d4 points of nonlethal damage per 10 feet. If flying, a Tiny or smaller creature is blown back 2d6x10 feet and takes 2d6 points of nonlethal damage due to battering and buffeting.&lt;/p&gt;&lt;p&gt;Small creatures are knocked prone by the force of the wind, or if flying are blown back 1d6x10 feet.&lt;/p&gt;&lt;p&gt;Medium creatures are unable to move forward against the force of the wind, or if flying are blown back 1d6x5 feet.&lt;/p&gt;&lt;p&gt;Large or larger creatures may move normally within a &lt;i&gt;gust of wind&lt;/i&gt; effect.&lt;/p&gt;&lt;p&gt;A &lt;i&gt;gust of wind&lt;/i&gt; can''t move a creature beyond the limit of its range.&lt;/p&gt;&lt;p&gt;Any creature, regardless of size, takes a -4 penalty on ranged attacks and Listen checks in the area of a &lt;i&gt;gust of wind&lt;/i&gt;.&lt;/p&gt;&lt;p&gt;The force of the &lt;i&gt;gust&lt;/i&gt; automatically extinguishes candles, torches, and similar unprotected flames. It causes protected flames, such as those of lanterns, to dance wildly and has a 50% chance to extinguish those lights.&lt;/p&gt;&lt;p&gt;In addition to the effects noted, a &lt;i&gt;gust of wind&lt;/i&gt; can do anything that a sudden blast of wind would be expected to do. It can create a stinging spray of sand or dust, fan a large fire, overturn delicate awnings or hangings, heel over a small boat, and blow gases or vapors to the edge of its range.&lt;/p&gt;&lt;p&gt;&lt;i&gt;gust of wind&lt;/i&gt; can be made permanent with a &lt;i&gt;permanency&lt;/i&gt; spell.&lt;/p&gt;&lt;p/&gt;
&lt;/div&gt;', 'SRD 3.5 SpellsF-G')
GO

INSERT INTO [spell] ([id], [name], [altname], [school], [subschool], [descriptor], [spellcraft_dc], [level], [components], [casting_time], [range], [target], [area], [effect], [duration], [saving_throw], [spell_resistance], [short_description], [to_develop], [material_components], [arcane_material_components], [focus], [description], [xp_cost], [arcane_focus], [wizard_focus], [verbal_components], [sorcerer_focus], [bard_focus], [cleric_focus], [druid_focus], [full_text], [reference])
VALUES 
  (334, 'Hallow', 'hallow', 'Evocation', NULL, 'Good', NULL, 'Cleric 5, Druid 5', 'V, S, M, DF', '24 hours', 'Touch', NULL, '12 m (40-ft.) radius emanating from the touched point', NULL, 'Instantaneous', 'See Book', 'See Book', 'Designates location as holy.', NULL, 'Herbs, oils, and incense worth at least 1,000 gp, plus 1,000 gp per level of the spell to be included in the hallowed area.', NULL, NULL, '&lt;p&gt;&lt;i&gt;Hallow&lt;/i&gt; makes a particular site, building, or structure a holy site. This has four major effects.&lt;/p&gt;
      &lt;p&gt;First, the site or structure is guarded by a &lt;i&gt;magic circle against evil&lt;/i&gt; effect.&lt;/p&gt;
      &lt;p&gt;Second, all Charisma checks made to turn undead gain a +4 sacred bonus, and Charisma checks to command undead take a -4 penalty. Spell resistance does not apply to this effect. (This provision does not apply to the druid version of the spell.)&lt;/p&gt;
      &lt;p&gt;Third, any dead body interred in a &lt;i&gt;hallowed&lt;/i&gt; site cannot be turned into an undead creature.&lt;/p&gt;
      &lt;p&gt;Finally, you may choose to fix a single spell effect to the &lt;i&gt;hallowed&lt;/i&gt; site. The spell effect lasts for one year and functions throughout the entire site, regardless of the normal duration and area or effect. You may designate whether the effect applies to all creatures, creatures who share your faith or alignment, or creatures who adhere to another faith or alignment. At the end of the year, the chosen effect lapses, but it can be renewed or replaced simply by casting &lt;i&gt;Hallow&lt;/i&gt; again.&lt;/p&gt;
      &lt;p&gt;Spell effects that may be tied to a &lt;i&gt;hallowed&lt;/i&gt; site include &lt;i&gt;aid&lt;/i&gt;,  &lt;i&gt;bane&lt;/i&gt;,  &lt;i&gt;bless&lt;/i&gt;,  &lt;i&gt;cause fear&lt;/i&gt;,  &lt;i&gt;darkness&lt;/i&gt;,  &lt;i&gt;daylight&lt;/i&gt;,  &lt;i&gt;death ward&lt;/i&gt;,  &lt;i&gt;deeper darkness&lt;/i&gt;,  &lt;i&gt;detect evil&lt;/i&gt;,  &lt;i&gt;detect magic&lt;/i&gt;,  &lt;i&gt;dimensional anchor&lt;/i&gt;,  &lt;i&gt;discern lies&lt;/i&gt;,  &lt;i&gt;dispel magic&lt;/i&gt;,  &lt;i&gt;endure elements&lt;/i&gt;,  &lt;i&gt;freedom of movement&lt;/i&gt;,  &lt;i&gt;invisibility purge&lt;/i&gt;,  &lt;i&gt;protection from energy&lt;/i&gt;,  &lt;i&gt;remove fear&lt;/i&gt;,  &lt;i&gt;resist energy&lt;/i&gt;,  &lt;i&gt;silence&lt;/i&gt;,  &lt;i&gt;tongues&lt;/i&gt;,  and &lt;i&gt;zone of truth&lt;/i&gt;. Saving throws and spell resistance might apply to these spells'' effects. (See the individual spell descriptions for details.)&lt;/p&gt;
      &lt;p&gt;An area can receive only one &lt;i&gt;Hallow&lt;/i&gt; spell (and its associated spell effect) at a time. &lt;i&gt;Hallow&lt;/i&gt; counters but does not dispel &lt;i&gt;unhallow&lt;/i&gt;.&lt;/p&gt;
      &lt;p/&gt;', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '&lt;div topic=&quot;Hallow&quot; level=&quot;5&quot;&gt;&lt;p&gt;&lt;h5&gt;Hallow&lt;/h5&gt;&lt;/p&gt;&lt;table width=&quot;100%&quot; border=&quot;1&quot; cellpadding=&quot;2&quot; cellspacing=&quot;2&quot; frame=&quot;VOID&quot; rules=&quot;ROWS&quot;&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;/&gt;&lt;td&gt;Evocation [Good]&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Level:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Cleric 5, Druid 5&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Components:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;V, S, M, DF&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Casting Time:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;24 hours&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Range:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Touch&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Area:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;12 m (40-ft.) radius emanating from the touched point&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Duration:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Instantaneous&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Saving Throw:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;See Book&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Spell Resistance:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;See Book&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;p&gt;&lt;i&gt;Hallow&lt;/i&gt; makes a particular site, building, or structure a holy site. This has four major effects.&lt;/p&gt;&lt;p&gt;First, the site or structure is guarded by a &lt;i&gt;magic circle against evil&lt;/i&gt; effect.&lt;/p&gt;&lt;p&gt;Second, all Charisma checks made to turn undead gain a +4 sacred bonus, and Charisma checks to command undead take a -4 penalty. Spell resistance does not apply to this effect. (This provision does not apply to the druid version of the spell.)&lt;/p&gt;&lt;p&gt;Third, any dead body interred in a &lt;i&gt;hallowed&lt;/i&gt; site cannot be turned into an undead creature.&lt;/p&gt;&lt;p&gt;Finally, you may choose to fix a single spell effect to the &lt;i&gt;hallowed&lt;/i&gt; site. The spell effect lasts for one year and functions throughout the entire site, regardless of the normal duration and area or effect. You may designate whether the effect applies to all creatures, creatures who share your faith or alignment, or creatures who adhere to another faith or alignment. At the end of the year, the chosen effect lapses, but it can be renewed or replaced simply by casting &lt;i&gt;Hallow&lt;/i&gt; again.&lt;/p&gt;&lt;p&gt;Spell effects that may be tied to a &lt;i&gt;hallowed&lt;/i&gt; site include &lt;i&gt;aid&lt;/i&gt;,  &lt;i&gt;bane&lt;/i&gt;,  &lt;i&gt;bless&lt;/i&gt;,  &lt;i&gt;cause fear&lt;/i&gt;,  &lt;i&gt;darkness&lt;/i&gt;,  &lt;i&gt;daylight&lt;/i&gt;,  &lt;i&gt;death ward&lt;/i&gt;,  &lt;i&gt;deeper darkness&lt;/i&gt;,  &lt;i&gt;detect evil&lt;/i&gt;,  &lt;i&gt;detect magic&lt;/i&gt;,  &lt;i&gt;dimensional anchor&lt;/i&gt;,  &lt;i&gt;discern lies&lt;/i&gt;,  &lt;i&gt;dispel magic&lt;/i&gt;,  &lt;i&gt;endure elements&lt;/i&gt;,  &lt;i&gt;freedom of movement&lt;/i&gt;,  &lt;i&gt;invisibility purge&lt;/i&gt;,  &lt;i&gt;protection from energy&lt;/i&gt;,  &lt;i&gt;remove fear&lt;/i&gt;,  &lt;i&gt;resist energy&lt;/i&gt;,  &lt;i&gt;silence&lt;/i&gt;,  &lt;i&gt;tongues&lt;/i&gt;,  and &lt;i&gt;zone of truth&lt;/i&gt;. Saving throws and spell resistance might apply to these spells'' effects. (See the individual spell descriptions for details.)&lt;/p&gt;&lt;p&gt;An area can receive only one &lt;i&gt;Hallow&lt;/i&gt; spell (and its associated spell effect) at a time. &lt;i&gt;Hallow&lt;/i&gt; counters but does not dispel &lt;i&gt;unhallow&lt;/i&gt;.&lt;/p&gt;&lt;p&gt;&lt;i&gt;Material Component:&lt;/i&gt; Herbs, oils, and incense worth at least 1,000 gp, plus 1,000 gp per level of the spell to be included in the &lt;i&gt;hallowed&lt;/i&gt; area.&lt;/p&gt;&lt;p/&gt;
&lt;/div&gt;', 'SRD 3.5 SpellsH-L')
GO

INSERT INTO [spell] ([id], [name], [altname], [school], [subschool], [descriptor], [spellcraft_dc], [level], [components], [casting_time], [range], [target], [area], [effect], [duration], [saving_throw], [spell_resistance], [short_description], [to_develop], [material_components], [arcane_material_components], [focus], [description], [xp_cost], [arcane_focus], [wizard_focus], [verbal_components], [sorcerer_focus], [bard_focus], [cleric_focus], [druid_focus], [full_text], [reference])
VALUES 
  (335, 'Hallucinatory Terrain', 'hallucinatory terrain', 'Illusion', 'Glamer', NULL, NULL, 'Bard 4, Sorcerer/Wizard 4', 'V, S, M', '10 minutes', 'Long (400 ft. + 12 m (40 ft.)/level)', NULL, 'One 9 m (30-ft.) cube/level (S)', NULL, '2 hours/level (D)', 'Will disbelief (if interacted with)', 'No', 'Makes one type of terrain appear like another (field into forest, or the like).', NULL, 'A stone, a twig, and a bit of green plant.', NULL, NULL, '&lt;p&gt;You make natural terrain look, sound, and smell like some other sort of natural terrain. Structures, equipment, and creatures within the area are not hidden or changed in appearance.&lt;/p&gt;
      &lt;p/&gt;', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '&lt;div topic=&quot;Hallucinatory Terrain&quot; level=&quot;5&quot;&gt;&lt;p&gt;&lt;h5&gt;Hallucinatory Terrain&lt;/h5&gt;&lt;/p&gt;&lt;table width=&quot;100%&quot; border=&quot;1&quot; cellpadding=&quot;2&quot; cellspacing=&quot;2&quot; frame=&quot;VOID&quot; rules=&quot;ROWS&quot;&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;/&gt;&lt;td&gt;Illusion (Glamer)&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Level:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Bard 4, Sorcerer/Wizard 4&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Components:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;V, S, M&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Casting Time:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;10 minutes&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Range:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Long (400 ft. + 12 m (40 ft.)/level)&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Area:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;One 9 m (30-ft.) cube/level (S)&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Duration:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;2 hours/level (D)&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Saving Throw:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Will disbelief (if interacted with)&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Spell Resistance:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;No&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;p&gt;You make natural terrain look, sound, and smell like some other sort of natural terrain. Structures, equipment, and creatures within the area are not hidden or changed in appearance.&lt;/p&gt;&lt;p&gt;&lt;i&gt;Material Component:&lt;/i&gt; A stone, a twig, and a bit of green plant.&lt;/p&gt;&lt;p/&gt;
&lt;/div&gt;', 'SRD 3.5 SpellsH-L')
GO

INSERT INTO [spell] ([id], [name], [altname], [school], [subschool], [descriptor], [spellcraft_dc], [level], [components], [casting_time], [range], [target], [area], [effect], [duration], [saving_throw], [spell_resistance], [short_description], [to_develop], [material_components], [arcane_material_components], [focus], [description], [xp_cost], [arcane_focus], [wizard_focus], [verbal_components], [sorcerer_focus], [bard_focus], [cleric_focus], [druid_focus], [full_text], [reference])
VALUES 
  (336, 'Halt Undead', 'halt undead', 'Necromancy', NULL, NULL, NULL, 'Sorcerer/Wizard 3', 'V, S, M', '1 standard action', 'Medium (30 m (100 ft.) + 3 m (10 ft.)/level)', 'Up to three undead creatures, no two of which can be more than 9 m (30 ft.) apart', NULL, NULL, '1 round/level', 'Will negates (See Book)', 'Yes', 'Immobilizes undead for 1 round/level.', NULL, 'A pinch of sulfur and powdered garlic.', NULL, NULL, '&lt;p&gt;This spell renders as many as three undead creatures immobile. A nonintelligent undead creature gets no saving throw; an intelligent undead creature does. If the spell is successful, it renders the undead creature immobile for the duration of the spell (similar to the effect of &lt;i&gt;hold person&lt;/i&gt; on a living creature). The effect is broken if the &lt;i&gt;halted&lt;/i&gt; creatures are attacked or take damage.&lt;/p&gt;
      &lt;p/&gt;', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '&lt;div topic=&quot;Halt Undead&quot; level=&quot;5&quot;&gt;&lt;p&gt;&lt;h5&gt;Halt Undead&lt;/h5&gt;&lt;/p&gt;&lt;table width=&quot;100%&quot; border=&quot;1&quot; cellpadding=&quot;2&quot; cellspacing=&quot;2&quot; frame=&quot;VOID&quot; rules=&quot;ROWS&quot;&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;/&gt;&lt;td&gt;Necromancy&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Level:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Sorcerer/Wizard 3&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Components:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;V, S, M&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Casting Time:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;1 standard action&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Range:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Medium (30 m (100 ft.) + 3 m (10 ft.)/level)&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Targets:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Up to three undead creatures, no two of which can be more than 9 m (30 ft.) apart&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Duration:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;1 round/level&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Saving Throw:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Will negates (See Book)&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Spell Resistance:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Yes&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;p&gt;This spell renders as many as three undead creatures immobile. A nonintelligent undead creature gets no saving throw; an intelligent undead creature does. If the spell is successful, it renders the undead creature immobile for the duration of the spell (similar to the effect of &lt;i&gt;hold person&lt;/i&gt; on a living creature). The effect is broken if the &lt;i&gt;halted&lt;/i&gt; creatures are attacked or take damage.&lt;/p&gt;&lt;p&gt;&lt;i&gt;Material Component:&lt;/i&gt; A pinch of sulfur and powdered garlic.&lt;/p&gt;&lt;p/&gt;
&lt;/div&gt;', 'SRD 3.5 SpellsH-L')
GO

INSERT INTO [spell] ([id], [name], [altname], [school], [subschool], [descriptor], [spellcraft_dc], [level], [components], [casting_time], [range], [target], [area], [effect], [duration], [saving_throw], [spell_resistance], [short_description], [to_develop], [material_components], [arcane_material_components], [focus], [description], [xp_cost], [arcane_focus], [wizard_focus], [verbal_components], [sorcerer_focus], [bard_focus], [cleric_focus], [druid_focus], [full_text], [reference])
VALUES 
  (337, 'Harm', 'harm', 'Necromancy', NULL, NULL, NULL, 'Cleric 6, Destruction 6', 'V, S', '1 standard action', 'Touch', 'Creature touched', NULL, NULL, 'Instantaneous', 'Will half; See Book', 'Yes', 'Deals 10 points/level damage to target.', NULL, NULL, NULL, NULL, '&lt;p&gt;&lt;i&gt;Harm&lt;/i&gt; charges a subject with negative energy that deals 10 points of damage per caster level (to a maximum of 150 points at 15th level). If the creature successfully saves, &lt;i&gt;Harm&lt;/i&gt; deals half this amount, but it cannot reduce the target''s hit points to less than 1.&lt;/p&gt;
      &lt;p&gt;If used on an undead creature, &lt;i&gt;Harm&lt;/i&gt; acts like &lt;i&gt;heal&lt;/i&gt;.&lt;/p&gt;
      &lt;p/&gt;', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '&lt;div topic=&quot;Harm&quot; level=&quot;5&quot;&gt;&lt;p&gt;&lt;h5&gt;Harm&lt;/h5&gt;&lt;/p&gt;&lt;table width=&quot;100%&quot; border=&quot;1&quot; cellpadding=&quot;2&quot; cellspacing=&quot;2&quot; frame=&quot;VOID&quot; rules=&quot;ROWS&quot;&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;/&gt;&lt;td&gt;Necromancy&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Level:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Cleric 6, Destruction 6&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Components:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;V, S&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Casting Time:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;1 standard action&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Range:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Touch&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Target:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Creature touched&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Duration:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Instantaneous&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Saving Throw:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Will half; See Book&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Spell Resistance:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Yes&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;p&gt;&lt;i&gt;Harm&lt;/i&gt; charges a subject with negative energy that deals 10 points of damage per caster level (to a maximum of 150 points at 15th level). If the creature successfully saves, &lt;i&gt;Harm&lt;/i&gt; deals half this amount, but it cannot reduce the target''s hit points to less than 1.&lt;/p&gt;&lt;p&gt;If used on an undead creature, &lt;i&gt;Harm&lt;/i&gt; acts like &lt;i&gt;heal&lt;/i&gt;.&lt;/p&gt;&lt;p/&gt;
&lt;/div&gt;', 'SRD 3.5 SpellsH-L')
