VALUES 
  (346, 'Heroism, Greater', 'greater heroism', 'Enchantment', 'Compulsion', 'Mind-Affecting', NULL, 'Bard 5, Sorcerer/Wizard 6', 'V, S', '1 standard action', 'Touch', 'Creature touched', NULL, NULL, '1 min./level', 'Will negates (harmless)', 'Yes (harmless)', 'Gives +4 bonus on attack rolls, saves, skill checks; immunity to fear; temporary hp.', NULL, NULL, NULL, NULL, '&lt;p&gt;This spell functions like &lt;i&gt;heroism&lt;/i&gt;,  except the creature gains a +4 morale bonus on attack rolls, saves, and skill checks, immunity to fear effects, and temporary hit points equal to your caster level (maximum 20).&lt;/p&gt;
      &lt;p/&gt;', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '&lt;div topic=&quot;Heroism, Greater&quot; level=&quot;5&quot;&gt;&lt;p&gt;&lt;h5&gt;Heroism, Greater&lt;/h5&gt;&lt;/p&gt;&lt;table width=&quot;100%&quot; border=&quot;1&quot; cellpadding=&quot;2&quot; cellspacing=&quot;2&quot; frame=&quot;VOID&quot; rules=&quot;ROWS&quot;&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;/&gt;&lt;td&gt;Enchantment (Compulsion) [Mind-Affecting]&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Level:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Bard 5, Sorcerer/Wizard 6&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Duration:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;1 min./level&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;p&gt;This spell functions like &lt;i&gt;heroism&lt;/i&gt;,  except the creature gains a +4 morale bonus on attack rolls, saves, and skill checks, immunity to fear effects, and temporary hit points equal to your caster level (maximum 20).&lt;/p&gt;&lt;p/&gt;
&lt;/div&gt;', 'SRD 3.5 SpellsH-L')
