INSERT INTO [spell] ([id], [name], [altname], [school], [subschool], [descriptor], [spellcraft_dc], [level], [components], [casting_time], [range], [target], [area], [effect], [duration], [saving_throw], [spell_resistance], [short_description], [to_develop], [material_components], [arcane_material_components], [focus], [description], [xp_cost], [arcane_focus], [wizard_focus], [verbal_components], [sorcerer_focus], [bard_focus], [cleric_focus], [druid_focus], [full_text], [reference])
VALUES 
  (464, 'Permanent Image', 'permanent image', 'Illusion', 'Figment', NULL, NULL, 'Bard 6, Sorcerer/Wizard 6', 'V, S, F', '1 standard action', 'Long (400 ft. + 12 m (40 ft.)/level)', NULL, NULL, 'Figment that cannot extend beyond a 6 m (20-ft.). cube + one 3 m (10-ft.) cube/level (S)', 'Permanent (D)', 'Will disbelief (if interacted with)', 'No', 'Includes sight, sound, and smell.', NULL, 'A bit of fleece plus powdered jade worth 100 gp.', NULL, 'A bit of fleece.', '&lt;p&gt;This spell functions like &lt;i&gt;silent image&lt;/i&gt;,  except that the figment includes visual, auditory, olfactory, and thermal elements, and the spell is permanent. By concentrating, you can move the image within the limits of the range, but it is static while you are not concentrating.&lt;/p&gt;
      &lt;p/&gt;', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '&lt;div topic=&quot;Permanent Image&quot; level=&quot;5&quot;&gt;&lt;p&gt;&lt;h5&gt;Permanent Image&lt;/h5&gt;&lt;/p&gt;&lt;table width=&quot;100%&quot; border=&quot;1&quot; cellpadding=&quot;2&quot; cellspacing=&quot;2&quot; frame=&quot;VOID&quot; rules=&quot;ROWS&quot;&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;/&gt;&lt;td&gt;Illusion (Figment)&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Level:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Bard 6, Sorcerer/Wizard 6&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Effect:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Figment that cannot extend beyond a 6 m (20-ft.). cube + one 3 m (10-ft.) cube/level (S)&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Duration:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Permanent (D)&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;p&gt;This spell functions like &lt;i&gt;silent image&lt;/i&gt;,  except that the figment includes visual, auditory, olfactory, and thermal elements, and the spell is permanent. By concentrating, you can move the image within the limits of the range, but it is static while you are not concentrating.&lt;/p&gt;&lt;p&gt;&lt;i&gt;Material Component:&lt;/i&gt; A bit of fleece plus powdered jade worth 100 gp.&lt;/p&gt;&lt;p/&gt;
&lt;/div&gt;', 'SRD 3.5 SpellsP-R')
GO

INSERT INTO [spell] ([id], [name], [altname], [school], [subschool], [descriptor], [spellcraft_dc], [level], [components], [casting_time], [range], [target], [area], [effect], [duration], [saving_throw], [spell_resistance], [short_description], [to_develop], [material_components], [arcane_material_components], [focus], [description], [xp_cost], [arcane_focus], [wizard_focus], [verbal_components], [sorcerer_focus], [bard_focus], [cleric_focus], [druid_focus], [full_text], [reference])
VALUES 
  (465, 'Persistent Image', 'persistent image', 'Illusion', 'Figment', NULL, NULL, 'Bard 5, Sorcerer/Wizard 5', 'V, S, F', '1 standard action', 'Long (400 ft. + 12 m (40 ft.)/level)', NULL, NULL, 'Visual figment that cannot extend beyond four 3 m (10-ft.) cubes + one 3 m (10-ft.) cube/level (S)', '1 min./level (D)', 'Will disbelief (if interacted with)', 'No', 'As major image, but no concentration required.', NULL, 'A bit of fleece and several grains of sand.', NULL, 'A bit of fleece.', '&lt;p&gt;This spell functions like &lt;i&gt;silent image&lt;/i&gt;,  except that the figment includes visual, auditory, olfactory, and thermal components, and the figment follows a script determined by you. The figment follows that script without your having to concentrate on it. The illusion can include intelligible speech if you wish.&lt;/p&gt;
      &lt;p/&gt;', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '&lt;div topic=&quot;Persistent Image&quot; level=&quot;5&quot;&gt;&lt;p&gt;&lt;h5&gt;Persistent Image&lt;/h5&gt;&lt;/p&gt;&lt;table width=&quot;100%&quot; border=&quot;1&quot; cellpadding=&quot;2&quot; cellspacing=&quot;2&quot; frame=&quot;VOID&quot; rules=&quot;ROWS&quot;&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;/&gt;&lt;td&gt;Illusion (Figment)&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Level:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Bard 5, Sorcerer/Wizard 5&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Duration:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;1 min./level (D)&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;p&gt;This spell functions like &lt;i&gt;silent image&lt;/i&gt;,  except that the figment includes visual, auditory, olfactory, and thermal components, and the figment follows a script determined by you. The figment follows that script without your having to concentrate on it. The illusion can include intelligible speech if you wish.&lt;/p&gt;&lt;p&gt;&lt;i&gt;Material Component:&lt;/i&gt; A bit of fleece and several grains of sand.&lt;/p&gt;&lt;p/&gt;
&lt;/div&gt;', 'SRD 3.5 SpellsP-R')
GO

INSERT INTO [spell] ([id], [name], [altname], [school], [subschool], [descriptor], [spellcraft_dc], [level], [components], [casting_time], [range], [target], [area], [effect], [duration], [saving_throw], [spell_resistance], [short_description], [to_develop], [material_components], [arcane_material_components], [focus], [description], [xp_cost], [arcane_focus], [wizard_focus], [verbal_components], [sorcerer_focus], [bard_focus], [cleric_focus], [druid_focus], [full_text], [reference])
VALUES 
  (466, 'Phantasmal Killer', 'phantasmal killer', 'Illusion', 'Phantasm', 'Fear, Mind-Affecting', NULL, 'Sorcerer/Wizard 4', 'V, S', '1 standard action', 'Medium (30 m (100 ft.) + 3 m (10 ft.)/level)', 'One living creature', NULL, NULL, 'Instantaneous', 'Will disbelief (if interacted with), then Fortitude partial; See Book', 'Yes', 'Fearsome illusion kills subject or deals 3d6 damage.', NULL, NULL, NULL, NULL, '&lt;p&gt;You create a phantasmal image of the most fearsome creature imaginable to the subject simply by forming the fears of the subject''s subconscious mind into something that its conscious mind can visualize: this most horrible beast. Only the spell''s subject can see the phantasmal killer. You see only a vague shape. The target first gets a Will save to recognize the image as unreal. If that save fails, the phantasm touches the subject, and the subject must succeed on a Fortitude save or die from fear. Even if the Fortitude save is successful, the subject takes 3d6 points of damage.&lt;/p&gt;
      &lt;p&gt;If the subject of a &lt;i&gt;phantasmal killer&lt;/i&gt; attack succeeds in disbelieving and is wearing a &lt;i&gt;helm of telepathy&lt;/i&gt;,  the beast can be turned upon you. You must then disbelieve it or become subject to its deadly fear attack.&lt;/p&gt;
      &lt;p/&gt;', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '&lt;div topic=&quot;Phantasmal Killer&quot; level=&quot;5&quot;&gt;&lt;p&gt;&lt;h5&gt;Phantasmal Killer&lt;/h5&gt;&lt;/p&gt;&lt;table width=&quot;100%&quot; border=&quot;1&quot; cellpadding=&quot;2&quot; cellspacing=&quot;2&quot; frame=&quot;VOID&quot; rules=&quot;ROWS&quot;&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;/&gt;&lt;td&gt;Illusion (Phantasm) [Fear, Mind-Affecting]&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Level:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Sorcerer/Wizard 4&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Components:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;V, S&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Casting Time:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;1 standard action&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Range:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Medium (30 m (100 ft.) + 3 m (10 ft.)/level)&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Target:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;One living creature&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Duration:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Instantaneous&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Saving Throw:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Will disbelief (if interacted with), then Fortitude partial; See Book&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Spell Resistance:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Yes&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;p&gt;You create a phantasmal image of the most fearsome creature imaginable to the subject simply by forming the fears of the subject''s subconscious mind into something that its conscious mind can visualize: this most horrible beast. Only the spell''s subject can see the phantasmal killer. You see only a vague shape. The target first gets a Will save to recognize the image as unreal. If that save fails, the phantasm touches the subject, and the subject must succeed on a Fortitude save or die from fear. Even if the Fortitude save is successful, the subject takes 3d6 points of damage.&lt;/p&gt;&lt;p&gt;If the subject of a &lt;i&gt;phantasmal killer&lt;/i&gt; attack succeeds in disbelieving and is wearing a &lt;i&gt;helm of telepathy&lt;/i&gt;,  the beast can be turned upon you. You must then disbelieve it or become subject to its deadly fear attack.&lt;/p&gt;&lt;p/&gt;
&lt;/div&gt;', 'SRD 3.5 SpellsP-R')
GO

INSERT INTO [spell] ([id], [name], [altname], [school], [subschool], [descriptor], [spellcraft_dc], [level], [components], [casting_time], [range], [target], [area], [effect], [duration], [saving_throw], [spell_resistance], [short_description], [to_develop], [material_components], [arcane_material_components], [focus], [description], [xp_cost], [arcane_focus], [wizard_focus], [verbal_components], [sorcerer_focus], [bard_focus], [cleric_focus], [druid_focus], [full_text], [reference])
VALUES 
  (467, 'Phantom Steed', 'phantom steed', 'Conjuration', 'Creation', NULL, NULL, 'Bard 3, Sorcerer/Wizard 3', 'V, S', '10 minutes', '0 ft.', NULL, NULL, 'One quasi-real, horselike creature', '1 hour/level (D)', 'None', 'No', 'Magic horse appears for 1 hour/level.', NULL, NULL, NULL, NULL, '&lt;p&gt;You conjure a Large, quasi-real, horselike creature. The steed can be ridden only by you or by the one person for whom you specifically created the mount. A phantom steed has a black head and body, gray mane and tail, and smoke-colored, insubstantial hooves that make no sound. It has what seems to be a saddle, bit, and bridle. It does not fight, but animals shun it and refuse to attack it.&lt;/p&gt;
      &lt;p&gt;The mount has an AC of 18 (-1 size, +4 natural armor, +5 Dex) and 7 hit points +1 hit point per caster level. If it loses all its hit points, the phantom steed disappears. A phantom steed has a speed of 20 feet per caster level, to a maximum of 240 feet. It can bear its rider''s weight plus up to 10 pounds per caster level.&lt;/p&gt;
      &lt;p&gt;These mounts gain certain powers according to caster level. A mount''s abilities include those of mounts of lower caster levels.&lt;/p&gt;
      &lt;p&gt;&lt;i&gt;8th Level:&lt;/i&gt; The mount can ride over sandy, muddy, or even swampy ground without difficulty or decrease in speed.&lt;/p&gt;
      &lt;p&gt;&lt;i&gt;10th Level:&lt;/i&gt; The mount can use &lt;i&gt;water walk&lt;/i&gt; at will (as the spell, no action required to activate this ability).&lt;/p&gt;
      &lt;p&gt;&lt;i&gt;12th Level:&lt;/i&gt; The mount can use &lt;i&gt;air walk&lt;/i&gt; at will (as the spell, no action required to activate this ability) for up to 1 round at a time, after which it falls to the ground.&lt;/p&gt;
      &lt;p&gt;&lt;i&gt;14th Level:&lt;/i&gt; The mount can fly at its speed (average maneuverability).&lt;/p&gt;
      &lt;p/&gt;', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '&lt;div topic=&quot;Phantom Steed&quot; level=&quot;5&quot;&gt;&lt;p&gt;&lt;h5&gt;Phantom Steed&lt;/h5&gt;&lt;/p&gt;&lt;table width=&quot;100%&quot; border=&quot;1&quot; cellpadding=&quot;2&quot; cellspacing=&quot;2&quot; frame=&quot;VOID&quot; rules=&quot;ROWS&quot;&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;/&gt;&lt;td&gt;Conjuration (Creation)&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Level:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Bard 3, Sorcerer/Wizard 3&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Components:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;V, S&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Casting Time:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;10 minutes&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Range:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;0 ft.&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Effect:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;One quasi-real, horselike creature&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Duration:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;1 hour/level (D)&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Saving Throw:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;None&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Spell Resistance:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;No&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;p&gt;You conjure a Large, quasi-real, horselike creature. The steed can be ridden only by you or by the one person for whom you specifically created the mount. A phantom steed has a black head and body, gray mane and tail, and smoke-colored, insubstantial hooves that make no sound. It has what seems to be a saddle, bit, and bridle. It does not fight, but animals shun it and refuse to attack it.&lt;/p&gt;&lt;p&gt;The mount has an AC of 18 (-1 size, +4 natural armor, +5 Dex) and 7 hit points +1 hit point per caster level. If it loses all its hit points, the phantom steed disappears. A phantom steed has a speed of 20 feet per caster level, to a maximum of 240 feet. It can bear its rider''s weight plus up to 10 pounds per caster level.&lt;/p&gt;&lt;p&gt;These mounts gain certain powers according to caster level. A mount''s abilities include those of mounts of lower caster levels.&lt;/p&gt;&lt;p&gt;&lt;i&gt;8th Level:&lt;/i&gt; The mount can ride over sandy, muddy, or even swampy ground without difficulty or decrease in speed.&lt;/p&gt;&lt;p&gt;&lt;i&gt;10th Level:&lt;/i&gt; The mount can use &lt;i&gt;water walk&lt;/i&gt; at will (as the spell, no action required to activate this ability).&lt;/p&gt;&lt;p&gt;&lt;i&gt;12th Level:&lt;/i&gt; The mount can use &lt;i&gt;air walk&lt;/i&gt; at will (as the spell, no action required to activate this ability) for up to 1 round at a time, after which it falls to the ground.&lt;/p&gt;&lt;p&gt;&lt;i&gt;14th Level:&lt;/i&gt; The mount can fly at its speed (average maneuverability).&lt;/p&gt;&lt;p/&gt;
&lt;/div&gt;', 'SRD 3.5 SpellsP-R')
GO

INSERT INTO [spell] ([id], [name], [altname], [school], [subschool], [descriptor], [spellcraft_dc], [level], [components], [casting_time], [range], [target], [area], [effect], [duration], [saving_throw], [spell_resistance], [short_description], [to_develop], [material_components], [arcane_material_components], [focus], [description], [xp_cost], [arcane_focus], [wizard_focus], [verbal_components], [sorcerer_focus], [bard_focus], [cleric_focus], [druid_focus], [full_text], [reference])
VALUES 
  (468, 'Phantom Trap', 'phantom trap', 'Illusion', 'Glamer', NULL, NULL, 'Sorcerer/Wizard 2', 'V, S, M', '1 standard action', 'Touch', 'Object touched', NULL, NULL, 'Permanent (D)', 'None', 'No', 'Makes item seem trapped.', NULL, 'A piece of iron pyrite touched to the object to be trapped while the object is sprinkled with a special dust requiring 50 gp to prepare.', NULL, NULL, '&lt;p&gt;This spell makes a lock or other small mechanism seem to be trapped to anyone who can detect traps. You place the spell upon any small mechanism or device, such as a lock, hinge, hasp, cork, cap, or ratchet. Any character able to detect traps, or who uses any spell or device enabling trap detection, is 100% certain a real trap exists. Of course, the effect is illusory and nothing happens if the trap is ''sprung''; its primary purpose is to frighten away thieves or make them waste precious time.&lt;/p&gt;
      &lt;p&gt;If another &lt;i&gt;phantom trap&lt;/i&gt; is active within 50 feet when the spell is cast, the casting fails.&lt;/p&gt;
      &lt;p/&gt;', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '&lt;div topic=&quot;Phantom Trap&quot; level=&quot;5&quot;&gt;&lt;p&gt;&lt;h5&gt;Phantom Trap&lt;/h5&gt;&lt;/p&gt;&lt;table width=&quot;100%&quot; border=&quot;1&quot; cellpadding=&quot;2&quot; cellspacing=&quot;2&quot; frame=&quot;VOID&quot; rules=&quot;ROWS&quot;&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;/&gt;&lt;td&gt;Illusion (Glamer)&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Level:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Sorcerer/Wizard 2&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Components:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;V, S, M&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Casting Time:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;1 standard action&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Range:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Touch&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Target:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Object touched&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Duration:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Permanent (D)&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Saving Throw:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;None&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Spell Resistance:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;No&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;p&gt;This spell makes a lock or other small mechanism seem to be trapped to anyone who can detect traps. You place the spell upon any small mechanism or device, such as a lock, hinge, hasp, cork, cap, or ratchet. Any character able to detect traps, or who uses any spell or device enabling trap detection, is 100% certain a real trap exists. Of course, the effect is illusory and nothing happens if the trap is ''sprung''; its primary purpose is to frighten away thieves or make them waste precious time.&lt;/p&gt;&lt;p&gt;If another &lt;i&gt;phantom trap&lt;/i&gt; is active within 50 feet when the spell is cast, the casting fails.&lt;/p&gt;&lt;p&gt;&lt;i&gt;Material Component:&lt;/i&gt; A piece of iron pyrite touched to the object to be trapped while the object is sprinkled with a special dust requiring 50 gp to prepare.&lt;/p&gt;&lt;p/&gt;
&lt;/div&gt;', 'SRD 3.5 SpellsP-R')
GO

INSERT INTO [spell] ([id], [name], [altname], [school], [subschool], [descriptor], [spellcraft_dc], [level], [components], [casting_time], [range], [target], [area], [effect], [duration], [saving_throw], [spell_resistance], [short_description], [to_develop], [material_components], [arcane_material_components], [focus], [description], [xp_cost], [arcane_focus], [wizard_focus], [verbal_components], [sorcerer_focus], [bard_focus], [cleric_focus], [druid_focus], [full_text], [reference])
VALUES 
  (469, 'Phase Door', 'phase door', 'Conjuration', 'Creation', NULL, NULL, 'Sorcerer/Wizard 7, Travel 8', 'V', '1 standard action', '0 ft.', NULL, NULL, 'Ethereal 1,5 m (5 ft.) by 2,4 m (8 ft.) opening, 3 m (10 ft.) deep + 1,5 m (5 ft.) deep per three levels', 'One usage per two levels', 'None', 'No', 'Creates an invisible passage through wood or stone.', NULL, NULL, NULL, NULL, '&lt;p&gt;This spell creates an ethereal passage through wooden, plaster, or stone walls, but not other materials. The &lt;i&gt;phase door&lt;/i&gt; is invisible and inaccessible to all creatures except you, and only you can use the passage. You disappear when you enter the &lt;i&gt;phase door&lt;/i&gt; and appear when you exit. If you desire, you can take one other creature (Medium or smaller) through the door. This counts as two uses of the door. The door does not allow light, sound, or spell effects through it, nor can you see through it without using it. Thus, the spell can provide an escape route, though certain creatures, such as phase spiders, can follow with ease. A &lt;i&gt;gem of true seeing&lt;/i&gt; or similar magic reveals the presence of a &lt;i&gt;phase door&lt;/i&gt; but does not allow its use.&lt;/p&gt;
      &lt;p&gt;A &lt;i&gt;phase door&lt;/i&gt; is subject to &lt;i&gt;dispel magic&lt;/i&gt;. If anyone is within the passage when it is dispelled, he is harmlessly ejected just as if he were inside a &lt;i&gt;passwall&lt;/i&gt; effect.&lt;/p&gt;
      &lt;p&gt;You can allow other creatures to use the &lt;i&gt;phase door&lt;/i&gt; by setting some triggering condition for the door. Such conditions can be as simple or elaborate as you desire. They can be based on a creature''s name, identity, or alignment, but otherwise must be based on observable actions or qualities. Intangibles such as level, class, Hit Dice, and hit points don''t qualify.&lt;/p&gt;
      &lt;p&gt;&lt;i&gt;phase door&lt;/i&gt; can be made permanent with a &lt;i&gt;permanency&lt;/i&gt; spell.&lt;/p&gt;
      &lt;p/&gt;', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '&lt;div topic=&quot;Phase Door&quot; level=&quot;5&quot;&gt;&lt;p&gt;&lt;h5&gt;Phase Door&lt;/h5&gt;&lt;/p&gt;&lt;table width=&quot;100%&quot; border=&quot;1&quot; cellpadding=&quot;2&quot; cellspacing=&quot;2&quot; frame=&quot;VOID&quot; rules=&quot;ROWS&quot;&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;/&gt;&lt;td&gt;Conjuration (Creation)&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Level:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Sorcerer/Wizard 7, Travel 8&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Components:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;V&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Casting Time:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;1 standard action&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Range:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;0 ft.&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Effect:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Ethereal 1,5 m (5 ft.) by 2,4 m (8 ft.) opening, 3 m (10 ft.) deep + 1,5 m (5 ft.) deep per three levels&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Duration:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;One usage per two levels&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Saving Throw:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;None&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Spell Resistance:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;No&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;p&gt;This spell creates an ethereal passage through wooden, plaster, or stone walls, but not other materials. The &lt;i&gt;phase door&lt;/i&gt; is invisible and inaccessible to all creatures except you, and only you can use the passage. You disappear when you enter the &lt;i&gt;phase door&lt;/i&gt; and appear when you exit. If you desire, you can take one other creature (Medium or smaller) through the door. This counts as two uses of the door. The door does not allow light, sound, or spell effects through it, nor can you see through it without using it. Thus, the spell can provide an escape route, though certain creatures, such as phase spiders, can follow with ease. A &lt;i&gt;gem of true seeing&lt;/i&gt; or similar magic reveals the presence of a &lt;i&gt;phase door&lt;/i&gt; but does not allow its use.&lt;/p&gt;&lt;p&gt;A &lt;i&gt;phase door&lt;/i&gt; is subject to &lt;i&gt;dispel magic&lt;/i&gt;. If anyone is within the passage when it is dispelled, he is harmlessly ejected just as if he were inside a &lt;i&gt;passwall&lt;/i&gt; effect.&lt;/p&gt;&lt;p&gt;You can allow other creatures to use the &lt;i&gt;phase door&lt;/i&gt; by setting some triggering condition for the door. Such conditions can be as simple or elaborate as you desire. They can be based on a creature''s name, identity, or alignment, but otherwise must be based on observable actions or qualities. Intangibles such as level, class, Hit Dice, and hit points don''t qualify.&lt;/p&gt;&lt;p&gt;&lt;i&gt;phase door&lt;/i&gt; can be made permanent with a &lt;i&gt;permanency&lt;/i&gt; spell.&lt;/p&gt;&lt;p/&gt;
&lt;/div&gt;', 'SRD 3.5 SpellsP-R')
GO

INSERT INTO [spell] ([id], [name], [altname], [school], [subschool], [descriptor], [spellcraft_dc], [level], [components], [casting_time], [range], [target], [area], [effect], [duration], [saving_throw], [spell_resistance], [short_description], [to_develop], [material_components], [arcane_material_components], [focus], [description], [xp_cost], [arcane_focus], [wizard_focus], [verbal_components], [sorcerer_focus], [bard_focus], [cleric_focus], [druid_focus], [full_text], [reference])
VALUES 
  (470, 'Planar Ally', 'planar ally', 'Conjuration', 'Calling', 'See Book for lesser planar ally', NULL, 'Cleric 6', 'V, S, DF, XP', '10 minutes', 'Close (7,5 m (25 ft.) + 1,5 m (5 ft.)/2 levels)', NULL, NULL, 'One or two called elementals or outsiders, totaling no more than 12 HD, which cannot be more than 9 m (30 ft.) apart when they appear', 'Instantaneous', 'None', 'No', 'As lesser planar ally, but up to 12 HD.', NULL, NULL, NULL, NULL, '&lt;p&gt;This spell functions like &lt;i&gt;lesser planar ally&lt;/i&gt;, except you may call a single creature of 12 HD or less, or two creatures of the same kind whose Hit Dice total no more than 12. The creatures agree to help you and request your return payment together.&lt;/p&gt;
      &lt;p/&gt;', '250 XP.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '&lt;div topic=&quot;Planar Ally&quot; level=&quot;5&quot;&gt;&lt;p&gt;&lt;h5&gt;Planar Ally&lt;/h5&gt;&lt;/p&gt;&lt;table width=&quot;100%&quot; border=&quot;1&quot; cellpadding=&quot;2&quot; cellspacing=&quot;2&quot; frame=&quot;VOID&quot; rules=&quot;ROWS&quot;&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;/&gt;&lt;td&gt;Conjuration (Calling) [See Book for lesser planar ally]&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Level:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Cleric 6&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Effect:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;One or two called elementals or outsiders, totaling no more than 12 HD, which cannot be more than 9 m (30 ft.) apart when they appear&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;p&gt;This spell functions like &lt;i&gt;lesser planar ally&lt;/i&gt;, except you may call a single creature of 12 HD or less, or two creatures of the same kind whose Hit Dice total no more than 12. The creatures agree to help you and request your return payment together.&lt;/p&gt;&lt;p&gt;&lt;i&gt;XP Cost:&lt;/i&gt; 250 XP.&lt;/p&gt;&lt;p/&gt;
&lt;/div&gt;', 'SRD 3.5 SpellsP-R')
GO

INSERT INTO [spell] ([id], [name], [altname], [school], [subschool], [descriptor], [spellcraft_dc], [level], [components], [casting_time], [range], [target], [area], [effect], [duration], [saving_throw], [spell_resistance], [short_description], [to_develop], [material_components], [arcane_material_components], [focus], [description], [xp_cost], [arcane_focus], [wizard_focus], [verbal_components], [sorcerer_focus], [bard_focus], [cleric_focus], [druid_focus], [full_text], [reference])
VALUES 
  (471, 'Planar Ally, Greater', 'greater planar ally', 'Conjuration', 'Calling', 'See Book for lesser planar ally', NULL, 'Cleric 8', 'V, S, DF, XP', '10 minutes', 'Close (7,5 m (25 ft.) + 1,5 m (5 ft.)/2 levels)', NULL, NULL, 'Up to three called elementals or outsiders, totaling no more than 18 HD, no two of which can be more than 9 m (30 ft.) apart when they appear.', 'Instantaneous', 'None', 'No', 'As lesser planar ally, but up to 18 HD.', NULL, NULL, NULL, NULL, '&lt;p&gt;This spell functions like &lt;i&gt;lesser planar ally&lt;/i&gt;, except that you may call a single creature of 18 HD or less, or up to three creatures of the same kind whose Hit Dice total no more than 18. The creatures agree to help you and request your return payment together.&lt;/p&gt;
      &lt;p/&gt;', '500 XP.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '&lt;div topic=&quot;Planar Ally, Greater&quot; level=&quot;5&quot;&gt;&lt;p&gt;&lt;h5&gt;Planar Ally, Greater&lt;/h5&gt;&lt;/p&gt;&lt;table width=&quot;100%&quot; border=&quot;1&quot; cellpadding=&quot;2&quot; cellspacing=&quot;2&quot; frame=&quot;VOID&quot; rules=&quot;ROWS&quot;&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;/&gt;&lt;td&gt;Conjuration (Calling) [See Book for lesser planar ally]&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Level:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Cleric 8&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Effect:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Up to three called elementals or outsiders, totaling no more than 18 HD, no two of which can be more than 9 m (30 ft.) apart when they appear.&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;p&gt;This spell functions like &lt;i&gt;lesser planar ally&lt;/i&gt;, except that you may call a single creature of 18 HD or less, or up to three creatures of the same kind whose Hit Dice total no more than 18. The creatures agree to help you and request your return payment together.&lt;/p&gt;&lt;p&gt;&lt;i&gt;XP Cost:&lt;/i&gt; 500 XP.&lt;/p&gt;&lt;p/&gt;
&lt;/div&gt;', 'SRD 3.5 SpellsP-R')
