INSERT INTO [spell] ([id], [name], [altname], [school], [subschool], [descriptor], [spellcraft_dc], [level], [components], [casting_time], [range], [target], [area], [effect], [duration], [saving_throw], [spell_resistance], [short_description], [to_develop], [material_components], [arcane_material_components], [focus], [description], [xp_cost], [arcane_focus], [wizard_focus], [verbal_components], [sorcerer_focus], [bard_focus], [cleric_focus], [druid_focus], [full_text], [reference])
VALUES 
  (628, 'Summon Swarm', 'summon swarm', 'Conjuration', 'Summoning', NULL, NULL, 'Bard 2, Druid 2, Sorcerer/Wizard 2', 'V, S, M/DF', '1 round', 'Close (7,5 m (25 ft.) + 1,5 m (5 ft.)/2 levels)', NULL, NULL, 'One swarm of bats, rats, or spiders', 'Concentration + 2 rounds', 'None', 'No', 'Summons swarm of bats, rats, or spiders.', NULL, NULL, 'A square of red cloth.', NULL, '&lt;p&gt;You summon a swarm of bats, rats, or spiders (your choice), which attacks all other creatures within its area. (You may summon the swarm so that it shares the area of other creatures.) If no living creatures are within its area, the swarm attacks or pursues the nearest creature as best it can. The caster has no control over its target or direction of travel.&lt;/p&gt;
      &lt;p/&gt;', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '&lt;div topic=&quot;Summon Swarm&quot; level=&quot;5&quot;&gt;&lt;p&gt;&lt;h5&gt;Summon Swarm&lt;/h5&gt;&lt;/p&gt;&lt;table width=&quot;100%&quot; border=&quot;1&quot; cellpadding=&quot;2&quot; cellspacing=&quot;2&quot; frame=&quot;VOID&quot; rules=&quot;ROWS&quot;&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;/&gt;&lt;td&gt;Conjuration (Summoning)&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Level:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Bard 2, Druid 2, Sorcerer/Wizard 2&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Components:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;V, S, M/DF&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Casting Time:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;1 round&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Range:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Close (7,5 m (25 ft.) + 1,5 m (5 ft.)/2 levels)&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Effect:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;One swarm of bats, rats, or spiders&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Duration:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Concentration + 2 rounds&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Saving Throw:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;None&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Spell Resistance:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;No&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;p&gt;You summon a swarm of bats, rats, or spiders (your choice), which attacks all other creatures within its area. (You may summon the swarm so that it shares the area of other creatures.) If no living creatures are within its area, the swarm attacks or pursues the nearest creature as best it can. The caster has no control over its target or direction of travel.&lt;/p&gt;&lt;p&gt;&lt;i&gt;Arcane Material Component:&lt;/i&gt; A square of red cloth.&lt;/p&gt;&lt;p/&gt;
&lt;/div&gt;', 'SRD 3.5 SpellsS')
GO

INSERT INTO [spell] ([id], [name], [altname], [school], [subschool], [descriptor], [spellcraft_dc], [level], [components], [casting_time], [range], [target], [area], [effect], [duration], [saving_throw], [spell_resistance], [short_description], [to_develop], [material_components], [arcane_material_components], [focus], [description], [xp_cost], [arcane_focus], [wizard_focus], [verbal_components], [sorcerer_focus], [bard_focus], [cleric_focus], [druid_focus], [full_text], [reference])
VALUES 
  (629, 'Sunbeam', 'sunbeam', 'Evocation', NULL, 'Light', NULL, 'Druid 7, Sun 7', 'V, S, DF', '1 standard action', '60 ft.', NULL, 'Line from your hand', NULL, '1 round/level or until all beams are exhausted', 'Reflex negates and Reflex half; See Book', 'Yes', 'Beam blinds and deals 4d6 damage.', NULL, NULL, NULL, NULL, '&lt;p&gt;For the duration of this spell, you can use a standard action to evoke a dazzling beam of intense light each round. You can call forth one beam per three caster levels (maximum six beams at 18th level). The spell ends when its duration runs out or your allotment of beams is exhausted.&lt;/p&gt;
      &lt;p&gt;Each creature in the beam is blinded and takes 4d6 points of damage. Any creatures to which sunlight is harmful or unnatural take double damage. A successful Reflex save negates the blindness and reduces the damage by half.&lt;/p&gt;
      &lt;p&gt;An undead creature caught within the beam takes 1d6 points of damage per caster level (maximum 20d6), or half damage if a Reflex save is successful. In addition, the beam results in the destruction of any undead creature specifically harmed by bright light if it fails its save.&lt;/p&gt;
      &lt;p&gt;The ultraviolet light generated by the spell deals damage to fungi, mold, oozes, and slimes just as if they were undead creatures.&lt;/p&gt;
      &lt;p/&gt;', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '&lt;div topic=&quot;Sunbeam&quot; level=&quot;5&quot;&gt;&lt;p&gt;&lt;h5&gt;Sunbeam&lt;/h5&gt;&lt;/p&gt;&lt;table width=&quot;100%&quot; border=&quot;1&quot; cellpadding=&quot;2&quot; cellspacing=&quot;2&quot; frame=&quot;VOID&quot; rules=&quot;ROWS&quot;&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;/&gt;&lt;td&gt;Evocation [Light]&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Level:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Druid 7, Sun 7&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Components:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;V, S, DF&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Casting Time:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;1 standard action&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Range:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;60 ft.&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Area:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Line from your hand&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Duration:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;1 round/level or until all beams are exhausted&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Saving Throw:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Reflex negates and Reflex half; See Book&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Spell Resistance:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Yes&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;p&gt;For the duration of this spell, you can use a standard action to evoke a dazzling beam of intense light each round. You can call forth one beam per three caster levels (maximum six beams at 18th level). The spell ends when its duration runs out or your allotment of beams is exhausted.&lt;/p&gt;&lt;p&gt;Each creature in the beam is blinded and takes 4d6 points of damage. Any creatures to which sunlight is harmful or unnatural take double damage. A successful Reflex save negates the blindness and reduces the damage by half.&lt;/p&gt;&lt;p&gt;An undead creature caught within the beam takes 1d6 points of damage per caster level (maximum 20d6), or half damage if a Reflex save is successful. In addition, the beam results in the destruction of any undead creature specifically harmed by bright light if it fails its save.&lt;/p&gt;&lt;p&gt;The ultraviolet light generated by the spell deals damage to fungi, mold, oozes, and slimes just as if they were undead creatures.&lt;/p&gt;&lt;p/&gt;
&lt;/div&gt;', 'SRD 3.5 SpellsS')
GO

INSERT INTO [spell] ([id], [name], [altname], [school], [subschool], [descriptor], [spellcraft_dc], [level], [components], [casting_time], [range], [target], [area], [effect], [duration], [saving_throw], [spell_resistance], [short_description], [to_develop], [material_components], [arcane_material_components], [focus], [description], [xp_cost], [arcane_focus], [wizard_focus], [verbal_components], [sorcerer_focus], [bard_focus], [cleric_focus], [druid_focus], [full_text], [reference])
VALUES 
  (630, 'Sunburst', 'sunburst', 'Evocation', NULL, 'Light', NULL, 'Druid 8, Sorcerer/Wizard 8, Sun 8', 'V, S, M/DF', '1 standard action', 'Long (400 ft. + 12 m (40 ft.)/level)', NULL, '24 m (80-ft.) -radius burst', NULL, 'Instantaneous', 'Reflex partial; See Book', 'Yes', 'Blinds all within 10 ft., deals 6d6 damage.', NULL, NULL, 'A piece of sunstone and a naked flame.', NULL, '&lt;p&gt;&lt;i&gt;Sunburst&lt;/i&gt; causes a globe of searing radiance to explode silently from a point you select. All creatures in the globe are blinded and take 6d6 points of damage. A creature to which sunlight is harmful or unnatural takes double damage. A successful Reflex save negates the blindness and reduces the damage by half.&lt;/p&gt;
      &lt;p&gt;An undead creature caught within the globe takes 1d6 points of damage per caster level (maximum 25d6), or half damage if a Reflex save is successful. In addition, the burst results in the destruction of any undead creature specifically harmed by bright light if it fail its save.&lt;/p&gt;
      &lt;p&gt;The ultraviolet light generated by the spell deals damage to fungi, mold, oozes, and slimes just as if they were undead creatures.&lt;/p&gt;
      &lt;p&gt;&lt;i&gt;Sunburst&lt;/i&gt; dispels any darkness spells of lower than 9th level within its area.&lt;/p&gt;
      &lt;p/&gt;', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '&lt;div topic=&quot;Sunburst&quot; level=&quot;5&quot;&gt;&lt;p&gt;&lt;h5&gt;Sunburst&lt;/h5&gt;&lt;/p&gt;&lt;table width=&quot;100%&quot; border=&quot;1&quot; cellpadding=&quot;2&quot; cellspacing=&quot;2&quot; frame=&quot;VOID&quot; rules=&quot;ROWS&quot;&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;/&gt;&lt;td&gt;Evocation [Light]&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Level:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Druid 8, Sorcerer/Wizard 8, Sun 8&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Components:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;V, S, M/DF&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Casting Time:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;1 standard action&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Range:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Long (400 ft. + 12 m (40 ft.)/level)&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Area:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;24 m (80-ft.) -radius burst&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Duration:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Instantaneous&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Saving Throw:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Reflex partial; See Book&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Spell Resistance:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Yes&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;p&gt;&lt;i&gt;Sunburst&lt;/i&gt; causes a globe of searing radiance to explode silently from a point you select. All creatures in the globe are blinded and take 6d6 points of damage. A creature to which sunlight is harmful or unnatural takes double damage. A successful Reflex save negates the blindness and reduces the damage by half.&lt;/p&gt;&lt;p&gt;An undead creature caught within the globe takes 1d6 points of damage per caster level (maximum 25d6), or half damage if a Reflex save is successful. In addition, the burst results in the destruction of any undead creature specifically harmed by bright light if it fail its save.&lt;/p&gt;&lt;p&gt;The ultraviolet light generated by the spell deals damage to fungi, mold, oozes, and slimes just as if they were undead creatures.&lt;/p&gt;&lt;p&gt;&lt;i&gt;Sunburst&lt;/i&gt; dispels any darkness spells of lower than 9th level within its area.&lt;/p&gt;&lt;p&gt;&lt;i&gt;Arcane Material Component:&lt;/i&gt; A piece of sunstone and a naked flame.&lt;/p&gt;&lt;p/&gt;
&lt;/div&gt;', 'SRD 3.5 SpellsS')
GO

INSERT INTO [spell] ([id], [name], [altname], [school], [subschool], [descriptor], [spellcraft_dc], [level], [components], [casting_time], [range], [target], [area], [effect], [duration], [saving_throw], [spell_resistance], [short_description], [to_develop], [material_components], [arcane_material_components], [focus], [description], [xp_cost], [arcane_focus], [wizard_focus], [verbal_components], [sorcerer_focus], [bard_focus], [cleric_focus], [druid_focus], [full_text], [reference])
VALUES 
  (631, 'Symbol of Death', 'symbol of death', 'Necromancy', NULL, 'Death', NULL, 'Cleric 8, Sorcerer/Wizard 8', 'V, S, M', '10 minutes', '0 ft.; See Book', NULL, NULL, 'One symbol', 'See Book', 'Fortitude negates', 'Yes', 'Triggered rune slays nearby creatures.', NULL, 'Mercury and phosphorus, plus powdered diamond and opal with a total value of at least 5,000 gp each.', NULL, NULL, '&lt;p&gt;This spell allows you to scribe a potent rune of power upon a surface. When triggered, a &lt;i&gt;symbol of death&lt;/i&gt; slays one or more creatures within 60 feet of the symbol (treat as a burst) whose combined total current hit points do not exceed 150. The &lt;i&gt;symbol of death&lt;/i&gt; affects the closest creatures first, skipping creatures with too many hit points to affect. Once triggered, the &lt;i&gt;symbol&lt;/i&gt; becomes active and glows, lasting for 10 minutes per caster level or until it has affected 150 hit points'' worth of creatures, whichever comes first. Any creature that enters the area while the &lt;i&gt;symbol of death&lt;/i&gt; is active is subject to its effect, whether or not that creature was in the area when it was triggered. A creature need save against the &lt;i&gt;symbol&lt;/i&gt; only once as long as it remains within the area, though if it leaves the area and returns while the &lt;i&gt;symbol&lt;/i&gt; is still active, it must save again.&lt;/p&gt;
      &lt;p&gt;Until it is triggered, the &lt;i&gt;symbol of death&lt;/i&gt; is inactive (though visible and legible at a distance of 60 feet). To be effective, a &lt;i&gt;symbol of death&lt;/i&gt; must always be placed in plain sight and in a prominent location. Covering or hiding the rune renders the &lt;i&gt;symbol of death&lt;/i&gt; ineffective, unless a creature removes the covering, in which case the &lt;i&gt;symbol of death&lt;/i&gt; works normally.&lt;/p&gt;
      &lt;p&gt;As a default, a &lt;i&gt;symbol of death&lt;/i&gt; is triggered whenever a creature does one or more of the following, as you select: looks at the rune; reads the rune; touches the rune; passes over the rune; or passes through a portal bearing the rune. Regardless of the trigger method or methods chosen, a creature more than 60 feet from a symbol of death can''t trigger it (even if it meets one or more of the triggering conditions, such as reading the rune). Once the spell is cast, a &lt;i&gt;symbol of death&lt;/i&gt;''s triggering conditions cannot be changed.&lt;/p&gt;
      &lt;p&gt;In this case, ''reading'' the rune means any attempt to study it, identify it, or fathom its meaning. Throwing a cover over a &lt;i&gt;symbol of death&lt;/i&gt; to render it inoperative triggers it if the symbol reacts to touch. You can''t use a &lt;i&gt;symbol of death&lt;/i&gt; offensively; for instance, a touch-triggered symbol of death remains untriggered if an item bearing the &lt;i&gt;symbol of death&lt;/i&gt; is used to touch a creature. Likewise, a &lt;i&gt;symbol of death&lt;/i&gt; cannot be placed on a weapon and set to activate when the weapon strikes a foe.&lt;/p&gt;
      &lt;p&gt;You can also set special triggering limitations of your own. These can be as simple or elaborate as you desire. Special conditions for triggering a &lt;i&gt;symbol of death&lt;/i&gt; can be based on a creature''s name, identity, or alignment, but otherwise must be based on observable actions or qualities. Intangibles such as level, class, Hit Dice, and hit points don''t qualify.&lt;/p&gt;
      &lt;p&gt;When scribing a &lt;i&gt;symbol of death&lt;/i&gt;, you can specify a password or phrase that prevents a creature using it from triggering the effect. Anyone using the password remains immune to that particular rune''s effects so long as the creature remains within 60 feet of the rune. If the creature leaves the radius and returns later, it must use the password again.&lt;/p&gt;
      &lt;p&gt;You also can attune any number of creatures to the &lt;i&gt;symbol of death&lt;/i&gt;,  but doing this can extend the casting time. Attuning one or two creatures takes negligible time, and attuning a small group (as many as ten creatures) extends the casting time to 1 hour. Attuning a large group (as many as twenty-five creatures) takes 24 hours. Attuning larger groups takes proportionately longer. Any creature attuned to a &lt;i&gt;symbol of death&lt;/i&gt; cannot trigger it and is immune to its effects, even if within its radius when triggered. You are automatically considered attuned to your own &lt;i&gt;symbols of death&lt;/i&gt;, and thus always ignore the effects and cannot inadvertently trigger them.&lt;/p&gt;
      &lt;p&gt;&lt;i&gt;Read magic&lt;/i&gt; allows you to identify a &lt;i&gt;symbol of death&lt;/i&gt; with a DC 19 Spellcraft check. Of course, if the &lt;i&gt;symbol of death&lt;/i&gt; is set to be triggered by reading it, this will trigger the symbol.&lt;/p&gt;
      &lt;p&gt;A &lt;i&gt;symbol of death&lt;/i&gt; can be removed by a successful &lt;i&gt;dispel magic&lt;/i&gt; targeted solely on the rune. An &lt;i&gt;erase&lt;/i&gt; spell has no effect on a &lt;i&gt;symbol of death&lt;/i&gt;. Destruction of the surface where a &lt;i&gt;symbol of death&lt;/i&gt; is inscribed destroys the &lt;i&gt;symbol&lt;/i&gt; but also triggers it.&lt;/p&gt;
      &lt;p&gt;&lt;i&gt;symbol of death&lt;/i&gt; can be made permanent with a &lt;i&gt;permanency&lt;/i&gt; spell. A permanent &lt;i&gt;symbol of death&lt;/i&gt; that is disabled or that has affected its maximum number of hit points becomes inactive for 10 minutes, then can be triggered again as normal.&lt;/p&gt;
      &lt;p&gt;&lt;i&gt;Note:&lt;/i&gt; Magic traps such as &lt;i&gt;symbol of death&lt;/i&gt; are hard to detect and disable. A rogue (only) can use the Search skill to find a &lt;i&gt;symbol of death&lt;/i&gt; and Disable Device to thwart it. The DC in each case is 25 + spell level, or 33 for &lt;i&gt;symbol of death&lt;/i&gt;.&lt;/p&gt;
      &lt;p/&gt;', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '&lt;div topic=&quot;Symbol of Death&quot; level=&quot;5&quot;&gt;&lt;p&gt;&lt;h5&gt;Symbol of Death&lt;/h5&gt;&lt;/p&gt;&lt;table width=&quot;100%&quot; border=&quot;1&quot; cellpadding=&quot;2&quot; cellspacing=&quot;2&quot; frame=&quot;VOID&quot; rules=&quot;ROWS&quot;&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;/&gt;&lt;td&gt;Necromancy [Death]&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Level:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Cleric 8, Sorcerer/Wizard 8&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Components:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;V, S, M&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Casting Time:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;10 minutes&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Range:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;0 ft.; See Book&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Effect:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;One symbol&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Duration:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;See Book&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Saving Throw:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Fortitude negates&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Spell Resistance:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Yes&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;p&gt;This spell allows you to scribe a potent rune of power upon a surface. When triggered, a &lt;i&gt;symbol of death&lt;/i&gt; slays one or more creatures within 60 feet of the symbol (treat as a burst) whose combined total current hit points do not exceed 150. The &lt;i&gt;symbol of death&lt;/i&gt; affects the closest creatures first, skipping creatures with too many hit points to affect. Once triggered, the &lt;i&gt;symbol&lt;/i&gt; becomes active and glows, lasting for 10 minutes per caster level or until it has affected 150 hit points'' worth of creatures, whichever comes first. Any creature that enters the area while the &lt;i&gt;symbol of death&lt;/i&gt; is active is subject to its effect, whether or not that creature was in the area when it was triggered. A creature need save against the &lt;i&gt;symbol&lt;/i&gt; only once as long as it remains within the area, though if it leaves the area and returns while the &lt;i&gt;symbol&lt;/i&gt; is still active, it must save again.&lt;/p&gt;&lt;p&gt;Until it is triggered, the &lt;i&gt;symbol of death&lt;/i&gt; is inactive (though visible and legible at a distance of 60 feet). To be effective, a &lt;i&gt;symbol of death&lt;/i&gt; must always be placed in plain sight and in a prominent location. Covering or hiding the rune renders the &lt;i&gt;symbol of death&lt;/i&gt; ineffective, unless a creature removes the covering, in which case the &lt;i&gt;symbol of death&lt;/i&gt; works normally.&lt;/p&gt;&lt;p&gt;As a default, a &lt;i&gt;symbol of death&lt;/i&gt; is triggered whenever a creature does one or more of the following, as you select: looks at the rune; reads the rune; touches the rune; passes over the rune; or passes through a portal bearing the rune. Regardless of the trigger method or methods chosen, a creature more than 60 feet from a symbol of death can''t trigger it (even if it meets one or more of the triggering conditions, such as reading the rune). Once the spell is cast, a &lt;i&gt;symbol of death&lt;/i&gt;''s triggering conditions cannot be changed.&lt;/p&gt;&lt;p&gt;In this case, ''reading'' the rune means any attempt to study it, identify it, or fathom its meaning. Throwing a cover over a &lt;i&gt;symbol of death&lt;/i&gt; to render it inoperative triggers it if the symbol reacts to touch. You can''t use a &lt;i&gt;symbol of death&lt;/i&gt; offensively; for instance, a touch-triggered symbol of death remains untriggered if an item bearing the &lt;i&gt;symbol of death&lt;/i&gt; is used to touch a creature. Likewise, a &lt;i&gt;symbol of death&lt;/i&gt; cannot be placed on a weapon and set to activate when the weapon strikes a foe.&lt;/p&gt;&lt;p&gt;You can also set special triggering limitations of your own. These can be as simple or elaborate as you desire. Special conditions for triggering a &lt;i&gt;symbol of death&lt;/i&gt; can be based on a creature''s name, identity, or alignment, but otherwise must be based on observable actions or qualities. Intangibles such as level, class, Hit Dice, and hit points don''t qualify.&lt;/p&gt;&lt;p&gt;When scribing a &lt;i&gt;symbol of death&lt;/i&gt;, you can specify a password or phrase that prevents a creature using it from triggering the effect. Anyone using the password remains immune to that particular rune''s effects so long as the creature remains within 60 feet of the rune. If the creature leaves the radius and returns later, it must use the password again.&lt;/p&gt;&lt;p&gt;You also can attune any number of creatures to the &lt;i&gt;symbol of death&lt;/i&gt;,  but doing this can extend the casting time. Attuning one or two creatures takes negligible time, and attuning a small group (as many as ten creatures) extends the casting time to 1 hour. Attuning a large group (as many as twenty-five creatures) takes 24 hours. Attuning larger groups takes proportionately longer. Any creature attuned to a &lt;i&gt;symbol of death&lt;/i&gt; cannot trigger it and is immune to its effects, even if within its radius when triggered. You are automatically considered attuned to your own &lt;i&gt;symbols of death&lt;/i&gt;, and thus always ignore the effects and cannot inadvertently trigger them.&lt;/p&gt;&lt;p&gt;&lt;i&gt;Read magic&lt;/i&gt; allows you to identify a &lt;i&gt;symbol of death&lt;/i&gt; with a DC 19 Spellcraft check. Of course, if the &lt;i&gt;symbol of death&lt;/i&gt; is set to be triggered by reading it, this will trigger the symbol.&lt;/p&gt;&lt;p&gt;A &lt;i&gt;symbol of death&lt;/i&gt; can be removed by a successful &lt;i&gt;dispel magic&lt;/i&gt; targeted solely on the rune. An &lt;i&gt;erase&lt;/i&gt; spell has no effect on a &lt;i&gt;symbol of death&lt;/i&gt;. Destruction of the surface where a &lt;i&gt;symbol of death&lt;/i&gt; is inscribed destroys the &lt;i&gt;symbol&lt;/i&gt; but also triggers it.&lt;/p&gt;&lt;p&gt;&lt;i&gt;symbol of death&lt;/i&gt; can be made permanent with a &lt;i&gt;permanency&lt;/i&gt; spell. A permanent &lt;i&gt;symbol of death&lt;/i&gt; that is disabled or that has affected its maximum number of hit points becomes inactive for 10 minutes, then can be triggered again as normal.&lt;/p&gt;&lt;p&gt;&lt;i&gt;Note:&lt;/i&gt; Magic traps such as &lt;i&gt;symbol of death&lt;/i&gt; are hard to detect and disable. A rogue (only) can use the Search skill to find a &lt;i&gt;symbol of death&lt;/i&gt; and Disable Device to thwart it. The DC in each case is 25 + spell level, or 33 for &lt;i&gt;symbol of death&lt;/i&gt;.&lt;/p&gt;&lt;p&gt;&lt;i&gt;Material Component:&lt;/i&gt; Mercury and phosphorus, plus powdered diamond and opal with a total value of at least 5,000 gp each.&lt;/p&gt;&lt;p/&gt;
&lt;/div&gt;', 'SRD 3.5 SpellsS')
GO

