INSERT INTO [spell] ([id], [name], [altname], [school], [subschool], [descriptor], [spellcraft_dc], [level], [components], [casting_time], [range], [target], [area], [effect], [duration], [saving_throw], [spell_resistance], [short_description], [to_develop], [material_components], [arcane_material_components], [focus], [description], [xp_cost], [arcane_focus], [wizard_focus], [verbal_components], [sorcerer_focus], [bard_focus], [cleric_focus], [druid_focus], [full_text], [reference])
VALUES 
  (636, 'Symbol of Sleep', 'symbol of sleep', 'Enchantment', 'Compulsion', 'Mind-Affecting', NULL, 'Cleric 5, Sorcerer/Wizard 5', 'V, S, M', '10 minutes', '0 ft.; See Book', NULL, NULL, 'One symbol', 'See Book', 'Will negates', 'Yes', 'Triggered rune puts nearby creatures into catatonic slumber.', NULL, 'Mercury and phosphorus, plus powdered diamond and opal with a total value of at least 1,000 gp.', NULL, NULL, '&lt;p&gt;This spell functions like &lt;i&gt;symbol of death&lt;/i&gt;, except that all creatures of 10 HD or less within 60 feet of the &lt;i&gt;symbol of sleep&lt;/i&gt; instead fall into a catatonic slumber for 3d6x10 minutes. Unlike with the &lt;i&gt;sleep&lt;/i&gt; spell, sleeping creatures cannot be awakened by nonmagical means before this time expires.&lt;/p&gt;
      &lt;p&gt;Unlike &lt;i&gt;symbol of death&lt;/i&gt;, &lt;i&gt;symbol of sleep&lt;/i&gt; has no hit point limit; once triggered, a &lt;i&gt;symbol of sleep&lt;/i&gt; simply remains active for 10 minutes per caster level.&lt;/p&gt;
      &lt;p&gt;&lt;i&gt;Note:&lt;/i&gt; Magic traps such as &lt;i&gt;symbol of sleep&lt;/i&gt; are hard to detect and disable. A rogue (only) can use the Search skill to find a &lt;i&gt;symbol of sleep&lt;/i&gt; and Disable Device to thwart it. The DC in each case is 25 + spell level, or 30 for &lt;i&gt;symbol of sleep&lt;/i&gt;.&lt;/p&gt;
      &lt;p/&gt;', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '&lt;div topic=&quot;Symbol of Sleep&quot; level=&quot;5&quot;&gt;&lt;p&gt;&lt;h5&gt;Symbol of Sleep&lt;/h5&gt;&lt;/p&gt;&lt;table width=&quot;100%&quot; border=&quot;1&quot; cellpadding=&quot;2&quot; cellspacing=&quot;2&quot; frame=&quot;VOID&quot; rules=&quot;ROWS&quot;&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;/&gt;&lt;td&gt;Enchantment (Compulsion) [Mind-Affecting]&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Level:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Cleric 5, Sorcerer/Wizard 5&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr maxcol=&quot;3&quot; curcol=&quot;3&quot;&gt;&lt;td width=&quot;25%&quot;&gt;&lt;b&gt;Saving Throw:&lt;/b&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;Will negates&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;p&gt;This spell functions like &lt;i&gt;symbol of death&lt;/i&gt;, except that all creatures of 10 HD or less within 60 feet of the &lt;i&gt;symbol of sleep&lt;/i&gt; instead fall into a catatonic slumber for 3d6x10 minutes. Unlike with the &lt;i&gt;sleep&lt;/i&gt; spell, sleeping creatures cannot be awakened by nonmagical means before this time expires.&lt;/p&gt;&lt;p&gt;Unlike &lt;i&gt;symbol of death&lt;/i&gt;, &lt;i&gt;symbol of sleep&lt;/i&gt; has no hit point limit; once triggered, a &lt;i&gt;symbol of sleep&lt;/i&gt; simply remains active for 10 minutes per caster level.&lt;/p&gt;&lt;p&gt;&lt;i&gt;Note:&lt;/i&gt; Magic traps such as &lt;i&gt;symbol of sleep&lt;/i&gt; are hard to detect and disable. A rogue (only) can use the Search skill to find a &lt;i&gt;symbol of sleep&lt;/i&gt; and Disable Device to thwart it. The DC in each case is 25 + spell level, or 30 for &lt;i&gt;symbol of sleep&lt;/i&gt;.&lt;/p&gt;&lt;p&gt;&lt;i&gt;Material Component:&lt;/i&gt; Mercury and phosphorus, plus powdered diamond and opal with a total value of at least 1,000 gp.&lt;/p&gt;&lt;p/&gt;
&lt;/div&gt;', 'SRD 3.5 SpellsS')
GO

