CREATE TABLE [dbo].[SpellsCompendium] 
(
	[Id] INT IDENTITY(1,1) PRIMARY KEY NOT NULL, 
	[Name] VARCHAR(255) NOT NULL, 					-- spell.name
	[School] VARCHAR (255) NOT NULL,				-- spell.school
	[SubSchool] VARCHAR (255) NULL,					-- spell.subschool
	[Descriptor] VARCHAR (255) NULL,				-- spell.descriptor
	[Level] VARCHAR (255) NULL,						-- spell.level
	[Components] TEXT NOT NULL,						-- spell.components
	[CastingTime] VARCHAR (255) NOT NULL,			-- spell.casting_time
	[Range] VARCHAR(255) NULL,						-- spell.range
	[Target] VARCHAR(255) NULL,						-- spell.target
	[Effect] VARCHAR(255) NULL,						-- spell.effect
	[Area] VARCHAR(255) NULL,						-- spell.area
	[Duration] VARCHAR(255) NOT NULL,				-- spell.duration
	[SavingThrow] VARCHAR(255) NULL,				-- spell.saving_throw
	[SpellResistance] VARCHAR(255) NULL,			-- spell.spell_resistance
	[BriefDescription] VARCHAR(255) NULL,			-- spell.short_description
	[MaterialComponents] VARCHAR(255) NULL,			-- spell.material_components
	[ArcaneMaterialComponents] VARCHAR (255) NULL,	-- spell.arcane_material_components
	[XpCost] VARCHAR (255) NULL,					-- spell.xp_cost
	[Focus] VARCHAR (255) NULL,						-- spell.focus
	[ArcaneFocus] VARCHAR (255) NULL,				-- spell.arcane_focus
	[BardFocus] VARCHAR (255) NULL,					-- spell.bard_focus
	[ClericFocus] VARCHAR (255) NULL,				-- spell.cleric_focus
	[DruidFocus] VARCHAR (255) NULL,				-- spell.druid_focus
	[SorcererFocus] VARCHAR (255) NULL,				-- spell.sorcerer_focus
	[WizardFocus] VARCHAR (255) NULL,				-- spell.wizard_focus
	[Source] VARCHAR(255) NOT NULL					-- spell.reference
)

INSERT INTO SpellsCompendium 
				(Name, School, SubSchool, Descriptor, Level,
				Components, CastingTime, Range, Target, Effect,
				Area, Duration, SavingThrow, SpellResistance, BriefDescription,
				MaterialComponents, ArcaneMaterialComponents, XpCost, Focus, ArcaneFocus,
				BardFocus, ClericFocus, DruidFocus, SorcererFocus, WizardFocus, Source)

SELECT  name, school, subschool, descriptor,level,
		components, casting_time, range, target, effect, 
		area, duration, saving_throw, spell_resistance, short_description, 
		material_components, arcane_material_components, xp_cost, focus, arcane_focus, 
		bard_focus, cleric_focus, druid_focus, sorcerer_focus, wizard_focus, reference
FROM spell
GO

UPDATE SpellsCompendium
SET Level = ''
WHERE Level IS NULL

DROP TABLE spell